INSERT INTO institution(`id`, `website`, `phases`, `name`, `fields`, `avatar`, `description`) values(26, '暂无', '成长发展期', '挚信资本', '互联网,移动互联网,医疗健康,环保能源,消费品,现代服务,传媒新媒体,教育培训,硬件', 'c4cee6136de50b1961857b1da7992372.jpg', '挚信资本创立于2006年，是一家专注于投资中国市场的私募股权投资基金。目前管理着两期基金，金额超过4亿美元。重点投资于成长期的优秀中国企业，投资对象主要分布于节能环保、新材料、消费品和服务、教育、新媒体、医疗等领域。');
INSERT INTO institution(`id`, `website`, `phases`, `name`, `fields`, `avatar`, `description`) values(21, 'http://www.zero2ipovc.com/', '天使初创期', '清科创投', '互联网,移动互联网,医疗健康,环保能源,消费品,现代服务,传媒新媒体,教育培训,金融,文化创意,硬件', '75c88dbe1ba198519c8428111923d88b.jpg', '清科创投是由清科集团所募集成立的，专注于投资具有高增长潜质中国企业的创业投资基金。借助清科集团在国内强大的网络资源，清科创投主要采取联合投资的方式和国内外著名创投机构投资于中后期企业，同时，以领投或联合领投的方式投资于早期的创业企业。投资的行业涉及互联网、移动无线应用、数字媒体、健康医疗、能源环保、消费品、服务行业及其他新兴领域。');
INSERT INTO institution(`id`, `website`, `phases`, `name`, `fields`, `avatar`, `description`) values(27, 'http://www.baifund.com/', '天使初创期,成长发展期', '贝塔斯曼亚洲投资基金', '互联网,移动互联网,传媒新媒体,教育培训', '0a824d1ca60320836a374e34a42b9f89.jpg', '贝塔斯曼亚洲投资基金（BAI）成立于2008年，成功注资并扶持了16家处于不同创业期的公司和4家联投基金，其中凤凰新媒体、易车等三项投资已在纽约上市。BAI目前的投资重点在于垂直化媒体、社交类媒体、移动互联网、在线广告技术服务、在线教育和业务流程外包等领域。这些投资不仅与贝塔斯曼集团现有的业务密切相关，更是为集团在中国加大数字媒体和教育领域的投资奠定了坚实的基础。');
INSERT INTO institution(`id`, `website`, `phases`, `name`, `fields`, `avatar`, `description`) values(28, 'http://www.szvc.com.cn', '成长发展期,成熟期', '深圳市创新投资集团(深创投)', '互联网,移动互联网,医疗健康,环保能源,制造业,消费品,现代服务,传媒新媒体,教育培训,金融,文化创意,硬件,农业', '6405c02fc115a4dc5b03416074e51021.jpg', '深圳市创新投资集团成立于2002年10月，关注IT、生物医药、新材料、光机电、新能源、新材料等行业，以投资成长型企业为主，覆盖早、中、晚三个阶段。');
INSERT INTO institution(`id`, `website`, `phases`, `name`, `fields`, `avatar`, `description`) values(33, 'http://www.qingsongfund.com', '天使初创期', '青松基金', '互联网,移动互联网', '8c1772b0d1ed2fb07a1db7c5e515ef6c.jpg', '青松基金成立于2012年6月，由3位互联网领域创业和投资人士发起设立。该基金定位于投资早期阶段(尤其是天使投资阶段)的互联网和移动互联网项目。');
INSERT INTO institution(`id`, `website`, `phases`, `name`, `fields`, `avatar`, `description`) values(30, 'http://www.istartvc.com', '天使初创期', '起点创业营', '互联网,移动互联网,医疗健康,环保能源,现代服务,传媒新媒体,文化创意', 'd073d810f93711a3b1fbf377d2c5d31d.jpg', '起点创业营成立于2011年12月，是一家聚焦于早期高科技和创新型创业企业的孵化机构，其投资领域包括：无线互联网、新媒体（TMT） 、健康服务及环保科技等领域，以种子期和A轮投资为主。');
INSERT INTO institution(`id`, `website`, `phases`, `name`, `fields`, `avatar`, `description`) values(32, 'http://www.ventechchina.com/', '成长发展期', '银泰资本Ventech', '互联网,移动互联网,医疗健康,传媒新媒体,硬件', 'b301a45e2bdf6d478d973abf34bd0716.jpg', '银泰资本是由法国金融管理局管理的风投机构，2007成立中国基金Ventech China，致力于培育并扶植中国本土的创新及成长性企业，主要投资处于发展早期的信息技术和生命科学公司。');
INSERT INTO institution(`id`, `website`, `phases`, `name`, `fields`, `avatar`, `description`) values(31, 'http://www.cdhfund.com/', '成长发展期,成熟期', '鼎晖投资', '互联网,移动互联网,医疗健康,环保能源,消费品,现代服务,传媒新媒体,教育培训,金融,文化创意,硬件', 'e7509a37a3294f616652e91dcf5283d5.jpg', '鼎晖投资成立于2002年，目前管理基金规模超过70亿美元，包括私募股权、创业投资、地产投资和证券投资基金。投资领域包括但不限于信息技术与服务、新媒体、大众消费品和消费服务、医疗设备及服务、教育、绿色技术等行业。');
INSERT INTO institution(`id`, `website`, `phases`, `name`, `fields`, `avatar`, `description`) values(22, '暂无', '天使初创期', '险峰华兴', '互联网,移动互联网', '3bef3037dc0b9baea6bbc52cc08abbcd.jpg', '险峰华兴(China Renaissance K2 Ventures)由华兴资本创始人包凡携手陈科屹创立，是专注互联网、移动互联网和科技类项目的早期天使投资机构。');
