INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(959, '北京', '', '中金公司CICC,高盛', '方风雷', '职位:  , 创始人', '互联网,环保能源,制造业,消费品,现代服务,金融', '朝阳区', 'c0c7660420abb95223867f0179ce1b34.jpg', '哈佛大学,中山大学', '厚朴基金', '方风雷，厚朴基金创始人。1952年出生于湖南，1982年毕业于中山大学，曾在美国哈佛大学商学院进修高级管理学课程。1993年，开始参与筹建中国首家中外合资投资银行中国国际金融有限公司，成为“第一代中国本土投资银行家”。曾任中国国际金融有限公司副总裁，中银国际控股有限公司执行总裁，中国工商东亚金融控股有限公司执行总裁，高盛高华证券有限责任公司董事长等职。现任，厚朴投资管理公司董事长。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(895, '北京', '', '', '王秋虎', '职位:  , 合伙人', '互联网,移动互联网,环保能源,现代服务,传媒新媒体,文化创意,硬件', '朝阳区', '1780bfe08dc5b8f4e66ca675c72e7610.jpg', '清华大学,大连交通大学', '开物投资', '王秋虎，开物投资合伙人。王秋虎先生有10年创业及投资经验，深刻理解企业家需求，建立广泛企业家人脉网络及资源；参与众多与VC/PE的融资及收购兼并交易，在互联网、清洁技术、新能源、媒体领域富有战略管理、私募股权投融资和并购经验。同时，王秋虎先生曾共同创立中文在线和普能科技两家知名公司。作为共同创始人，将中文在线打造为中国最大的电子书籍提供商、中文数字出版业领跑者；帮助普能科技获得垄断性行业地位，成为世界钒电池行业领袖。王秋虎先生拥有清华大学和MIT联合培养的国际工商管理（IMBA）项目的硕士学位以及大连交通大学工学学士学位。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(980, '北京', 'http://weibo.com/acforeverandever', '百度,汉能投资,凯旋创投,计算机世界', '姚亚平', '职位:  , 执行董事 , , 副总裁(已离职)', '互联网,移动互联网,消费品,现代服务,传媒新媒体,游戏', '朝阳区', 'a932ee6a68e0f90fa17a1fcec0d2180d.jpg', '清华大学,西安交通大学', '高原资本,凯旋创投', '姚亚平，高原资本执行董事，常驻北京，主要专注于早期和成长期的中国互联网企业投资，尤其是互联网、移动互联网、网络游戏、新媒体和新消费类企业方向。加入高原之前，姚亚平是凯旋创投副总裁，主导投资过7k7k、股票雷达、青芒果等公司。加入凯旋创投之前，姚亚平就职于百度投资并购部，帮助百度制订公司的投资和并购策略，在互联网各个细分领域实施战略投资和并购。在此期间，他参与了去哪儿网（NASDAQ：QUNR）、安居客、华扬联众、黑米网络等多个投资并购项目。在此之前，姚亚平在汉能投资集团工作，负责亚太区的互联网、媒体和电信类公司的跨境并购、私募融资、战略咨询等服务。期间帮助过SK电讯、新闻集团、道琼斯等多家知名国际集团进行战略收购，以及链家地产等公司进行私募融资。姚亚平的职业生涯开始于IDG旗下的计算机世界传媒集团，为《首席财务官》杂志及计世网的创始团队之一。姚亚平拥有清华大学工商管理硕士和西安交通大学材料科学与工程学士学位。');
