INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(857, '北京', 'http://weibo.com/p/1005051644262853', '热酷', '刘勇', '职位:  , 创始人', '互联网,移动互联网', '朝阳区', 'e138e92afb6136ed477950e6d72b0d74.jpg', '北京大学,美国西北大学,美国伊利诺斯大学', '热酷游戏', '刘勇，热酷游戏创始人及CEO，毕业于北京大学物理系。后取得美国伊利诺斯大学计算机硕士学位，及美国西北大学凯洛格 商学院MBA。2003年11月联合创办亿友网，并于2006年成功被法国上市公司并购。2007年底创办热酷。刘勇先生拥有多年的SNS及无线网络创业和管理经验，创业前曾任美国Motorola公司总部研发部门无线网络应用开发项目负责人 。');
