INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(713, '北京', 'http://weibo.com/p/1005052052084560', '华兴资本', '蔡蓉', '职位:  , 投资经理', '互联网,移动互联网,消费品,现代服务,传媒新媒体', '朝阳区', 'e9c0d3df5439b9ac813186a5dc8be201.jpg', '浙江大学', '富达亚洲风险投资', '蔡蓉于2011年加入富达亚洲风险投资担任投资经理，主要关注消费品、教育、TMT行业的投资机会。加入富达前，蔡蓉服务于华兴资本担任经理，在教育培训、消费品和TMT行业累计完成了超过1亿美元的私募融资项目。蔡蓉拥有浙江大学金融学硕士学位及经济学学士学位。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(695, '北京', 'http://weibo.com/p/1005051902604487', 'SK集团', '叶颖涛', '职位:  , CEO', '互联网,移动互联网', '朝阳区', '3901772209b2267e5765ba5ea1839d02.jpg', '中国人民大学', '掌趣科技', '叶颖涛，掌趣科技创始人及CEO。叶颖涛先生系中国人民大学高等教育自学考试财务会计专业大专毕业，曾任职于中国航空结算中心海外业务部，历任北京笔电新人信息技术有限公司系统部系统工程师，北京听听电子商务服务有限公司技术部技术总监，SK中国投资有限公司互联网事业部无线数据部无线业务总监，北京当途摩宝科技发展有限公司CEO，2008年1月加入公司，2008年6月至今任公司总经理。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(731, '上海', '', '科尔尼咨询,罗兰贝格', '蔡昱晨', '职位:  , 董事', '消费品,现代服务', '浦东新区', 'c6286d5ba4d70c189504e9f3d34d999c.jpg', '上海交通大学,中欧国际工商学院', 'ClearVue锴明投资', '蔡昱晨，ClearVue 董事。蔡女士在消费行业有14年的投资和咨询经验，曾服务的公司包括CP Equity China，罗兰•贝格管理咨询和科尔尼管理咨询公司。她在食品饮料、时尚和零售等子行业的投资、战略、营销等领域都有丰富的经验。在过去五年中，还帮助多个私募股权投资基金完成了大量消费行业投资项目的尽职调查、投后管理等投资支持工作。曾合作过的客户包括消费行业各个领域的国内和国际领先企业，如伊利、中粮、联合利华、卡夫、李宁等。CEIBS（中欧国际工商学院）EMBA，上海交通大学经济学硕士。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(676, '北京', 'http://weibo.com/p/1005051554710050', '奇虎360,经纬中国', '傅盛', '职位:  , CEO', '互联网,移动互联网', '朝阳区', '00abc3b3055781e91c6fd5a5ac4eeda0.jpg', '山东工商学院', '金山网络/猎豹移动', '傅盛，金山网络CEO。傅盛毕业于山东工商学院，之后加入厦华电脑、北京国信贝斯软件公司。2003年加入3721公司。2005年11月，加入奇虎公司负责客户端业务，打造了安全类软件360安全卫士。离开奇虎后，傅盛加入经纬创投中国的中国团队，出任副总裁职位。2009年9月9日，傅盛正式出任可牛影像CEO兼董事长。2010年11月10日，金山安全与可牛正式合并成立独立公司 傅盛出任金山网络CEO。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(678, '北京', '', '联想', '唐旭东', '职位:  , 总经理', '互联网,移动互联网,传媒新媒体,硬件', '海淀区', 'acbec6a9ef2f10eff2fb79022d139aff.jpg', '长江商学院', '联想之星', '唐旭东，联想控股有限公司高级副总裁、北京联想之星创业投资有限公司董事长兼总经理、联想投资有限公司监事、LC FUND Ⅱ 基金董事 、北京融科智地房地产开发有限公司董事、中关村国家自主创新示范区企业家顾问委员会副主任委员。 1990年加入联想，历任联想集团法务部总经理、人事部总经理、联想控股企划办主任等职，有着丰富的高级管理和投资管理经验。长江商学院EMBA。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(681, '北京', 'http://weibo.com/p/1005051854595357', '清科', '王明耀', '职位:  , 投资总监', '互联网,移动互联网,传媒新媒体,硬件', '海淀区', 'a6847862bf57ccb7ee0f7450bb851d6a.jpg', '清华大学,河海大学', '联想之星', '王明耀，联想之星投资总监。2010年加入联想之星，负责TMT领域天使投资。2001年涉足投资领域，先后担任大成基金市场部经理、清科集团高级副总裁、九帆投资合伙人，考察与辅导过大量创业企业，操作过数十个融资并购案例，对中小企业成长与资本运作有较深认识。主导投资了乐逗游戏、Zaker、恒图好照片、可滋泉、爱调研、火信网络等项目。清华大学MBA、河海大学工学学士。');
