INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(679, '北京', 'http://weibo.com/p/1005051242289755', '联想', '陆刚', '职位:  , 常务副总', '互联网,移动互联网,传媒新媒体,硬件', '海淀区', 'abe704e198990569e0efe8acc74f7980.jpg', '清华大学,西安电子科技大学', '联想之星', '陆刚，联想之星天使投资常务副总经理。2004年加入联想控股，作为投资人（LP）的代表，对风险投资公司进行评估、投资和管理，对创业投资有着深入的研究。2007年至2009年，作为联想控股的高级投资经理开展直接投资业务，投资项目涉及健康服务、节能环保、芯片设计等领域。2009年底起，开始主持联想之星天使投资业务，确立了TMT、医疗健康、先进制造三个天使投资核心领域，目前担任多家被投企业的董事。加入联想前，在中国民航从事导航工作，有着丰富的技术和管理经验。清华大学MBA、西安电子科技大学电磁场工程学士学位。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(683, '北京', '', '施耐德', '张瑞琪', '职位:  , 投资副总裁', '环保能源,制造业', '海淀区', '5fabe39663888b2f7ea94aa4f2741c2d.jpg', '清华大学,北京大学', '联想之星', '张瑞琪，联想之星投资副总裁。2012年加入联想，主要从事先进制造领域的天使投资，关注节能环保、新材料以及自动化等产业升级主题。加入联想前，曾就职于施耐德电气，历经工业化制造、全球项目管理、企业战略并购整合等多个工作。北大国际MBA（BiMBA）、清华大学电气工程学硕士、学士。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(680, '北京', '', '阿里巴巴,联想', '周自强', '职位:  , 副总经理', '互联网,移动互联网,传媒新媒体,硬件', '朝阳区', '3560afca6edf2c85efe21aea88f685ec.jpg', '国际关系学院', '联想之星', '周自强，联想之星副总经理。2003年加入联想控股，担任柳传志先生英文助理并参与联想集团董事会工作及投后管理。2008年，参与联想之星筹备与运作，2010年任教务长，总体负责联想之星创业培训和开放平台等工作。周自强对初创企业团队搭建、文化建设和战略制定有深入理解，并为联想之星多家被投企业提供深入的增值服务。加入联想前,先后在中国科学院、阿里巴巴、日本石川岛播磨重工业株式会社任职。北京国际关系学院法学士学位。');
