INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(702, '上海', 'http://weibo.com/p/1005051870505245', '', '蒋晓冬', '职位:  , 中国区董事总经理', '互联网,移动互联网,医疗健康,制造业,消费品,现代服务,硬件', '静安区', '1856f992fb189693f49ebef8c7659984.jpg', '南京大学,美国伊利诺斯大学', 'NEA恩颐投资/(New Enterprise Associates)', '蒋晓冬，NEA全球合伙人、中国区董事总经理。蒋晓冬2005年加入NEA，创建北京和上海团队，并负责NEA在快速成长的中国市场的投资。蒋先生投资领域广泛，先进制造、大众消费和医疗健康产业的成长资本投资是他目前关注的方向。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(704, '上海', '', 'AT&T,UT斯达康', '薛村禾', '职位:  , 执行主管合伙人', '互联网,移动互联网,传媒新媒体,硬件', '长宁区', 'c9448b736f7078e6befc9f71e4d8ff05.jpg', '上海交通大学,纽约州立大学', '软银中国/软银集团', '薛村禾先生是国内外著名的风险投资家和高科技创业人士。薛村禾先生早年相继在美国和中国拥有成功的创业经验，于2000年创立软银中国，担任执行主管合伙人至今。创立软银中国之前，薛村禾先生是UTStarcom Inc的创始人之一，并曾同时出任公司董事及高级副总裁。在创立UTStarcom之前，薛村禾先生曾任StarCom Network Systems, Inc 副总裁。在此之前，薛先生曾在美国AT&T Bell Labs和美国DGM&S公司任职。薛村禾先生曾数次当选福布斯中国最佳创业投资人。薛村禾先生拥有上海交通大学电子工程学士学位，以及美国纽约州立大学计算机硕士学位。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(705, '上海', '', '麦肯锡,西门子', '华平', '职位:  , 主管合伙人', '互联网,移动互联网,医疗健康,环保能源,消费品,现代服务,传媒新媒体,硬件', '长宁区', '780ac17f437f226e81399fdd7485a5bd.jpg', '上海交通大学,美国西北大学', '软银中国/软银集团', '华平博士在高成长企业的投资和管理方面有着丰富的经验。2000年加盟软银中国，他的主要投资领域包括信息技术、医疗、清洁技术和消费等行业。他领导和参与了众多知名企业的投资，并担任董事。其中，多家企业己成功上市或并购退出。加入软银中国之前，华平博士曾任麦肯锡公司的管理顾问、西门子（美国）研发和市场营销管理等职务。华平博士在美国拥有多个专利，曾任美国国家电子制造协会标准委员会核心成员。华平先生拥有上海交通大学的学士学位、美国西北大学凯洛格（Kellogg）管理学院的MBA，以及美国威斯康星大学的博士学位。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(706, '上海', '', 'UT斯达康', '宋安澜', '职位:  , 主管合伙人', '互联网,移动互联网,传媒新媒体,硬件', '黄浦区', '086b9ccd3e7d3fd8bff792065e6c2a80.jpg', '清华大学', '软银中国/软银集团', '宋安澜博士在创业和投资两方面都经验丰富，在高科技领域里知识渊博，尤其注重对高科技领域的早中期企业的扶植。宋安澜博士于2000年1月加盟软银中国，主要负责投资及管理，目前担任多个高科技公司的董事。在软银中国之前，宋安澜博士是UTStarcom创始团队成员，任信息技术总监；他还担任过MDC Telecom 首席技术官，并且是GDS等公司的董事及创始人。在此之前，宋安澜博士曾任美国康涅迪格大学电机系助理教授，从事计算机网络和信息系统领域的科研教学。宋安澜先生拥有清华大学自动化系的学士学位和硕士学位，以及清华大学经济管理学院的博士学位。目前还担任中国中小企业协会特约金融顾问，中国国际投资促进会投融资工作委员会副理事长，APEC电子商务工商联盟专家委员会主任委员，千人计划创业大赛创业导师，上海天使投资俱乐部执行委员等职务。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(707, '上海', '', '三星,西门子,同方股份', '刘天民', '职位:  , 主管合伙人', '互联网,移动互联网,传媒新媒体,硬件', '长宁区', '8a7b9bcfc6b2aa176ed7bdb1ca2073d5.jpg', '西安交通大学,西安电子科技大学', '软银中国/软银集团', '刘天民先生拥有广泛的专业知识、成功的创业经历、丰富的企业管理及投资实践，并具有欧、美、亚三大陆的实际工作经验。刘天民先生于2009年11月加盟软银中国，主要负责投资及管理。在此之前，刘天民先生曾任同方股份有限公司副总裁兼同方数字集团本部总经理，投资和管理的公司业务涉及互联网、新媒体、电子商务、广播电视、通信及信息技术、网络教育、消费与零售、军工和装备制造等。在同方股份之前，刘天民先生的主要经历还包括：美国Broadcom Corp主任科学家，Nextwave communications Corp 创始人，刘天民先生还曾在美国RCA Labs ，三星电子美国研究所，德国西门子研究中心任职。在以上任职期间，刘天民先生的许多研发成果在市场上得到广泛应用，特别是在数字电视领域；刘天民先生拥有20多项美国和欧洲专利。刘天民先生本科毕业于西安交通大学无线电系，之后他被选送至西安电子科技大学电子系修读硕士和博士学位。');
