INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(724, '北京', '', '诺基亚,苹果,AMD', '邓元鋆', '职位:  , 合伙人', '互联网,移动互联网', '朝阳区', '4c9b4734d2e003c36799d9fb3a9e0b2b.jpg', '加州州立大学', '诺基亚成长基金', '邓元鋆，诺基亚成长基金合伙人。加入NGP之前，邓元鋆曾担任AMD全球高级副总裁兼大中华区总裁；2004年至2010年期间，邓元鋆担任诺基亚大中华地区副董事长及销售部副总裁，带领诺基亚公司在中国的市场占有率从20%增长至40%以上。此外，邓元鋆在苹果公司、3Com公司、DEC公司与AST公司均担任过高级职位。邓元鋆是中国知名的企业领袖，曾担任中国欧盟商会副主席、中国外商投资企业协会副会长以及北京国际商会副会长等职位。在过去几年中，邓元鋆还获得业界的广泛认可及许多奖项，其中包括中国CEO&CIO杂志授予的“十年最佳职业经理人”称号。邓元鋆持有加州州立大学长滩分校计算机科学与工程学士学位以及加州州立大学富尔顿分校工商管理硕士学位。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(725, '北京', '', 'Oracle甲骨文,凯旋创投,贝恩咨询', '郭路', '职位:  , 执行董事', '互联网,移动互联网', '朝阳区', 'e7ab7cfcff85824cf79214458cdfe218.jpg', '清华大学,加州大学伯克利分校,美国伊利诺伊大学', '诺基亚成长基金', '郭路，诺基亚成长伙伴基金执行董事。在加入诺基亚成长基金之前，郭路曾服务于凯旋创投并关注于高科技领取早期和发展期的投资机会。在此之前，他曾服务于贝恩管理咨询公司，协助多个私募股权基金和企业客户在华投资战略和投资尽职调查。郭路曾在甲骨文从事和电信行业相关的企业合作、战略投资及并购业务，还曾在ADI和Amdocs等大型高科技公司从事产品研发工作。郭路拥有加州伯克利大学的工商管理硕士，清华大学学士，以及美国南伊利诺伊大学工程学硕士。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(727, '北京', 'http://weibo.com/p/1005051401607751', '美银美林,高盛,毕马威KPMG', '张阳', '职位:  , 董事', '互联网,移动互联网,消费品,现代服务,教育培训', '朝阳区', 'b950261777665fac2c0bf4325c2dd365.jpg', '中国人民大学,哥伦比亚大学', '集富亚洲JAFCO', '张阳，集富亚洲执行董事。2008年加入集富亚洲，负责中国区的投资业务，主要在北京工作。在加入集富亚洲前，张阳女士就职于美林亚太公司自有资金投资部，主要从事大中华区私人股权投资/增长资本投资业务，对天然资源、消费服务和互联网领域拥有丰富投资经验。在加入美林前，张女士曾在高盛顾问公司,雷曼兄弟公司从事大中华区投资业务，寻找中国私营部门的投资机会，主要包括对私营公司、不良资产和房地产的债权和股权投资。');
