INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(729, '香港', 'http://weibo.com/p/1005052697008673', '百事公司', '许智伟', '职位:  , 董事总经理', '互联网,移动互联网,消费品,现代服务', '中西区', '313bf01b1e9c1f1fe5f3de6bdfc1e673.jpg', '南加州大学,纽约州立大学', 'ClearVue锴明投资', '许智伟是锴明投资的创始人及董事总经理。他曾是CP Equity China的董事总经理，与Catterton Partners联手将国际品牌引入中国，并为其全球基金寻找投资机会。在此之前，他是百事可乐大中华区的首席营销官，负责所有碳酸饮料和非碳酸饮料品牌的中国营销和创新工作，管理品牌包括百事可乐、佳得乐、纯果乐、都乐、七喜、美年达、星巴克及立顿茶饮料等。在此期间，他同时还担任百事投资公司的企业关系副总裁，负责为公司寻找投资和合作机会。在加入百事之前，他在亚洲的媒体和音乐行业有十多年的经验，历任MTV北亚区域董事总经理和环球唱片公司东南亚业务总裁，期间带领他所任职的公司实现了快速业绩增长和商业模式转型。在环球唱片和百事任职期间，许先生还主导打造了“我型我秀”和百事群音乐队大赛。他投资了多个消费品、媒体及高科技企业，如英利绿色能源控股有限公司（纽约证券交易所代码：YGE）、Lumi、摩提工房、创毅视讯、PPTV、郁金香传媒、亿动广告传媒等。南加州大学MBA，纽约州立大学学士。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(732, '上海', '', '普华永道,高盛,埃森哲', '应丽', '职位:  , 董事', '消费品,现代服务', '浦东新区', 'da772269ae75a84614d2418981467f4e.jpg', '复旦大学,宾夕法尼亚大学', 'ClearVue锴明投资', '应丽，ClearVue董事。加入锴明投资之前，她曾任职远东宏信（中化集团下属香港上市公司）股权投资部副总监。加入远东宏信之前，她在高盛高华证券有限责任公司投资银行部工作，主要负责境外市场IPO以及跨境并购交易。在加入高盛之前，她曾任职埃森哲管理咨询公司咨询顾问以及普华永道中天会计师事务所审计师。沃顿商学院MBA，复旦大学学士。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(730, '上海', '', 'IBM,埃森哲,eBay易趣', '陈维立', '职位:  , 董事总经理', '互联网,移动互联网,消费品,现代服务', '浦东新区', 'cb9a2acefad0f5c83aed79e07e09682e.jpg', '哈佛大学,佛罗里达大学', 'ClearVue锴明投资', '陈维立，ClearVue创始人及董事总经理。陈先生曾任德同资本的董事总经理。德同资本是一家中国领先的风投机构，运作资本达8亿美金。他在过去任职的五年间，负责德同资本在消费、数字新媒体和能源领域的投资。加入德同之前，他是Accelergy(亚申科技)的创始人和首席执行官。亚申科技是一家在美国和中国运营的替代能源科技公司。在此之前，他还是OnePage的创始人和首席执行官。这是一家企业版软件公司，在2002年由Sybase收购。在创立OnePage之前，陈先生是网络在线个人支付概念的先锋公司Billpoint的创始人之一。Billpoint是P2P网上支付服务的主要提供商，并于1999年被eBay收购，成为eBay在线拍卖市场的支付工具。 他还先后在互联网门户Excite，Classifieds2000（1998年被Excite收购）、安达信咨询战略服务 (埃森哲)和IBM任管理职位。陈先生曾在多家消费和科技类公司中任投资者和董事，这些公司包括梦芭莎、乐淘、酷6网、冰动娱乐、橘子酒店、喜瑞能源和普能能源等。他是哈佛商学院校友董事，曾获2006年亚斯本国际菁英组织亨利克隆奖，2003年佛罗里达州大学最佳企业家。他是AAMA（亚杰商会）主席和总裁、YPO（青年总裁协会）上海分会主席和佛罗里达州大学商学院创业中心顾问。哈佛商学院MBA，佛罗里达大学学士学位。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(734, '上海', '', '通用电气,罗兰贝格', '刘展', '职位:  , 副总裁', '消费品,现代服务', '浦东新区', '7f6e87966ceeb9bbc59917975149951a.jpg', '北京大学,美国西北大学', 'ClearVue锴明投资', '刘展，ClearVue副总裁。刘展在投资和金融相关领域有8年以上经验。他曾在CP Equity China和一家总部位于芝加哥的投资基金任职。在此之前，他在罗兰•贝格管理咨询和GE分别有5年的管理顾问经验及2年的财务分析经验。他与多个私募股权投资基金合作完成了多个尽职调查项目，涵盖市场分析、竞争评估、财务模型、估值和重组等内容。中国注册金融分析师，西北大学凯洛格商学院MBA，北京大学财务管理专业管理学学士。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(733, '上海', '', '普华永道,安永,汇丰', '李佳', '职位:  , 副总裁', '消费品,现代服务', '浦东新区', '0de77cfd63a7c236b977aede76a6bd02.jpg', '北京大学', 'ClearVue锴明投资', '李佳，ClearVue副总裁。李佳在消费行业的投资领域拥有超过10年的经验。她曾在雀巢中国担任了三年经理职位，在此期间，她在22个月内完成了对银鹭和徐福记的收购，收购资产逾20亿美元。她在消费行业拥有极为丰富的经验，包括乳品、饮料、休闲食品、调味品和冷冻食品等多个子行业的行业和企业研究经验。在加入雀巢之前，她在汇丰直接投资公司担任过两年经理，负责在消费、服装和科技板块进行投资并管理投资组合公司。此前，她曾先后在PWC（普华永道）的公司理财部和安永任职。北京大学学士。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(735, '上海', '', '', '夏禹', '职位:  , 副总裁', '消费品,现代服务', '浦东新区', '00eeb30e0e471d681e917bebb4b41a8c.jpg', '上海交通大学', 'ClearVue锴明投资', '夏禹，ClearVue副总裁。夏禹有超过11年的品牌营销、业务拓展和管理经验，曾服务的公司包括Evian（依云），Maxxium（寰盛集团）和PernodRicard（保乐力加）。他曾担任依云大中华区的营销总监，在任期间将依云品牌发展成为中国占据75%市场份额的第一大高端饮料品牌。在此之前，他在寰盛任职期间将Macallan（麦卡伦）建成最大的单一麦芽威士忌品牌，在保乐力加任职期间把Chivas Regal（芝华士）发展成为中国最成功的进口烈性酒品牌。上海交通大学管理学学士。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(736, '上海', '', '', '潘正峰', '职位:  , 投资经理', '互联网,移动互联网,消费品,现代服务', '浦东新区', '11d7c863e8b77bc3dce385f32fa44a78.jpg', '纽约大学', 'ClearVue锴明投资', '潘正峰，ClearVue投资经理。潘正峰在美国Catterton Partners和软银赛富香港有两年多的投资从业经验。在Catterton工作期间，他参与了多个投资项目，其中包括Noodles & Co.的杠杆收购项目。同时他在餐饮行业有丰富的经验，其中包括在香港创立了米其林星级酒店Island Tang Restaurant。纽约大学学士学位。');
