INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(738, '北京', '', '麦肯锡,凯雷投资', '李基培', '职位:  , 董事总经理', '互联网,消费品,现代服务,金融', '朝阳区', '5df57cc58d0f71fbeb85f2babd608a86.jpg', '斯坦福大学,加州大学伯克利分校,麻省理工学院MIT', '兰馨亚洲', '李基培，兰馨亚洲董事总经理。李基培先生曾在香港和洛杉矶担任麦肯锡公司咨询顾问。1997年到2000年，李先生加盟兰馨亚洲，在中国和北亚地区作了大量投资。后来，李先生供职于凯雷集团香港公司任执行董事，管理凯雷集团在亚洲地区技术领域投资事务。二〇〇三年李先生重返兰馨， 领导了兰馨亚洲在携程旅游网及易趣网的第一轮投资。李先生现在还担任纳斯达克上市公司－携程旅游网的副主席和董事。李基培先生以最高荣誉称号（summa cum laude）毕业于加州大学柏克利分校，在麻省理工学院获得科学硕士学位，并在斯坦福大学获得工商管理硕士学位。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(740, '浙江', '', '', '宗佩民', '职位:  , 董事长', '医疗健康,环保能源,制造业,消费品,现代服务,硬件', '杭州', 'de68bd743814cc71ba365fc22e8743ab.jpg', '浙江工商大学', '华睿投资', '宗佩民，华睿投资董事长、创始合伙人，2002年创立浙江华睿投资管理有限公司，现兼任浙江省股权投资协会副会长、浙江省风险投资协会副会长、浙商全国理事会投资委员会主席等职。倡导“投资+服务”与产业链投资模式，先后为200多家浙江民营企业提供过管理顾问和财务顾问服务，主导或参与多起浙江民企并购重组上市公司案例，主持投资项目70多项，其中许多项目已经成功IPO。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(741, '台湾', 'http://weibo.com/p/1005051677179810', '', '林之晨', '职位:  , 合伙人', '互联网,移动互联网', '台北市', '13854e1491e0f9410ada290041703584.jpg', '国立台湾大学,纽约大学', 'appWorks之初创投', '林之晨，appWorks合伙人。1999 年份的网络创业老骨头，曾先后参与创办四家网路公司，成功募得创投资金 3 亿元。中间也曾投身创投,在 All Asia Partners 与 HSS Ventures 等基金历练。创业的经历包括:个人电脑零售网站 Hotcool.com (1999)、中文知识管理第一品牌硕网资讯 (2000)、北美知名旅游社群 Sosauce (2006)、3D 游戏制作公司 Muse Games (2008)。2009创办appWorks。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(739, '北京', '', '摩托罗拉,凯雷投资,UBS瑞银', '郭德宏', '职位:  , 董事总经理', '互联网,移动互联网,消费品,现代服务', '朝阳区', 'b79b12d9d0f949ee49fd592f42752f28.jpg', '南加州大学,芝加哥大学,威斯康星大学', '兰馨亚洲', '郭德宏，兰馨亚洲董事总经理。郭德宏先生原任职于美国摩托罗拉公司的高级开发工程师，后来曾在香港及纽约的所罗门兄弟公司 担任高级证券分析师。在此之后郭先生任凯雷集团 香港公司的副总裁，负责在大中华地区的增长型企业的投资项目。后郭先生转至瑞士联合银行资本投资公司任大中华地区主管及公司电信，媒体和科技部主管，负责建立和管理大中华地区的投资团队，并负责寻找和发现投资项目，对项目进行投资评估调查，以及管理投资项目等。郭先生出生在中国，在威斯康星大学获电子工程学士学位，在南加州大学获得计算机科学硕士学位，并在芝加哥大学获工商管理硕士学位。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(742, '台湾', '', '', '詹益鑑', '职位:  , 合伙人', '互联网,移动互联网', '台北市', 'dc5345ba46ab821bc159a37994577930.jpg', '国立台湾大学,台湾政治大学', 'appWorks之初创投', '詹益鑑博士，appWorks合伙人。具有跨领域背景及创业投资双轨经历，台大电机博士，政大商学硕士。自 2004 年起先后服务于诚信开发 (CX Ventures)、台湾工银 (IBTM) 及联讯创投 (Harbinger Ventures)，并曾参与台北市府无线网路城市与网站整合计画。于 2007 年起投身新创事业，具有丰富的募资经验与事业发展经历，于 2010 年加入 appWorks，积极推动台湾的创业与投资环境变革。自许为务实的理想主义者，热爱三项运动与阅读思辩。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(743, '台湾', '', '', '程九如', '职位:  , 合伙人', '互联网,移动互联网', '台北市', 'ff044141e93002d0ba3bb907fe2fc681.jpg', '', 'appWorks之初创投', '程九如，appWorks合伙人。台湾网络创业的先驱，于 1999 年创办 Webs-TV.com，是当年少数真正获利的「达康」公司，也担任过天空传媒 Yam 策略长兼营运长，目前仍身兼多个网路公司的董事职位，是非常受创业者爱戴的网路创业导师。Nice 毕业于美国 South Eastern University，但是他说在网路创业的世界，学历一点都x不重要。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(744, '台湾', '', '', '詹德弘', '职位:  , 合伙人', '互联网,移动互联网', '台北市', '5ec95c2bcb7e88b5b18b62863aaf6e6a.jpg', '国立台湾大学', 'appWorks之初创投', '詹德弘，appWorks合伙人。加入 appWorks 前，于华威集团服务长达 11 年，协助 CID 完成两支基金共 5.75 亿美金的募集，长期负责该集团共 14 支创投基金的投资人关係与营运管理，并带领团队完成 CID 自有基金管理系统的建置，曾评估过的创投桉件超过 200 个。加入 CID 前，曾服务于台湾少数纳斯达克挂牌公司和信超媒体旗下网路与系统部门。台大农机硕士，日语是母语，喜欢的运动是单车，食物是巧克力。');
