INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(826, '上海', '', 'AMD', '陈泰杰', '职位:  , 联合创始人', '互联网,移动互联网,消费品,现代服务,传媒新媒体,硬件', '黄浦区', '63f2a9fa5aa0665d4659123af36b4e07.jpg', '哈佛大学,美国罗彻斯特大学', 'AngelVest天使谷', '陈泰杰，AngelVest的联合创始人。陈泰杰先生在中美两国都拥有非常广泛的投资交易经验。他曾经受聘于索罗门兄弟公司，超威半导体（AMD），本田汽车以及一家小型专业投资银行。陈泰杰先生拥有罗彻斯特大学电子工程的学士学位以及哈佛商学院的工商管理硕士学位。陈泰杰先生的母语为英语，同时他的普通话非常流利。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(827, '上海', '', '华纳唱片,迪士尼', 'Ted Lai', '职位:  , 联合创始人', '互联网,移动互联网,现代服务,传媒新媒体,文化创意,硬件', '黄浦区', '37d7ece28eac912f04c1ec2452262227.jpg', '斯坦福大学,哈佛大学', 'AngelVest天使谷', 'Ted Lai，AngelVest联合创始人、执行董事长。Ted Lai曾在时代华纳就职超过十年时间，并最终就任于华纳兄弟高级副总裁一职；同时，Ted Lai曾在美国数字媒体产业中担任商业发展和产品管理总经理工作。Ted Lai是一位活跃的投资人，同时在将近15年的时间中担任中美两国科技，媒体和客户关注公司的顾问。Ted Lai拥有斯坦福大学工程系的学士学位以及哈佛商学院的工商管理硕士学位。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(828, '北京', 'http://weibo.com/p/1005051863878181', '三星,清科', '肖永泉', '职位:  , 创始合伙人', '互联网,移动互联网', '海淀区', '6c316cad7a8142c19dcc98efeb414194.jpg', '湖南大学', '龙渊云腾', '肖永泉，龙渊云腾创始合伙人，当乐网创始人、董事长。肖永泉先生在大学二年级开始互联网创业，是湖南省最早的一批大学生创业者。 2002年从湖南大学材料学院毕业后供职于三星Samsung SDI， 主要为Nokia、TCL等国内知名手机制造商提供售后技术服务支持工作。2003年肖永泉进入风险投资和私募股权投资公司清科集团，主要从事互联网、游戏和移动互联网行业的投资分析和研究工作。2004年和好友一起创办当乐网。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(829, '北京', 'http://weibo.com/p/1005051109662170', '腾讯,盛大', '高波', '职位:  , 创始合伙人', '互联网,移动互联网', '海淀区', '15cc851f2dbf39878ef6bc8be6213205.jpg', '', '龙渊云腾', '高波，龙渊云腾创始合伙人。2004年－2007年就职于腾讯科技，先后担任无线产品部副总经理、3G产品中心副总经理等职务，在任期间负责腾讯公司移动增值业务的运营管理，后全面负责手机腾讯网(3g.qq.com)和手机QQ客户端的产品开发和运营。2007年—2010年担任魔龙国际CTO，在任期间，参与了行业最早期手机网游《水晶之树》、《魔龙宝贝》等产品的制作，领导了手机网游《三国OL》的开发和运营。2010年5月加入盛大网络，担任盛大无线副总裁，后任盛大游戏卓蚁工作室总经理、制作人。在任期间，运用盛大品牌成功开发运营了数款手机游戏，包括《龙之谷》、《三国杀》、《蜀山》、《彩虹岛》、《梦幻国度Online》、《悍将传世Online》等产品。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(831, '北京', 'http://weibo.com/p/1005051760413115', '', '王京', '职位:  , 副总裁(已离职) , , 副总裁', '互联网,移动互联网', '东城区', '03842486413676858005e225554f384c.jpg', '北京航空航天大学', '真顺基金,光速安振/光速创投', '王京先生担任光速安振中国创投的副总裁，于2014年加入团队。王先生有着5年的早期创投经验以及4年的互联网从业经验。在加入光速安振中国之前，王先生曾担任真顺基金副总裁、险峰华兴创投投资经理，以及饭统网战略合作经理。独特的职业经历，使得王先生在早期创业阶段和早期投资阶段有着较为突出的优势，同时在创业者和其他早期投资人中间，也有着很好的人脉关系。王先生所关注与擅长的领域包括：B2B平台、O2O模式、移动互联网以及电子商务。此前，他积极参与的被投企业包括：聚美优品、墨迹天气、找钢网以及加网等。王京先生毕业于北京航空航天大学，拥有计算机科学与技术工学学士学位。他兴趣广泛，热情开朗，爱好创作诗歌，尤其喜欢体育运动，是一名业余足球高手。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(832, '北京', 'http://weibo.com/p/1005051732509302', '宝洁', '崔广福', '职位:  , CEO', '互联网,移动互联网,现代服务', '朝阳区', 'de81464ea0be5c17275b6c0366667b40.jpg', '北京大学,美国西北大学', '艺龙旅行网', '崔广福，艺龙旅行网CEO，此前曾担任联邦快递金考中国区的董事总经理；还曾在宝洁中国有限公司工作超过12年的时间。崔先生拥有美国西北大学凯洛格商学院的MBA学位和北京大学国际政治系法学士学位。');
