INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(844, '北京', 'http://weibo.com/yqyh', '百度', '王梦秋', '职位:  , 董事总经理', '互联网,移动互联网', '海淀区', '848969a5e29195623efeec0e31c86797.jpg', '北京大学,加州大学洛杉矶分校', 'Crystal Stream清流资本', '王梦秋，清流资本董事总经理，前百度技术副总裁。2002年11月受邀回国加盟百度，作为搜索、网络方面的资深工程师，主持参与了大量项目的设计和开发， 历任项目经理，技术经理，高级技术经理，技术总监，高级技术总监，同时还接管了用户产品市场方面大量的管理工作。2011年1月，晋升为百度副总裁，负责搜索研发部、垂直&社区搜索产品市场部、用户体验部、社区搜索部、网页搜索产品市场部、贴吧事业部、百度音乐事业部、运维部的工作。2001年6月至2002年10月，王梦秋在美国加州ABAQOS 通信公司担任软件工程师，进行Router、LAN Switch、路由协议等网络方面的研发工作。王梦秋获有美国加州大学洛杉矶分校(UCLA)计算机硕士学位及北京大学计算机学士学位。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(846, '上海', 'http://weibo.com/p/1005052412144337', '宏碁Acer', '陈友忠', '职位:  , 创始管理合伙人', '互联网,移动互联网,消费品,现代服务,传媒新媒体,硬件', '黄浦区', '37bcb3c66d3e0ef0a35059c325547efb.jpg', '北京大学,国立台湾大学', '智基创投IDT VC/宏碁创投', '陈友忠 York Chen ，智基创投创始管理合伙人。1992年至2000年，York任职新加坡上市公司宏碁电脑国际，负责处理宏碁从莫斯科到新西兰，从汉城至印度各地之行销、市场、通路及地区业务，创建与管理十余家宏碁子公司及合资企业。此前三年负责经营宏碁在墨西哥及前苏联的业务。作为第一位在克里母林宫公开演讲的华人，他在1991年5月为2000多位俄罗斯政治及工业的领导人, 做市场改革开放前景及策略的报告。加入宏碁之前，York曾任公共行政人员。York拥有台湾大学理学士及美国 Fordham 大学MBA学位，并结业于北京大学国际MBA。');
