INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(478, '北京', '', '摩根大通,宏碁Acer', '路昌基', '职位:  , 董事总经理', '互联网,移动互联网,医疗健康,环保能源,硬件', '朝阳区', '82cda3c051f96eacc19afaa145c6907a.jpg', '哈佛大学,加州大学伯克利分校,耶鲁大学', '美国中经合集团/ WI Harper Group', '路昌基先生是中经合董事总经理。路先生在高科技和风险投资行业有超过20年的丰富经验，他负责中经合的全球运营。路先生目前是天奈科技，康圣环球，科美生物技术公司及StemCyte公司的董事。路昌基先生共同创立并任美国iD Ventures合伙人。在此前，路昌基先生曾在宏碁集团、JP Morgan等工作，路先生拥有耶鲁大学的学士学位和哈佛大学的MBA学位，并于1986年获得加州大学伯克利分校法律系颁发的法律学位。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(479, '北京', '', '华登国际,新加坡政府直接投资GIC', '周微曦', '职位:  , 董事总经理', '互联网,移动互联网,硬件', '朝阳区', 'eed6dcfa8f760ce1aec3a66ad72f71f9.jpg', '加州大学伯克利分校', '美国中经合集团/ WI Harper Group', '周微曦先生拥有超过18年的私募基金、咨询以及技术经验，负责督导中经合旗下的美国信息科技公司。在加入中经合之前，他是华登国际的副总裁，并且掌管其投资组合公司的投资与管理。他亦曾任由新加坡政府经营的财团法人GIC的副总裁。在GIC，他负责美国私募基金的直接投资。在此之前，他担任一家名为BARRA的负责老年基金管理的财务顾问公司的资深顾问。他曾经是USI保险公司董事会成员，目前亦担任几家科技公司的董事职位，包括DivX和LogicEase等。周微曦先生负责中经合对Ultriva，GCT，天奈科技和美芯晟的投资。周微曦先生拥有加州柏克莱大学的MBA学位以及坎特伯里大学的电子工程学士学位。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(480, '北京', 'http://weibo.com/1681183570', '', '彭适辰', '职位:  , 董事总经理', '互联网,移动互联网,硬件', '东城区', '91a9ff086fc7e9845d848c3c039278f9.jpg', '北京大学,加州大学圣塔芭芭拉分校', '美国中经合集团/ WI Harper Group', '彭适辰先生是中经合北京办公室的董事总经理。他有超过二十年在大中华地区和美国高科技行业、直接投资和基金管理行业的从业经验。加入中经合之前，彭适辰是汉鼎亚太(H&Q)的董事总经理，负责大中华地区的直接投资业务。在2002年加入汉鼎亚太之前，他曾任富鑫创业投资集团的高级副总裁，负责美国和大中华地区的投资业务。他还担任日鑫科技创业投资公司及美鑫创业投资公司总经理。在1996年加入富鑫之前，彭适辰在LSI Logic Corporation工作七年，负责研发和亚太区市场发展工作。彭适辰先生投资过的项目包括展讯通信, 美国芯源系统, 中芯国际,OmniVision, NetIQ, 艾讯电脑, 诚创科技 纬创资通, 慧友科技), 华冠通讯等多家上市公司。彭适辰先生获得北京大学EMBA学位，美国加州Santa Clara大学工商管理硕士学位，以及德州大学电机工程硕士学位。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(483, '北京', '', 'TPG德太投资,掌上灵通', '刘宏祥', '职位:  , 合伙人', '互联网,移动互联网,硬件', '朝阳区', '13eead268f08b2f62ac389ab5ee832d5.jpg', '上海交通大学', '美国中经合集团/ WI Harper Group', '刘宏祥先生是中经合北京办公室合伙人，负责TMT行业的投资以及维护中经合所投资公司，基金投资人和战略合作者的合作关系以及融资事宜。刘先生目前是磊友公司的董事。在加入中经合集团之前，刘先生是美国TPG集团中国业务负责人，该公司提供全面的投资者关系和金融咨询服务，在纽约，加利福尼亚和北京均设有办事处。他还曾担任新华悦动传媒公司投资者关系总监，该公司是多元化的体育媒体公司。在此之前，刘先生是掌上灵通有限公司的投资者关系及企业发展事业部总监，该公司是中国无线互动娱乐产品及服务的领先供应商。他还曾在加州硅谷银行参与管理1亿美金的投资资金，主要为早期生命科学和科技领域的公司提供风险投资，以及提供融资结构的咨询。刘先生在在风险管理，商业银行和投资者关系方面有多年的经验。他毕业于圣地亚哥大学的金融和营销专业，并以优异成绩获得上海交通大学工商管理硕士学位。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(482, '北京', 'http://weibo.com/1751004223', '阳狮集团', '蒋亚萌', '职位:  , 合伙人', '互联网,移动互联网,传媒新媒体,硬件', '东城区', '38c22bcb1018fbc74448f8fafea5ea9c.jpg', '哈佛大学,外交学院,大连理工大学', '美国中经合集团/ WI Harper Group', '蒋亚萌负责互联网、新媒体、移动和其他TMT领域的投资。他负责新投资项目的评估和执行，并为中经合的已投资公司提供增值服务：策略发展、业务拓展和管理团队招聘。加入中经合之前，蒋亚萌是阳狮锐奇大中华区数字战略和投资并购总监。阳狮锐奇是全球第三大传播集团阳狮集团的媒体和数字业务的控股公司。期间，他负责控股集团层面的数字战略制定，帮助阳狮锐奇在数字媒体、互联网广告、移动互联网、社交网络等领域建立战略合作关系。同时，蒋亚萌负责为阳狮锐奇建立和开发全球领先的数字媒体投放平台。在美国哈佛商学院MBA学习期间，他在金沙江创业投资基金担任实习投资经理，并在WPP集团纽约总部的投资并购团队担任实习投资经理。蒋亚萌获得哈佛商学院MBA学位，外交学院硕士学位和大连理工大学学士学位。工作之外，他喜欢和创业者交朋友，沟通讨论和参加各类创业社交活动。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(481, '台湾', '', '华登国际', '朱永光', '职位:  , 董事总经理', '互联网,移动互联网,硬件', '台北市', 'c0c366bc5a67d423815a996742a6faff.jpg', '乔治华盛顿大学', '美国中经合集团/ WI Harper Group', '朱永光拥有超过20年投资及科技产业经验，朱永光先生负责美商中经合集团在台湾的投资业务。加入中经合之前，朱永光先生担任台扬科技集团下维扬国际的总经理，负责通讯相关产业的投资，更早以前，他是台湾大哥大电信公司创办团队之一，并担任太平洋集团旗下的太铱公司副总，从事Iridium Taiwan Gateway的建设-商业发展及营运，经营扩及台湾、菲律宾、印尼、汶涞、巴布亚纽几内亚。此外，朱永光先生还曾担任过华登国际的副总、台翔航太国际业务处长以及台湾戴纳科技公司副总。朱永光先生具有华盛顿大学航太工程硕士、宾州德瑞克斯大学自动控制系统工程硕士以及台湾成功大学机械工程学士资格。');
