INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(523, '北京', '', '用友软件', '吴政平', '职位:  , 董事长', '互联网,移动互联网', '朝阳区', 'e69b4fd901737e1e2dcabaa1d03bb08b.jpg', '中欧国际工商学院,江西财经大学', '用友幸福投资/用友', '吴政平，用友软件股份有限公司董事，北京用友幸福投资管理有限公司董事长。1979年就读江西财经大学会计系，获经济学士；2005年就读中欧国际工商管理学院，获工商管理硕士。1983年就职于国家某大型科研单位。1992年加入用友公司，历任公司董事、执行副总裁、高级副总裁、副总裁、财务总监。长期负责公司的财务管理和投融资业务，2001年组织推进和实施了用友软件的上市工作。具备丰富的企业投融资与管理经验。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(524, '北京', '', '用友软件', '贾文新', '职位:  , 总经理', '互联网,移动互联网', '海淀区', '0a8aaafb0c9240ed265c8efdf1373241.jpg', '清华大学', '用友幸福投资/用友', '贾文新，用友软件股份有限公司副总裁，北京用友幸福投资管理有限公司总经理。1984年就读清华大学自动化系，获工学学士；1991年加入用友软件股份有限公司，历任杭州分公司总经理，用友集团投资经营管理部总经理，用友软件股份有限公司助理总裁、企管部总经理，主导和参与过多个项目的投资工作并多年负责用友集团所投资企业的管理工作，有着丰富的软件企业管理经验和投资经验。');
