INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(526, '北京', '', '用友软件', '寇双翔', '职位:  , 投资总监', '互联网,移动互联网', '海淀区', 'e3e81322a7a33e198f48d171a394b0d1.jpg', '北京大学', '用友幸福投资/用友', '寇双翔，北京用友幸福投资管理有限公司投资总监。1994年就读于北京大学，获经济学学士。曾在亿坤管理咨询、用友软件等公司担任高级管理职务，专注于研究软件与IT服务领域，长期跟踪研究中国软件产业发展特点及趋势，参与、主持多个软件企业投资咨询项目。拥有12年的IT管理咨询和业务拓展经验。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(527, '上海', '', 'UT斯达康', '龚挺', '职位:  , 董事总经理', '互联网,移动互联网,消费品,现代服务,传媒新媒体,硬件', '卢湾区', 'b0dcd5b4480f6184dd9c9a6190866bce.jpg', '上海交通大学,普林斯顿大学', 'SIG海纳亚洲', '龚挺先生从2006年1月起担任海纳亚洲创投基金的董事总经理职务。在加入海纳投资集团之前，他曾是优势资本的管理合伙人。1996年至1999年期间，龚先生曾任斯达康公司的总经理兼UT斯达康的高级管理顾问。1993年至1996年期间，龚先生曾服务于一家房地产基金。另外，龚先生为Hotvoice公司的创始人和总裁，Hotvoice公司曾被誉为统一通讯技术的行业先驱。1990年至1992年期间，龚先生曾任罗切斯特大学助理教授和研究学者。龚先生拥有上海交通大学应用物理学士学位，并于1990年获得普林斯顿大学电子工程的博士学位。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(528, '北京', '', 'AT&T', '王琼', '职位:  , 合伙人', '互联网,移动互联网,现代服务,传媒新媒体,文化创意,硬件', '朝阳区', '364ffda4ea58bb59a33a259d5284e360.jpg', '上海交通大学,INSEAD欧洲工商管理学院,纽约州立大学', 'SIG海纳亚洲', '王琼女士于2006年加入海纳国际集团，参与了蓝讯科技、博纳国际、印象中国、酷讯搜索等项目的投资和退出，目前投资并管理着我乐网，佳美口腔、至尊租车、中国票务在线、酷盘、奇矩互动、游戏吧、99Fang搜索、美育儿童教育、长胜印染等数十个项目。从AT&T贝尔实验室的通信工程师开始，王琼女士在IT及电信领域有超过13年的工作经验，并经历了公司从初创到上市的过程。 在过去的6年中，她致力于在互联网、无线、娱乐、现代服务和高科技制造等领域的早期和中早期的投资。和创始团队成为事业的伙伴是她的遵循的投资理念。王女士拥有INSEAD的MBA学位，纽约州立大学的电子工程硕士学位及上海交通大学的电子工程本科学位。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(529, '上海', '', '中信,里昂证券', '徐炳东', '职位:  , 副总裁', '互联网,移动互联网,消费品,现代服务,传媒新媒体,硬件', '卢湾区', 'f6f74962f8e3352769c8c3991dfe54f8.jpg', '上海财经大学,英国曼彻斯特大学', 'SIG海纳亚洲', '徐炳东先生于2007年加入SIG，从事风险投资和私募股权投资工作，此前他曾就职于TDF Capital和中信资本。在他从事投资工作之前，他任职于CLSA香港和上海的投资银行部，帮助中国及香港企业实现融资上市和并购业务。徐先生毕业于曼彻斯特大学，拥有金融学硕士学位以及上海财经大学学士学位。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(530, '北京', 'http://weibo.com/1721677927', '', '杜海涛', '职位:  , 副总裁', '互联网,移动互联网,传媒新媒体', '朝阳区', '0a564b17e4c62f387e08c3e58a9e9af1.jpg', '对外经济贸易大学', 'SIG海纳亚洲', '杜海涛先生于2009年加入SIG。加入SIG之前，杜海涛先生就职于挚信资本任副董事，从事私募股权投资工作。在此之前，杜海涛先生在国家商务部工作，对于宏观经济分析及政府性投资基金管理拥有丰富经验。杜海涛先生拥有对外经济贸易大学国际金融硕士学位及会计学学士学位，杜海涛先生是中国注册会计师。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(531, '北京', '', 'UT斯达康', '李晔', '职位:  , 副总裁', '互联网,移动互联网', '朝阳区', '023a941c3591799a6de3bf5092051afd.jpg', '中国科学技术大学', 'SIG海纳亚洲', '李晔先生于2011年加入SIG，专注TMT行业的风险投资工作。此前李晔先生在通讯和互联网行业有十七年工作经验，在一系列成功的美国高科技公司主持产品研发。在Ask.com任搜索引擎在线系统的研发主管，开发了业界领先的基于结构化数据的搜索技术。在UT斯达康工作期间主持研发了最早大规模商用的基于IP的宽带接入网通讯设备。李先生对互联网的最新科技发展和商业生态模式有深刻的了解，并有丰富的高科技创业公司团队管理经验。李先生拥有中国科学技术大学计算机科学学士及美国Rutgers大学计算机科学硕士学位。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(551, '上海', 'http://weibo.com/1930386083', '集富亚洲', '涂鸿川', '职位:  , 董事总经理', '互联网,移动互联网,消费品,现代服务,传媒新媒体', '黄浦区', '19ff0a356732abbf03a89596c1f21b4b.jpg', '明治大学', '高原资本', '涂鸿川是高原资本中国创办人和董事总经理。他拥有16年创业投资和国际业务经验。涂先生专注于在中国的媒体行业（新媒体，互联网）, 无线通信和消费服务领域。在2006年，他作为早期投资者主导了高原资本对奇虎360 (2011年纽交所上市，NYSE: QIHU) 的投资，目前任职奇虎360的董事会董事。涂先生也先后投入了网康科技，维旺明(Viva)及趣游的投资并担任董事会董事，且参与悠视网的投资项目。加入高原资本之前，涂鸿川分别是集富JAFCO集团在北美的投资合伙人，并负责通信及高科技领域的创业投资业务。他同时负责在亚太区的业务拓展。在职业生涯早期(1995年至1999年)，涂鸿川在集富集团总部(东京)任全球IT投资部门负责人。在此期间他完成了对中国、日本和北美的数个宽带通讯和网络服务公司的投资。他也协助集富集团拓展了在全球市场和对美国背景公司的投资业务。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(552, '上海', 'http://weibo.com/1743145010', '英特尔,瑞士信贷', '叶冠泰', '职位:  , 合伙人 , , 董事总经理(已离职)', '互联网,移动互联网,传媒新媒体,硬件', '黄浦区', '036b0efa880635edbe8adb829b5bfc18.jpg', '康奈尔大学', '启明创投,高原资本', '叶冠泰2014年6月加盟启明创投担任合伙人，此前是高原资本上海办事处的董事总经理，他主要专注于早中期中国高科技企业的投资，尤其在互联网、移动互联网、数字媒体、电子部件和半导体公司. 他参与了维旺明, 趣游和途牛的投资并成为维旺明和趣游的董事，及途牛的董事会观察员。加入高原资本之前，叶冠泰是英特尔风险投资部的中国区董事总经理。加入英特尔之前，叶冠泰是瑞士信贷第一波士顿公司驻香港的投资银行家，负责亚洲除日本地区科技行业的投资银行业务，为亚洲的高科技公司提供服务。叶冠泰的职业生涯开始于赛普拉斯半导体公司。');
