INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(576, '国外', '', '', '杨光智', '职位:  , 合伙人', '互联网,移动互联网,消费品,现代服务,硬件', '', 'default_user.png', '康奈尔大学,美国密歇根大学', '华威国际集团CID', '杨光智先生，合伙人，负责华威集团美国区域的投资业务。杨先生不仅为华威集团带来了专业的法律经验，也带来了美国的风险投资专业经验。他目前在美新半导体和Ooyala任董事。杨先生拥有美国密歇根大学学位及康奈尔大学法学博士学位。他是加州律师协会的成员，曾在Jones, Day, Reavis & Pogue律师事务所和常在国际法律事务所从事律师工作。');
