INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(577, '台湾', '', '中华开发工业银行', '黄文骏', '职位:  , 合伙人', '互联网,移动互联网,硬件', '台北市', 'default_user.png', '台湾政治大学,台湾成功大学', '华威国际集团CID', '黄文骏先生，合伙人，带领一支专注于台湾IT及电子零组件产业投资的团队。黄先生的投资生涯开始于1991年。他于2001年加入华威集团，在此之前黄先生是中华开发工业银行的资深投资经理，黄先生目前在华威创立的鸿威光电担任董事长。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(578, '台湾', '', '中华开发工业银行', '谢仁评', '职位:  , 合伙人', '消费品,现代服务', '台北市', 'default_user.png', '台湾清华大学,台湾交通大学', '华威国际集团CID', '谢仁评先生，合伙人，所带领的投资团队专注于两岸整合主题的投资。在1990年代，谢先生在中华开发工业银行担任投资经理，投资在各区域的IT企业超过3,000万美元。谢先生是引导台湾风险资本进入韩国市场的先驱，曾带领中华开发银行的团队建立在汉城的分支机构。1999年，他加入了CDIB & Partners Investment Holding，与台湾的金融和产业界一起从事区域性投资，总规模达3亿美元。谢先生在投资部门担任资深副总裁，负责东南亚、韩国和台湾地区的投资。2002年，谢先生加入华威集团任合伙人，目前专注于台湾和中国大陆地区消费主题的投资。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(579, '台湾', '', '富士康,花旗银行', '林翰飞', '职位:  , 合伙人', '制造业,消费品,现代服务,硬件', '台北市', 'default_user.png', '宾夕法尼亚大学,国立台湾大学', '华威国际集团CID', '林翰飞先生，合伙人，自2004年开始担任华威集团的首席战略官。林先生的职业生涯开始于华尔街，拥有丰富的金融知识和经验。他曾在花旗银行企业战略办公室工作，此后被任命为花旗银行所罗门美邦兼并收购部门副总裁。除了金融领域，林先生还拥有创业经历和投资经验，他曾分别担任Asia Bioinnovations的CEO和富士康投资事业部总监。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(581, '上海', '', '中华开发工业银行', '侯文山', '职位:  , 合伙人', '医疗健康,消费品,现代服务,硬件', '徐汇区', 'default_user.png', '台湾政治大学,台湾交通大学', '华威国际集团CID', '侯文山先生，合伙人，拥有超过13年的投资和投资后管理经验，目前带领一支位于上海的团队，从事大中华区域的投资。除了丰富的风险投资经验外，侯先生也是一名闪存领域的专家。他的风险投资生涯开始于中华开发工业银行。在2004年加入华威集团以前，侯先生曾在汉友创投任副总裁，投资过多家企业，并担任董事长。在2006年至2009年间，他曾带领华威大陆的研究团队从事六大新兴领域的深入研究。侯先生目前专注于医疗和消费相关领域的投资。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(582, '台湾', '', '富士康,友达光电', '卢博彦', '职位:  , 合伙人', '制造业,硬件', '台北市', 'default_user.png', '美国伊利诺伊大学', '华威国际集团CID', '卢博彦博士于2010年从液晶显示产业加入华威集团。为响应台湾政府建立液晶显示产业的号召，卢博士于1995年从贝尔实验室回到台湾。他曾是达碁科技液晶显示项目的创起者，该公司于2001年与联友光电及2006年与广辉电子合并，成为今日的友达光电。卢博士在友达光电担任执行副总经理，负责其制造、供应链管理、质量控制和研发等方面的运营管理，为友达光电的成长做出了重要的贡献。2003年，卢博士获得台湾经济部颁发的杰出人士贡献奖。2006年，卢博士加入富士康，为其建立起液晶电视事业处，后成长为今日富士康的国际液晶电视事业。在加入华威以前，卢博士在市值20亿美元的龙腾光电担任总经理兼CEO，为其建组织，扩产能，提良率，在8个月内使龙腾光电转亏为赢。卢博士现专注于应用其技术背景和管理经验，与华威其它合伙人协作，为华威所投资企业改善运营和管理。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(583, '台湾', '', '中华开发工业银行', '李世仁', '职位:  , 合伙人', '医疗健康', '台北市', 'default_user.png', '南加州大学,台湾辅仁大学', '华威国际集团CID', '李世仁博士是华威集团负责生命科学产业投资的合伙人。李博士在投资银行 、医疗保健产业投资和项目管理领域拥有超过十六年的经验。李博士曾任易生创投基金/浩理生技管理顾问公司董事总经理。2009年7月至2010年10月， 李博士担任合裕管理顾问股份有限公司投资长。在加入合裕以前，他曾担任浩理生技顾问股份有限公司董事总经理三年。浩理生技为Coller Capita管理两支规模达1亿美元的生技基金。2000年10月至2006年6月期间，李博士任职台湾知名投资银行中华开发工业银行的资深协理，亦兼任中华开发全球生技基金管理公司总经 理。十一年间，李博士主导了超过2亿美元的医疗产业领域投资。在进入国际创投领域之前，李博士藉由执行环境工程项目，累积了各产业面的丰富经验。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(580, '北京', 'http://weibo.com/1879778684', '中华开发工业银行', '罗文倩', '职位:  , 合伙人', '互联网,移动互联网,消费品,现代服务,传媒新媒体,硬件', '朝阳区', '224ebf56c9cbfb7ade7d7d3690b9695a.jpg', '国立台湾大学,台湾政治大学', '华威国际集团CID', '罗文倩女士是负责华威集团中国大陆地区投资的合伙人，也是华威北京办公室的总经理。罗女士在台湾和北美累积了丰富的技术相关投资经验。她于1992年加入中华开发工业银行，从事直接投资和中华开发银行海外事业部的工作。五年间，罗女士在台湾和美国参与了多个PC、半导体、通讯、材料等领域的项目。1998年，罗女士被派往硅谷，建立中华开发银行在硅谷的风险管理公司，管理1.5亿美元的投资金额。在此之后的六年里，罗女士担任执行总监，负责投资业务、基金募集和投资企业管理等工作。2004年，罗女士加入华威集团，为华威建立中国大陆的投资团队和投资项目。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(584, '北京', 'http://weibo.com/1726602910', '摩根大通,摩根斯坦利', '汪潮涌', '职位:  , 董事长', '互联网,移动互联网,医疗健康,环保能源,制造业,消费品,现代服务,传媒新媒体,教育培训,金融,文化创意,硬件,农业', '朝阳区', '5b4a2c110a8fb4022afbdbc8e017ead4.jpg', '清华大学,华中科技大学,罗格斯大学', '信中利资本', '汪潮涌先生为北京信中利投资有限公司的创始人董事长及总裁，在中国投资界非常活跃及有影响力。汪潮涌先生1984年毕业于华中科技大学，1985年由清华大学公派研究生赴美留学，获罗格斯大学商学院MBA学位，成为第一批大陆留学生进入华尔街的投融资专家。1987-1990年，汪先生任职于美国摩根大通银行纽约总部，从事不动产融资与金融资产证券化工作；1990-1993年，汪先生任职于美国标准普尔纽约总部，担任纽约机构融资债券部副主任，3年内参与评级的资产抵押债券超过300亿美元；1993-1998年，汪先生就职于美国摩根士丹利中国区首席代表。1998-1999年，汪先生受国家开发银行邀请担任全职高级顾问，参与筹备和组建国家开发银行的投资银行业务；任期届满后，汪先生与管理团队共同创建信中利资本集团，任董事长兼总裁至今。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(585, '北京', '', '', '张晶', '职位:  , 董事总经理', '消费品,现代服务,传媒新媒体,教育培训,文化创意', '朝阳区', 'ba6f44fcc382b9cd25d4e426dcf33422.jpg', '长江商学院,中国政法大学', '信中利资本', '张晶，信中利资本集团董事总经理、合伙人，多年创业投资经验，负责投资了龙文教育、华谊兄弟、东田造型等明星企业，曾任国内知名会计师事务所合伙人、大鹏证券投资银行部高管，总融资额超过100亿人民币；拥有中国政法大学硕士学位，长江EMBA，注册会计师、注册资产评估师、中国证券从业资格。');
