INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(629, '北京', 'http://weibo.com/p/1005051997449741', 'HP惠普', '卜君全', '职位:  , 投资总监', '互联网,移动互联网', '朝阳区', 'bd437440819580d3aa7a80afdaea0846.jpg', '宾夕法尼亚大学,威斯康辛大学麦迪逊分校,台湾清华大学', '英特尔投资Intel Capital', '卜君全(Chris Pu)先生现任英特尔投资中国区总监。加入英特尔之前，卜先生共同创建了普讯创投（WK Technology Fund）硅谷办公室，并成功投资了多家企业，包括一个IPO和四个收/并购项目。此前卜先生任惠普亚太区业务经理，负责企业解决方案业务。卜先生拥有沃顿商学院MBA学位，威斯康星大学麦迪逊分校控制工程硕士学位，以及台湾清华大学工程学士学位。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(630, '北京', 'http://weibo.com/p/1005051738686512', '掌上灵通,北极光创投', '杨镭', '职位:  , 创始合伙人', '互联网,移动互联网', '朝阳区', 'f31d985f0327065a86181066ae6b3b33.jpg', '清华大学', '泰山天使', '杨镭是中国一位执着的连续创业家。在中国，美国成功创立几家公司后，他于2003年从硅谷回到中国，成为掌上灵通的首席执行官。其后他领导掌上灵通获得了高额盈利和增长，仅用了11个月的时间就在美国纳斯达克成功上市。杨镭一直是位活跃的天使投资人，在创办泰山天使之前，他还创办领航资本并曾在北极光创投任投资合伙人，北极光创投管理高达1.2亿美金的风险投资基金。他曾得到许多荣誉称号，如：“中国十大CEO”、“2005年新经济最有影响力人物”和“2007年最佳天使投资人”。目前杨镭先生也担任专注于海外高科技私募股权投资公司——华山资本的创始合伙人及董事总经理。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(631, '国外', '', '', '酷伯乐', '职位:  , 创始合伙人 , , 创始合伙人', '互联网,移动互联网', '', 'd570ff9a90a791ca69c275a9e8bbcb05.jpg', '兰卡斯特大学', '泰山天使,泰山兄弟创始基金TXD Ventures', '酷伯乐先生是一位中国和欧洲互联网和TMT领域的企业家和活跃投资人。他与2003年初次来到中国，并从此对中国有了深入的了解。他于2005年创立了一家互联网公司。从2006年开始，他开始积极地从事天使投资，并与杨镭、陈亮于2008年共同成立了中国最有影响力的天使基金—泰山天使创业基金。他在德国罗伊特林根的欧洲商学院和英国兰卡斯特大学管理学院获得工商管理学位。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(632, '北京', 'http://weibo.com/p/1005051676831722', 'Disney迪斯尼,易观国际', '陈亮', '职位:  , 创始合伙人 , , 创始合伙人', '互联网,移动互联网', '朝阳区', 'b0128b1849059f8b423741dc05d3eaef.jpg', '', '泰山天使,泰山兄弟创始基金TXD Ventures', '陈亮从2007年起为泰山在中国的创立做准备工作。从泰山天使最初的筹建到全方位的运作，他贡献许多的精力和资源。他在泰山负责日常运营和投资工作。他曾主导投资了拉手网、佳品网、优士网、喜事网等一系列高成长的项目。 在加入泰山天使之前，他曾是投中集团投资银行部的副总裁，还曾任国际著名风投杂志红鲱鱼的亚洲太平洋地区的主管。在此之前，他还曾在易观咨询和沃特迪斯尼公司任职。他先后就读于辽宁工业大学和美国的西肯塔基大学，并获得MBA学位。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(633, '上海', '', '', '李广新', '职位:  , 执行董事', '互联网,移动互联网,医疗健康,环保能源,制造业,消费品,现代服务,传媒新媒体,教育培训,金融,文化创意,硬件', '静安区', '9400dadaa6c210a63a5952527b4d368e.jpg', '上海交通大学', 'DFJ/德丰杰中国', '李广新博士是德丰杰龙脉中国基金的发起合伙人，在企业运作、科技研发、高科技产业化以及风险投资领域拥有20余年的丰富经验。2000年开始李博士在上海张江创业投资公司，鼎嘉（国际）投资管理有限公司和上海创投 — 鼎嘉（国际）投资有限公司担任合伙人。李博士完成的投资项目包括炬力半导体、微创医疗科学、凯利泰医疗、世芯科技、EDDA、禹华通信技术公司和易保网络技术公司，其中炬力半导体2006年在美国纳斯达克上市，微创医疗2010年9月在香港主板上市，世芯电子2010年10月在台湾新兴市场上市。李博士的职业生涯始于1994年在上海金桥现代科技园高新技术创新中心担任主任，曾担任园区留学人员创业基地负责人。此后5年间，这个中心共扶持了超过20家创业公司，其中3家公司后来获得很好的回报。1998年，李博士至密歇根大学担任研究员，同年在戴姆勒克莱斯勒公司担任特聘研究员。李博士目前担任中国投资协会创业投资专业委员会副会长，上海市创业投资协会副会长职务。李博士拥有上海交通大学应用力学博士学位。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(635, '北京', 'http://weibo.com/p/1005051734548755', '三星,华旗资讯/爱国者,联想', '曲敬东', '职位:  , 执行董事', '互联网,移动互联网,硬件', '朝阳区', '0282fef77022dd5a57db3d826df9c576.jpg', '清华大学,北京理工大学', 'DFJ/德丰杰中国', '曲敬东先生是德丰杰龙脉中国基金的投资合伙人，具有24年大型中国企业及全球化企业的战略、管理、营销、融资、投资、上市等拥有运作经验，是中国IT领域最知名的高管之一。曾经自主创业，并任职于民企、国企及外企高管，熟悉各类企业的文化、理念、价值观及管理运作特点。曲敬东先生，1988年开始进入中关村创业，先后成立两家创业公司，实现了原始积累。95年赴美跨国收购酒店业务，成为中国最早期在美国收购资产的投资人之一。1998年，曲敬东先生加盟联想集团，曾担任联想集团副总裁，在产品、客户、渠道、市场方面积累了丰富的业务经验。其主管联想集团华东地区和全国市场销售工作期间，取得了优异成绩，助力联想品牌和销售业绩成为同期行业第一名。2007年3月，曲敬东先生加盟三星电子，担任三星电子大中华区常务董事、全球副总裁，主管三星中国区商用业务，成为当时三星最年轻的中国籍高管。在他的领导下，三星商用提出用3至5年的努力，成为商用领域专业提供商前三甲的战略目标。其后，迅速制定了商用业务战略及实施策略，打造了系统化的产品、客户、渠道及市场体系。2007年至2009年间，三星在中国商用领域连续两年实现了增值100%的快速成长，成为三星全球的业务典范。2010年1月，曲敬东先生受邀担任爱国者电子科技有限公司总裁兼CEO，帮助爱国者制定了全新的发展战略，实现了业务的快速增长，并致力于将公司打造业界一流的国际化高科技企业。2011年起，曲敬东先生担任鑫根资本 执行合伙人 ，主要负责企业融资、并购、重组及基金管理。主要参与爱国者融资并购、康得新融资及并购、普华雅龙融资、城建环并购，农业产业基金募集等项目。曲敬东先生拥有清华大学高级工商管理硕士学位。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(634, '上海', '', '', '赵光斗', '职位:  , 执行董事', '互联网,移动互联网,硬件', '静安区', '651ed2fb30d787f34a223776baf10542.jpg', '斯坦福大学', 'DFJ/德丰杰中国', '赵光斗先生是德丰杰龙脉基金的创始合伙人。赵先生对于国际和国内高科技领域的投资，尤其在半导体、硬件和软件等方面有着丰富的经验。他是Cadence Design Systems的创始人之一，并曾担任Cadence的副总裁和亚洲区域总经理，负责亚洲和日本地区的产品销售、市场营销和运营管理。离开Cadence后，他成立OCRON并担任该公司总裁和首席执行官，成为光学字符识别和文档管理空间开发的一名先驱，直至该公司于1993年被Umax集团并购。在于Umax集团任职期间，赵先生负责企业投资和营销业务。此后，赵先生又创立了TAMC并担任合伙人, 旗下管理的TechGains I基金3年活跃投资期的内部收益率超过100%。赵先生于1999年离开并成立美国龙脉公司任董事会主席，该公司是最早一批少数主张在中国积极拓展业务和市场的风险投资基金和孵化器企业之一。与此同时，赵先生也成为AboveNet（ABVT）、Oplink（OPLK）、Omnivision（OVTI）和NetScreen（JNPR）等公司的早期投资者。赵先生目前担任多家中国和美国公司的总裁和董事。赵先生分别获得乔治亚州立大学的物理学硕士学位以及斯坦福大学的航天工程硕士学位。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(636, '上海', '', '', '李忠强', '职位:  , 合伙人', '互联网,移动互联网,环保能源,硬件', '浦东新区', '921f0635104cfb3447e3879333acf54e.jpg', '上海交通大学', 'DFJ/德丰杰中国', '李忠强先生有十年的风险投资和创业孵化经验. 2012年7月加入德丰杰-龙脉基金担任合伙人。此前李先生曾担任江南资本投资总监负责TMT和先进制造领域投资，参与了派恩科技和驴妈妈的投资。此前于2006年李先生开始在德丰杰-龙脉基金担任高级副总裁等职，主要负责TMT和清洁技术领域的投资，已完成对五个项目的投资并担任两家公司的董事，完成的项目中有一个项目实现了并购退出，协助两个项目成功实现后续融资。具体项目领域涉及半导体、软件、互联网和新能源汽车等。加入德丰杰龙脉基金之前，李先生在汉世纪创业投资管理有限公司担任高级经理, 参与过半导体、游戏软件、手机维修等五个项目的投资，主导了其中三个项目并担任董事。作为团队成员李忠强还参与了珠海炬力（纳斯达克代码ACTS）和万得资讯的投资并参与了珠海炬力公开上市准备工作。在加入汉世纪公司前，李先生曾担任上海交大科技园投资部总经理及其母公司上海交大产业集团投资部副总经理, 参与了多个项目股权投资投资（其中慧谷信息和交大欧姆龙实现公开上市）以及另外两家下属企业的公开上市融资准备工作。李先生拥有上海交通大学的机械工程学士学位和MBA硕士学位。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(637, '上海', '', '', '王岳华', '职位:  , 副总裁', '互联网,移动互联网,硬件', '静安区', '8f5ee765f893a69081927c03efb51429.jpg', '台湾交通大学', 'DFJ/德丰杰中国', '王岳华先生具备有18年半导体行业内经验，在2011年12月加入德丰杰龙脉中国基金团队。王岳华先生在18年的从业经验里从事市场开发，技术支持以及渠道销售等等不同的工作岗位。 在加入德丰杰龙脉中国基金之前，王岳华先生服务于群众电子商务有限公司担任首席执行官职务，在服务期间成功的打开了移动互联网及IPTV 渠道，并在商务平台开台的三个月内，快速的积累了10万以上有效注册会员。王岳华先生在过去几年一直关注物联网，无线传感网络，智能节电等市场发展与应用，协助群众电子商务作为智能后台处理平台开展了很多的应用。在此之前，王先生服务于加拿大4G芯片供应商Wavesat,担任亚太区销售总监。在这个岗位上，成功的开发了WiMAX 16d的客户及市场，稍后也成功的完成几家 ODM  厂家的LTE 产品design win,建立和华为LTE基站的合作关系，并将产品导入中国移动的TD LTE 实验网。2002年到2008年期间，王岳华先生服务于北美手机功放供应商RF Micro Device, 担任技术支持及市场发展等不同职务。在此之前，王岳华先生在Garmin, SiRF 担任研发及技术支持等工作岗位。王岳华先生拥有台湾交通大学的电信工程硕士学位。并于IEEE Transaction 发表数篇关于微波技术的技术文献。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(638, '上海', '', '', '王雨荍', '职位:  , 副总裁', '互联网,移动互联网,医疗健康,文化创意', '静安区', '3ded867afd2f2ade416bb76556b37a8c.jpg', '', 'DFJ/德丰杰中国', '王雨荍，具有四年投资银行及企业管理的工作经验,并在移动互联网领域具有一定的经验积累。2009年起，就职于中诚信集团(穆迪中国),主要负责投资与参投企业的运营管理以及后续资本运作。曾参与投资管理的企业包含了TMT、医疗、旅游文化、新材料等多个领域，其中成功经历包括4家企业的投融资、2家企业的战略并购、1家企业的产业链整合，代表企业有亚布力旅游 (多伦多上市)、康得新（中小板上市）、神州智联、江苏中卡等。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(639, '上海', '', '普华永道,罗兰贝格', '孔翔飞', '职位:  , 主管合伙人', '互联网,医疗健康,环保能源,制造业,消费品,现代服务,金融,硬件', '浦东新区', 'b6e93cc7652aa52ca5c9159147d281be.jpg', '伦敦大学', '璞玉投资Jade Invest', '孔翔飞，璞玉投资管理公司主管合伙人。此前曾在伦敦普华永道兼并收购服务部担任战略咨询顾问，在中国罗兰贝格战略咨询部和瑞典领事馆投资顾问部工作过，还曾在朗新软件公司任职。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(643, '北京', 'http://weibo.com/1809774097', '', '薛军', '职位:  , 创始合伙人', '互联网,移动互联网,医疗健康,环保能源,现代服务,文化创意', '海淀区', '53a3f76e73fb82c1b8d05018495cb8d1.jpg', '清华大学', '启迪创投', '薛军先生现任清华科技园启迪创投创始合伙人。薛军先生曾任职于国家信息中心和IBM公司。2002年开始从事创业投资，投资的公司涉及软件技术、芯片设计、信息安全、通讯网络技术和传感器技术等领域，如展讯通信。');
