INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(651, '浙江', 'http://weibo.com/p/1005052145291155', '阿里巴巴', '马云', '职位:  , 创始人', '互联网,移动互联网,文化创意', '杭州', '965f662612247a7c966e5d372def788b.jpg', '杭州师范大学', '云锋基金', '马云，云锋基金创始人。阿里巴巴创始人及董事会主席。1988年担任杭州电子工学院英文及国际贸易讲师；1995年创办中国第一家互联网商业信息发布网站“中国黄页”；1998年出任中国国际电子商务中心国富通信息技术发展有限公司总经理；1999年创办阿里巴巴。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(650, '上海', '', '荷兰银行,施罗德基金', '洪伟力', '职位:  , 执行董事合伙人', '互联网,移动互联网,制造业,消费品,现代服务,硬件', '静安区', 'c4887bc08ef97ff1c77957a068fe0a19.jpg', '复旦大学', 'KTB Ventures', '洪伟力先生，KTB的执行董事和上海代表处的合伙人。 加入KTB之前担任星展亚洲融资上海代表处首席代表，并曾任荷兰商业银行上海分行副总裁，以及在英国施罗德基金管理集团伦敦总部，从事全球股权投资基金管理。在金融投资领域他拥有十六年的专业经验。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(653, '浙江', '', '分众传媒,通用电气', '黄鑫', '职位:  , 董事总经理', '消费品,现代服务,金融,文化创意', '杭州', 'd149c9cc4c57fc0bbcc652c3844f3897.jpg', '复旦大学,中欧国际工商学院', '云锋基金', '黄鑫，云峰基金董事总经理。黄先生拥有16年国内外资本市场投资经验和跨国公司财务管理经验。在加入云锋基金之前，黄先生在开拓投资担任副总裁，专注于文化传媒、消费、金融等领域，并完成了华谊兄弟、中国银联、蓝科高新、光线传媒、安信地板等投资。曾任聚众传媒副总裁全面负责聚众传媒的财务、融资及IPO事宜，之前在美国通用电气公司的中国和美国分部历任各级财务职务，在加入聚众传媒时为通用电气公司先进材料业务部特种塑料亚太区财务总监。毕业于复旦大学管理学院获会计学学位，2011年毕业于中欧商学院EMBA。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(652, '上海', 'http://weibo.com/p/1005051847428005', '分众传媒', '虞锋', '职位:  , 创始人', '互联网,移动互联网,消费品,现代服务,传媒新媒体,金融,文化创意', '黄浦区', '5aa4f5fa8f80099ad15bba096e255ab7.jpg', '复旦大学,中欧国际工商学院', '云锋基金', '虞锋，云锋基金创始人、主席。2003 年创办上海聚众目标传媒有限公司，并担任该公司的创始人兼CEO；2006 至2008 年，担任分众传媒控股有限公司的董事局联合主席及总裁；2010 年创办云锋基金。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(654, '上海', '', '麦肯锡,黑石集团', '郭师尧', '职位:  , 董事总经理', '医疗健康,环保能源,制造业,消费品,现代服务,农业', '静安区', '2bc778104bb8350fa2650537260c73f6.jpg', '麻省理工学院MIT,美国爱荷华大学', '云锋基金', '郭师尧，云峰基金董事总经理。郭先生拥有13年大中华地区私募投资、海外并购和财务分析经验，并专注于零售、消费品、医疗器械、农业和化工等行业的投资。在加入云锋基金之前，郭先生在黑石集团担任董事，曾领导多个大型投资项目，如黑石投资6.31亿美元于中国蓝星集团、中国投资公司投资7亿美元于蒙古的铁矿石公司。曾任美国泰山投资的高级投资经理、麦肯锡咨询顾问。毕业于麻省理工学院，获取土木工程硕士；以年级第一名的优异成绩毕业于爱荷华州立大学获取土木工程学士学位。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(655, '上海', 'http://weibo.com/p/1005052600821072', '麦肯锡,UT斯达康,KPCB凯鹏华盈', '李颖', '职位:  , 董事总经理', '互联网,移动互联网,环保能源,消费品,现代服务,硬件', '黄浦区', '755d5bf24f22cb57a61601e75fb99277.jpg', '麻省理工学院MIT', '云锋基金', '李颖，云锋基金董事总经理 。李女士拥有十多年的企业管理和投资经验，专注于消费服务、高科技、清洁能源等领域的投资。在加入云锋基金之前，李女士是KPCB美国总部的合伙人，负责KPCB中国公司的筹建战略及股权投资，如已成功上市的高德软件、启明星辰和阳光电源，并深入协助全球已投资公司在中国的业务，如谷歌，亚马逊等公司。在此之前，李女士担任UT斯达康IPTV事业部的副总经理，拥有丰富的运营管理经验。曾在麦肯锡公司的硅谷分公司担任管理咨询顾问。期间，帮助多个世界500强企业的董事会及最高管理层设计公司的产品营销战略，内部管理流程，购并策略等。毕业于麻省理工学院，她用三年时间取得了三个学位：计算机科学学士、经济学学士，和电子工程及计算机科学硕士。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(656, '浙江', '', '', '朱艺恺', '职位:  , 董事总经理', '互联网,移动互联网,环保能源,传媒新媒体,文化创意,硬件', '杭州', 'e9ef90cd4fa5ef9608079649ec9f45cd.jpg', '复旦大学,长江商学院,南京邮电大学', '云锋基金', '朱艺恺，云锋基金董事总经理。朱先生拥有多年外资和国有中大型企业经营管理经验和投资经验，并专注在通信，软件，互联网，物流信息化，文化产业，清洁能源等领域，作为项目经理参与了张艺谋北京印象系列的5000万美元的投资并担任北京印象公司首席运营官。在加入云锋基金之前，朱先生曾任上海亿通国际股份有限公司常务副总经理（主持工作），上海电子数据交换网络服务有限公司董事长，上海付费通公司董事以及阿尔卡特（中国）有限公司电子商务网络部中国区总监。1986年南京邮电学院通信工程本科毕业；1992年复旦大学经济学院研究生毕业；2000年复旦大学管理学院及挪威管理学院MBA毕业；2011年长江商学院EMBA毕业。高级工程师。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(657, '上海', '', '', '赵峻波', '职位:  , 董事总经理', '医疗健康,制造业,现代服务,教育培训,金融,文化创意,农业', '黄浦区', '5db29823e71223989375b8d10bb8d892.jpg', '中欧国际工商学院,东华大学', '云锋基金', '赵峻波，云锋基金董事总经理。赵女士拥有22年国际商务、国际咨询及金融服务管理经验和投资经验。目前专注于金融服务、文化教育、健康养老等现代服务业；先进制造业和农业等行业的投资。在加入云锋基金之前，赵女士在上海国际集团金融服务有限公司担任董事长，曾领导多个重大投资项目。赵女士取得了东华纺织工程学院纺织工程工学士学位。于2001年毕业于中欧国际工商学院，获得工商管理硕士学位。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(660, '国外', '', '', '胡猛', '职位:  , 创始合伙人', '互联网,移动互联网,制造业,消费品,现代服务,金融,硬件', '', 'fa20af1b50f60ccde104ed42d33a4778.jpg', '', '风和投资', '胡猛，风和投资创始合伙人。胡猛是中国大陆最早的专业投资人，在亚洲众多的行业拥有超过22年的投资经验。他与中国顶级的商界人士保持着良好的关系。胡猛多次参与中国上市股票和企业私有股权的投资，成功案例包括：2006年在深圳证券交易所上市的高新兴电信，这是一所高科技通讯企业。1991至2005年之间，胡猛在大中华区的不同领军投资和资产管理公司担任要职。其中包括在华夏证券担任投资委员会委员以及委托资产管理总部总经理，管理中国最大的投资组合之一，掌管整个机构的所有重要的投资活动。1998年，胡猛代表中国有色集团成功收购赞比亚谦比希铜矿。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(661, '国外', '', '', '陈立武', '职位:  , 董事长', '互联网,移动互联网,硬件', '', '29c920d14707bc9a7f1524c3180cf6f6.jpg', '麻省理工学院MIT,南洋理工大学,旧金山大学', '华登国际投资', '陈立武先生1987年创立华登国际并担任董事长，积极活跃在风险投资行业已逾20年。他擅长跨境和早期阶段的技术投资。创立华登前，他曾在Chappell公司担任副总裁， 在EDS 核公司和ECHO能源公司担任高管职务。陈立武先生在新加坡南洋大学获得物理学士学位，在美国麻省理工学院取得核工程硕士学位，并在美国旧金山大学获得工商管理硕士学位。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(659, '浙江', 'http://weibo.com/p/1005051002363820', '东方通信', '潘水苗', '职位:  , 董事', '互联网,移动互联网,制造业,消费品,现代服务', '杭州', 'd0184071229dc7c32ec6c3e2a1c9719e.jpg', '浙江大学', '云锋基金', '潘水苗，云锋基金董事。潘先生拥有十多年的战略投资与大型企业管理经验，并专注在消费、移动互联网、先进制造业等领域。在加入云锋基金之前，潘先生曾任杭州鸿雁电器有限公司总经理，东方通信股份有限公司总裁兼副董事长，浙江万马集团有限公司总裁兼浙江万马电缆股份有限公司董事长等职务。毕业于浙江大学，获工业自动化专业学士和自动控制理论及应用专业硕士学位。2003年毕业于中欧国际工商学院EMBA。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(662, '北京', 'http://weibo.com/p/1005051891518995', 'Google', '李文飚', '职位:  , 董事总经理', '互联网,移动互联网,传媒新媒体,硬件', '朝阳区', '7a3529414a81cf2b82d3f674f500fca6.jpg', '华中科技大学,旧金山大学', '华登国际投资', '李文飚，华登国际董事总经理。专注于互联网、通讯行业的投资。李文飚于2008年加入华登国际。在华登之前，他曾在金沙江风险投资公司工作。 李文飚拥有丰富的运营经验，曾在包括谷歌在内的世界知名科技企业、以及初创公司供职逾18 年。在谷歌工作期间，李先生开拓了与包括中国移动在内的世界顶级移动运营商的合作，负责研究、发布多项开创性的移动产品和服务。李先生早期还在美国硅谷和中国与人共同创办几家成功的互联网和移动服务公司。李先生毕业于华中科技大学计算机系并获得学士学位。他还荣获旧金山大学理科硕士及金门大学高级工商管理硕士学位。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(663, '北京', '', 'IBM', '黄庆', '职位:  , 董事总经理', '环保能源,硬件', '朝阳区', 'd623a06666dc925fe9097907c560513f.jpg', '加州大学伯克利分校,四川大学', '华登国际投资', '黄庆，华登国际董事总经理，专注于半导体、清洁能源方面的投资。黄庆2005年加入华登国际。此前， 他已从事半导体行业的技术、管理及业务开发等逾15年。黄庆曾在IBM微电子担任设计顾问工程师，还曾在Chromatic Research担任高级技术人员。 黄庆与他人共同创建了Silicon Access 网络公司，担任亚洲VLSI设计和业务发展部的副总裁。黄先生曾在中国四川大学就读物理学专业，后在加利福尼亚大学伯克莱分校获得物理学学士学位和电气工程博士学位。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(664, '北京', '', '爱立信', '王一敏', '职位:  , 董事总经理', '环保能源,现代服务,硬件', '朝阳区', 'a3cd1e5698a3e27a6d7292b13c119bf8.jpg', '加州大学伯克利分校,科罗拉多大学', '华登国际投资', '王一敏，华登国际董事总经理。专注于通讯、清洁能源、以及新兴技术与服务等领域的投资。王一敏2005年加入华登国际。加入华登前，她曾担任爱立信(中国)有限公司市场部总监。在早期职业生涯中，王一敏曾在concert 通讯公司业务拓展部任高级经理，还在MCI担任过高级系统工程师。王女士在美国加州大学贝克莱分校获得工商管理硕士学位， 并在科罗拉多大学获得电信工程硕士学位和心理学学士学位。');
