INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(442, '北京', '', '优势资本', '郭荣', '职位:  , 合伙人', '互联网,移动互联网', '朝阳区', '125ba9189ed6c65d5f7a0f4598185123.jpg', '清华大学', '芳晟股权投资基金', '郭荣先生有着多年的企业高层管理和资本运作经验，尤其在互联网、IT服务、电信增值服务、连锁经营等领域内进行了长期的经营管理和资本运作实践，对于企业战略制定、管理信息化和连锁经营运作等方面有丰富的经验。他曾担任数家公司的战略顾问和融资顾问。郭荣先生曾任优势资本高级副总裁，主要负责公司投资业务的运作。加入优势资本之前，郭荣先生于2003年创立了北京博德威投资顾问有限公司，并担任总经理。其间运作了数家企业的直接股权投资，行业涉及网络游戏、手机游戏、电信增值、连锁经营等。在此之前，郭荣先生曾担任美国二十一世纪不动产中国区下属MLS网络科技有限公司（主营IT技术服务和IT管理咨询业务）CEO；中国铁通下属中通网络科技产业有限公司（主营铁通全国的电信增值业务）CTO；清华大学下属德天软件公司常务副总裁。郭荣先生于1995年毕业于清华大学，获得固体力学和机械制造双学士学位，并于1998年获得清华大学经济管理学院管理科学与技术硕士学位。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(460, '广东', '', '', '张云鹏', '职位:  , 合伙人', '环保能源', '深圳', '5234a079ff9bf5238441ae1f8fb90eee.jpg', '西安交通大学,中科院', '松禾资本', '张云鹏，西安交大工学学士，中科院广州能源所工学硕士，松禾资本业务合伙人。多年从事风险投资及项目管理工作，积累了丰富的项目投资经验和企业管理经验。主要投资项目有：达意隆 (002209)、天元电机、迪森热能、西陇化工、邦凯电池、五峰米业、科隆化工、骆驼蓄电池等。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(424, '浙江', '', '阿里巴巴,中信,北极光创投', '张鸿平', '职位:  , 董事总经理', '互联网,移动互联网', '杭州', 'e30d426c2d4c4d33f03b6d3d7dc1b1ae.jpg', '清华大学,旧金山大学,罗切斯特理工学院', '阿里巴巴/阿里资本', '张鸿平现任阿里资本董事总经理兼阿里巴巴集团副总裁，负责投资和战略相关的工作。张鸿平从工程师开始他的职业生涯，之后拥有16年从事与中国相关的投资、管理咨询和业务拓展的经验。在加入阿里巴巴之前，张鸿平于2005至2011年在北极光创投从事风险投资工作，并在多家公司出任董事。之前，他曾任Lantern Communications亚太区业务发展总监，和ANDA Networks 大中华区总经理。1998年，张鸿平在美国硅谷共同创立了ServGate Technologies，并担任业务发展副总裁。张鸿平于1995年加入中信集团（CITIC）并负责投资基金管理和战略咨询工作，曾负责了海尔等多家中国企业以及跨国企业的战略咨询项目，后出任中信咨询公司在美首席代表。张鸿平曾担任NetScreen及清华紫光的顾问工作。张鸿平获得了清华大学光学工程学士学位，美国罗切斯特理工学院影像科学硕士学位和旧金山大学工商管理硕士学位。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(425, '北京', '', '微软,百度,Cisco思科', '汤和松', '职位:  , 战略投资部总经理', '互联网,移动互联网', '海淀区', 'f09a8de535c956fedb5090ca6326f3c6.jpg', '清华大学,斯坦福大学,芝加哥大学', '百度投资', '汤和松是百度战略投资部总经理，此前曾在微软任大中华区战略投资总监、CISCO思科业务拓展经理等。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(426, '北京', 'http://weibo.com/1668938145', '', '凌代鸿', '职位:  , 创始合伙人', '互联网,移动互联网,医疗健康,环保能源,消费品,现代服务,传媒新媒体,教育培训,文化创意,硬件', '海淀区', '7783f6a895ed9c60ba040807f5e42af1.jpg', '中国人民大学', '旦恩创投', '凌代鸿先生1998年在图文影像和印刷包装行业(简称GPP行业)创立兆迪公司，到2003年将公司发展为全国市场份额第二的龙头企业。2008年公司业务稳定后，辞去兆迪CEO，担任兆迪控股公司董事长。2009年正式进入创业投资领域，关注生物制药，物联网、消费升级，TMT中的O2O领域等。从2009年至今面谈过近两千个团队，被业界称为最勤奋的天使投资人，积累了丰富的一线投资判断能力。凌代鸿先生持有人民大学管理学硕士学位。');
