INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(340, '北京', '', '麦肯锡', '平苹', '职位:  , 执行董事', '互联网,移动互联网', '朝阳区', 'a9a7412df959d8dbf5a17303a16b183c.jpg', '北京大学,耶鲁大学', '成为资本', '平苹于2003年加入成为资本担任执行董事。平苹曾是麦肯锡管理咨询公司北京办事处的咨询顾问；此后她参与创立了一家由跨国公司投资的互联网软件公司。她曾就读于北京大学国际经济系、获得耶鲁大学商学院MBA学位。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(338, '上海', 'http://weibo.com/3085372503', '兰馨亚洲投资', '李世默', '职位:  , 创始人及执行董事', '互联网,移动互联网,环保能源,制造业,消费品,现代服务,教育培训', '静安区', '6d26f12e295d958a1bea6aa8233ff365.jpg', '斯坦福大学,加州大学伯克利分校', '成为资本', '李世默，成为资本创始人，兼任中欧国际工商学院董事、春秋综合研究院研究员、清华─卡内基全球政策研究中心咨询委员会成员等。在创立成为资本之前，李先生是兰馨亚洲基金的合伙人，在中国和亚太其他地区的诸多行业从事多年的实业投资。1989至1993年，李先生在美国的一家系统集成公司Perot Systems Corporation工作，是其初创成员之一。李先生拥有伯克利加州大学学士学位和斯坦福大学MBA学位。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(343, '上海', '', 'IBM', '康霈', '职位:  , 执行董事', '互联网,移动互联网,硬件', '静安区', '6c84616f9969cd0fe96669b392bc44de.jpg', '', '成为资本', '康霈先生是成为资本的执行董事。在此之前，康先生是台湾最大的风险投资基金WK科技基金的高级副总裁，负责在中国大陆和台湾地区软件和电子产品制造行业的投资。在加入WK科技基金之前，康先生在IBM公司担任高级管理职务达十八年，曾任IBM在中国金融行业业务的总经理。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(344, '国外', '', '华登国际,祥峰投资', '蔡其乐', '职位:  , 集团总裁兼首席执行官', '互联网,移动互联网,医疗健康,环保能源,消费品,现代服务,传媒新媒体,教育培训,金融,文化创意,硬件', '', 'f436bdcf9388edc5aa1fb190eb836d9f.jpg', '斯坦福大学,威斯康辛大学麦迪逊分校', '祥峰投资Vertex', '蔡其乐先生是祥峰投资控股有限公司（祥峰集团）和祥峰投资管理私人有限公司的集团总裁兼首席执行官，一直致力为集团的投资策略和方向提供宝贵的见解和指导。在任职祥峰集团总裁之前，蔡先生曾担任新加坡上市公司柏盛国际的总裁兼执行董事。在此之前的2003至2006年间，蔡先生曾担任美国风险投资公司华登国际的董事总经理，并负责其所有在南亚地区的投资活动。1987至1997年以及2001至2003年，他曾在大众钢铁集团（NatSteel Group）内任职各项职务，包括大众钢铁有限公司（NatSteel Ltd）副总裁一职。从1991年至1993年，他曾任职比弗电脑公司（Beaver Computer Corporation）的总经理兼营运总裁。蔡先生早先毕业自麦迪逊威斯康星大学机械工程系，荣获学士学位，并随即获得大众钢铁集团所颁发的特优奖学金在斯坦福大学深造，荣获工程硕士学位。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(346, '北京', 'http://weibo.com/1846293871', '祥峰投资,新加坡政府直接投资GIC', '郑俊聪', '职位:  , 董事总经理兼首席投资官', '互联网,移动互联网,医疗健康,环保能源,消费品,现代服务,传媒新媒体,教育培训,文化创意,硬件', '朝阳区', '5a8d34b6244d362ec94e93e785263e96.jpg', '斯坦福大学,伦敦帝国理工学院', '祥峰投资Vertex', '郑俊聪先生是祥峰集团董事经理兼首席投资官。在加入祥峰集团之前，郑先生曾于2007至2009年担任新加坡政府投资公司的高级副总裁，派驻中国北京。在此之前，他曾担任上市 公司新加坡科技工程的航空事业部高级副总裁，并负责其在斯堪的纳维亚及新加坡的飞机部件总维修支援业务。从2000年至2005年，郑先生曾就职一 家泛亚风险投资机构，富鑫创业投资集团。在此期间，他担任了富鑫新加坡以及其在新加坡的两支科技基金的总裁。郑先生的工作历程自1995年担任战斗 机航电系统设计工程师开始。郑先生曾荣获新加坡科技集团奖学金就读伦敦帝国学院以及美国斯坦福大学，先后获得电子工程荣誉学士学位以及电子工程硕士学位。');
