INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(383, '上海', 'http://weibo.com/1949744267', '美银美林,龙科创投', '邵俊', '职位:  , 主管合伙人', '互联网,移动互联网,环保能源,消费品,现代服务,传媒新媒体,教育培训,金融,文化创意,硬件', '卢湾区', '17d9eea844668a28d3166533833b0e23.jpg', '复旦大学,南加州大学,夏威夷大学', '德同资本', '邵俊先生是德同资本的创始合伙人。邵先生曾是国内最早的风险投资公司之一龙科创投的创始人及首席执行官，并为无锡尚德太阳能电力有限公司和上海银联电子支付的投资人和董事。在此之前，邵先生在上海实业集团担任过五年的董事及各类管理职位，上海实业集团是上海市政府的主要投资实体，旗下的上实控股在香港上市。1998年邵先生负责组建了上海实业与所罗门兄弟合资的资产管理公司，该公司重点投资于大中华区市场。邵先生的职业生涯是在1992年从美林证券开始的。邵先生拥有南加州大学马歇尔商学院的工商管理硕士学位，以优异成绩毕业于美国夏威夷大学和上海复旦大学金融专业。2005年，邵先生被清科第三次选为中国十大活跃创业投资人。2006-2010年，邵先生连续五年被《福布斯》中文版评为“中国最佳创业投资人”。2012年邵先生被上海证券报评为“金融资●卓越投资家”。邵先生现任中华股权投资协会（CVCA）执行理事，上海股权投资协会副理事长。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(384, '上海', '', '麦肯锡,美银美林,摩根大通,龙科创投', '田立新', '职位:  , 主管合伙人', '互联网,移动互联网,医疗健康,环保能源,制造业,消费品,现代服务,传媒新媒体,教育培训,金融,文化创意,硬件,农业', '卢湾区', '8c1044dde3a7fa1c98aefdd79b1bf2df.jpg', '宾夕法尼亚大学,重庆大学', '德同资本', '田立新先生是德同资本的创始合伙人。在创立德同资本之前，田先生曾是龙科创投基金的首席投资官和董事总经理。之前田先生曾是美林证券及摩根证券的投资银行家，主要从事大中华区公司的融资及收购兼并业务。加入投资银行之前，田先生曾在美国麦肯锡公司从事管理咨询。田先生拥有美国沃顿商学院的工商管理硕士(MBA)学位，美国布莱得雷大学工程硕士学位及中国重庆大学工程学士学位。田先生被2008, 2009, 2010, 2011, 2012《福布斯》中文版评为“中国最佳创业投资人”，ChinaVenture CVawards 2010年度中国最佳创业投资人物Top 10, 2008年第五届亚太投资峰会年度最佳投资人，2007年在第十届中国科博会上被评为“2007中国十佳最具影响的私募股权投资家”，中国国际投资促进会投融资工作委员会2012轮值执行理事长。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(385, '北京', '', '美银美林,ChinaVest', '赵军', '职位:  , 主管合伙人', '互联网,移动互联网,医疗健康,环保能源,制造业,消费品,现代服务,传媒新媒体,教育培训,金融,文化创意,硬件,农业', '朝阳区', '8cda922aedbbbffe455778530b02eefb.jpg', '上海交通大学,耶鲁大学,休斯顿大学', '德同资本', '赵军博士是德同资本的主管合伙人。他在德同资本成立之初加入，此前，赵军博士曾担任中国创业投资公司执行董事以及中国首席代表，从2000年起负责中国创业投资公司在中国的投资项目。之前,赵博士在欧美跨国企业中从事项目管理和业务发展工作，主要为中国地区项目。赵军博士拥有耶鲁大学管理学院工商管理硕士学位以及休斯顿大学土木工程博士学位。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(386, '国外', '', '高盛,沃尔玛', 'Greg_Penner', '职位:  , 合伙人', '消费品,现代服务', '', 'b725cd4e2eb310a0bb8e1b056a9ba7a2.jpg', '斯坦福大学,乔治城大学', '德同资本', 'Greg Penner先生是沃顿家族旗下Madrone Capital Partners(“MCP”)的合伙人。在创立MCP之前，Penner先生在沃尔玛连锁企业中担任过许多高层管理职位，并曾在半岛资本中任合伙人。他也曾在美国高盛投资银行的公司融资部门任职。Greg是美国沃尔玛，北京百度公司，上海快钱信息服务有限公司和日本西友百货公司的董事。Greg拥有史坦福大学工商管理硕士学位以及乔治城大学的经济学士学位。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(387, '上海', 'http://weibo.com/1924426785', '华为,阿尔卡特朗讯,高盛', '张乐', '职位:  , 合伙人', '互联网,移动互联网,硬件', '卢湾区', 'b4af35512f8a8993b2b892cb0f2a36df.jpg', '哈尔滨工业大学,麻省理工学院MIT', '德同资本', '张乐先生是德同资本的合伙人。在加入德同之前，张乐先生曾在高盛投资银行部任职，具有丰富的投资经验，曾参与领导多个上市，投资以及并购项目。在此之前，张乐先生在华为公司海外部（美国）担任高级经理，也曾就职于阿尔卡特公司担任产品经理。张乐先生在国内外的工作与学习经验，让他具备了国际化与本土化的双重优势。张乐先生曾就读于有麻省理工大学斯隆商学院，获得MBA学位，并拥有哈尔滨工业大学的电子工程学的本科和硕士学位。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(353, '北京', '', '华为,中国移动', '尚艳淳', '职位:  , 副总裁', '互联网,移动互联网,传媒新媒体,硬件', '朝阳区', '9937e101ac72e8f07c983572d9890169.jpg', '清华大学,北京邮电大学,法国巴黎高等商学院', '优点资本VantagePoint', '尚艳淳于2008年2月加入优点资本，目前担任新兴市场团队的投资副总裁。她在通信和移动互联网领域积累了丰富的行业经验。在加入优点资本之前，尚艳淳女士任职于中国移动公司，负责面向海关、银行、交通等行业客户的无线通信解决方案的设计、推广和实施。更早之前，她在华为公司的数据通信产品部门担任研发工程师。尚艳淳于北京邮电大学获得计算机科学与技术专业学士学位，并获得法国巴黎高等商学院和清华大学的工商管理硕士学位。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(354, '北京', '', '', '张寒', '职位:  , 投资经理', '互联网,移动互联网', '朝阳区', '9ed7597346d1bc7265cce19910dce2eb.jpg', '清华大学,加州大学洛杉矶分校,东京大学', '优点资本VantagePoint', '张寒于2010年加入Vantage Point 公司任投资经理。他曾在日本日研有限公司担任研发工程师，后回到中国参与创建了北京创想通科技有限公司并任副总经理。他在高科技、能源产业有丰富的经验。张寒拥有美国加州大学洛杉矶分校安德森管理学院的工商管理硕士学位，日本东京大学工学硕士学位以及清华大学工学学士学位。');
