INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(365, '上海', '', '', '陈荣', '职位:  , 董事长', '互联网,移动互联网,医疗健康,环保能源,制造业,消费品,现代服务,传媒新媒体,教育培训,金融,文化创意,硬件,农业', '浦东新区', 'bcda54cdc9d8d57d0e5970939afb8dc4.jpg', '', '中路集团/中路资本', '陈荣是中路集团创始人及董事长。现任上海市工商业联合会副会长、上海市私营企业协会副会长。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(366, '上海', 'http://weibo.com/1984434015', '', '吴克忠', '职位:  , 优势资本总裁', '互联网,移动互联网,消费品,现代服务,传媒新媒体,教育培训,金融,文化创意,硬件', '浦东新区', 'c59e7f731859d301f5913942a23b3acd.jpg', '上海交通大学,南加州大学', '中路集团/中路资本', '吴克忠先生是中路集团旗下优势资本主管合伙人/总裁。吴克忠先生自1987年先后在高校及政府部门从事教学和经济工作，1992年起在中国证券市场建设初期就开始从事证券投资咨询及投资银行工作，1995年起在美国华尔街专业从事风险投资和私募股权投资，于2002年回国，创立了优势资本，曾主导参股投资了数十家包括金融服务、媒体、电信、销售网络、消费产品、新材料等类型的企业。吴克忠先生不仅将PreIPO这种投资方式和实践最早带回中国，同时还最早将SPAC基金作为投资工具和理念在中国推广。由于其良好的投资业绩和表现，获得美国五十多家大型对冲基金的注资，募集创立了以中国拟上市企业（Pre-IPO）为主要投资对象的上市基金公司—磐天中国SPAC基金，作为合伙人和主要管理人为该SPAC基金甄选合并对象，并于2008年底成功实现合并。吴克忠先生获上海交通大学工程学和技术经济双学位，后获美国Univ. of Southwestern LA的工商管理硕士。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(363, '北京', 'http://weibo.com/1074123194', '', '张宇', '职位:  , 投资经理', '互联网,移动互联网', '朝阳区', '52f2b155ab434f927656c1a8e0767fbd.jpg', '北京航空航天大学', '联创策源', '张宇毕业于北京航空航天大学，此前曾在北京仙掌软件科技、北京恒宜中天、亚洲互动科技、金玉天立软件等工作。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(367, '上海', '', '', '卢晓晨', '职位:  , 优势资本合伙人', '医疗健康,环保能源,金融,硬件', '浦东新区', 'b10c222b06a67f1302bee29859ac637e.jpg', '', '中路集团/中路资本', '卢晓晨先生是中路集团旗下优势资本合伙人/执行董事。卢晓晨先生拥有超过十五年创业及企业管理实践经验，十年的专业投资、投资银行和咨询服务经历。2007年起至今负责和参与了优势资本的多个股权投资项目。卢晓晨先生从上世纪90年代初成功创立多家高科技及实业公司，担任多家国内集团公司和合资企业的总裁，对国内企业创业投资、战略创造、资本运作有非常丰富的经验和独到见解，与政府部门、创业群体、私募股权投资基金有广泛的渠道和良好的合作关系。近年来卢晓晨先生策划和参股投资了数十家包括金融、医疗、电力电子消费产品、新材料等类型企业。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(368, '上海', 'http://weibo.com/1649510097', '21世纪经济报道', '汪恭彬', '职位:  , 副总裁', '互联网,移动互联网,消费品,现代服务,传媒新媒体,文化创意,硬件', '徐汇区', 'bacf1fb65219b0deec745736b6355919.jpg', '同济大学', '中路集团/中路资本', '汪恭彬先生2010年4月加盟中路集团，现为中路集团副总裁，协助董事长分管集团直投业务。汪恭彬先生是集团投资决策委员会成员，同时出任集团旗下上市公司中路股份董事。汪恭彬先生主导投资的项目包括赛特斯网络、深圳宏电、真旅网、上海优爱宝、世仓物流等。汪恭彬先生在资本市场拥有超过十年的专业经验。加入中路集团之前，他曾先后任职上海荣正投资咨询有限公司、21世纪经济报道和理财周报等机构，先后为白云机场、张江高科、古井贡酒、嘉事堂等公司设计股权激励方案。在媒体工作期间，汪恭彬先生先后担任21世纪经济报道的首席记者和理财周报的副主编，参与了中国资本市场几乎所有重大新闻事件的报道。汪恭彬先生著有《公募的视界》，于2011年出版，销量逾万册。汪恭彬先生获得同济大学管理学硕士学位。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(369, '北京', 'http://weibo.com/1407381861', '', '戴周颖', '职位:  , 中国区董事总经理', '互联网,移动互联网', '朝阳区', '3ef8aed85761a87fd55da200f8078182.jpg', '青山学院大学', 'CA创投/CyberAgent（国内）', '戴周颖是CA创投中国区董事总经理。他曾留学日本，毕业于日本青山学院大学。2005年从事日本国内、国外游戏的开发、运营，以及海外游戏企业的并购工作；2007年加入CyberAgent从事对华风险投资工作。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(389, '四川', '', '', '李农', '职位:  , 投资合伙人', '互联网,移动互联网,医疗健康,环保能源,消费品,现代服务,传媒新媒体,教育培训,硬件', '成都', '9dafb45f7e4eea6bd2583f76788fd142.jpg', '北京大学,中科院', '德同资本', '李农先生是成都德同银科创业投资基金的合伙人、总经理，2007年在西南参与项目投融资超过6亿人民币，此前，李先生曾任美国上市公司DSET中国公司董事总经理，香港上市公司国讯国际集团战略副总裁、深圳金达工业公司副总经理。2002年创建天盟网络技术公司并任CEO。李农先生拥有北京大学光华管理学院高级工商管理硕士学位，中国科学院系统工程硕士学位。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(390, '陕西', '', '', '耿健', '职位:  , 投资合伙人', '互联网,移动互联网,医疗健康,环保能源,消费品,现代服务,传媒新媒体,教育培训,文化创意,硬件', '西安', '25143d0d3508b0e17c054bed1d9609e9.jpg', '北京大学,浙江大学', '德同资本', '耿健先生是德同资本的投资合伙人及德同人民币基金陕西基金合伙人。耿先生曾任教于西安交通大学管理学院，后开创丰佳国际集团，创建欧洲中国工业园，走出国门以实业投资的方式建立中国企业进入欧盟生产基地和物流配送中心。耿先生现任丰佳集团董事长，致公党市委经济工作委员会副主任，陕西省政协常委，中国上海国际商会副会长，中国对外贸易理事会副理事长等。耿先生拥有浙江大学工业心理学学士学位，北京大学中国经济研究中心EMBA。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(391, '上海', 'http://weibo.com/1871630990/', '小天鹅股份', '唐祖荣', '职位:  , 投资合伙人', '环保能源,制造业,硬件', '浦东新区', '47fbf9628b27b0a0caf9e5058b875d6c.jpg', '上海交通大学,南开大学', '德同资本', '唐祖荣先生是德同资本的投资合伙人。唐先生曾担任上市公司无锡小天鹅股份有限公司董事会秘书、无锡华光锅炉股份有限公司董事等高层管理职务，拥有丰富的企业上市和资本运营经验。唐先生后创立永淳投资顾问（上海）有限公司并担任董事长，永淳投资致力于上市公司与VC、PE被投项目间的并购顾问业务，同时还为上市公司提供再融资顾问、投资者关系管理顾问、资产管理顾问、资产证券化顾问等金融服务以及拟上市公司IPO顾问等服务。永淳于2011年入选《中国企业家》杂志“2011未来之星100强企业”榜单，是行业内唯一入选企业。唐先生目前还兼任上海交通大学中国创业资本研究中心常务副主任以及上海交大EMBA校友会常务理事、南开大学校友会上海分会副会长等社会职务。唐先生与资本市场的监管机构、投资机构、财经媒体和上市公司保持密切的交流、联系与合作。唐先生曾就读于南开大学和上海交通大学，获文学学士和EMBA学位。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(393, '上海', '', 'IBM,普华永道', '张湘宁', '职位:  , 合伙人', '互联网,移动互联网,消费品,现代服务,硬件', '卢湾区', '9074b2437a7c4e8ca75d4f68fc2e6b58.jpg', '上海交通大学', '德同资本', '张湘宁先生负责鉴别、筛选和评估新投资机会。加入德同之前，张先生曾作为战略咨询顾问就职于IBM业务咨询部，也曾在普华永道工作两年，其间参与多个海外上市和并购项目。张先生毕业于上海交通大学，获得电子工程硕士学位和学士学位，并拥有管理工程双学位。');
