INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(423, '浙江', '', '阿里巴巴,德勤Deloitte', '张飞燕', '职位:  , 董事总经理', '互联网,移动互联网', '杭州', '94d67298860b5ba3eedd02bbfbe78072.jpg', '北京航空航天大学,达特茅斯学院', '阿里巴巴/阿里资本', '张飞燕于2006年10月加入阿里巴巴集团，负责集团投资并购，风险投资和战略合作。在加入阿里巴巴之前，张飞燕2004年至2006年任职于德勤公司的财务顾问部，为企业客户提供并购、投资方面的顾问服务。她在2000年及2001年亦曾就职于互联网创业公司从事孵化器投资，以及财务顾问服务。张飞燕从北航毕业后至2000年，任职于中国航空技术进出口总公司投资团队，负责公司的对外投资和收购。张飞燕持有美国达特茅斯大学塔克商学院的工商管理硕士学位以及北京航空航天大学管理工程学士学位。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(427, '上海', 'http://weibo.com/1569164154', '华为', '钱致远', '职位:  , 首席运营合伙人', '互联网,移动互联网,医疗健康,环保能源,制造业,消费品,传媒新媒体,教育培训,文化创意,硬件', '闵行区', '0ab3615a38c93bfdd1c7225673e6f757.jpg', '北京航空航天大学,中国科学技术大学', '旦恩创投', '钱致远本科毕业于中国科技大学，研究生毕业于上海交通大学，分别获得信息学院的自动化/系统工程专业与管理学院的经济管理专业双学士学位；安泰经济与管理学院的企业管理硕士学位。曾在数个业内知名民营企业工作过（华为兆迪等），并为多个民营企业提供管理咨询服务，各行各业经历丰富。从事过的职业或职责包括客服、销售、售前支持、人力资源管理、流程管理、运营管理、战略管理和管理咨询顾问等，涉及行业包括：大型通信设备、卫星通信设备、印刷工业设备、管理信息化软件、商务包装印刷业、工业建筑与装饰设计、电子商务、互联网设备及应用、物联网应用、移动互联网应用、O2O、教育管理信息化等。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(435, '上海', '', '麦肯锡', '周姮', '职位:  , 合伙人', '互联网,移动互联网,传媒新媒体,金融,文化创意', '长宁区', 'default_user.png', '哈佛大学', '华映资本', '周姮是哈佛大学遗传学博士，麦肯锡管理咨询公司全球副董事。周博士是金融业与媒体业管理咨询专家，尤其擅长风险控制和企业整合和管理。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(429, '上海', '', '', '齐飞', '职位:  , 合伙人', '互联网,移动互联网', '虹口区', '626a09a3bf3ad38d47b1018a76545169.jpg', '', '苏河汇', '齐飞是罗钥的高中同学，毕业于清华大学；2008年获得中华人民共和国保荐人资格；多年VC/PE股权投资经验，成功运作多家高科技企业上市、并购事宜；苏河汇最早的天使投资人，和罗钥一起出资创立苏河汇。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(428, '上海', 'http://weibo.com/1867452690', 'UT斯达康', '罗钥', '职位:  , 创始合伙人', '互联网,移动互联网', '虹口区', '5043fa8dcd6fa750cff933324d9ec232.jpg', '成都电子科技大学', '苏河汇', 'Darren罗钥是苏河汇的创始合伙人及CEO。毕业于电子科技大学，计算机专业；最早在UT斯达康做研发后转市场，来到上海；加入国际知名手游公司，之后出来创立手游公司，白手起家，最终数十倍溢价卖给一上市公司；后续创立手机方案公司，同时进行多个项目，后觉无趣忍痛关掉；之后加入国际风投，再之后出来创立苏河汇，找到人生乐趣，是第三次创业也是最后一次创业。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(430, '北京', 'http://weibo.com/1651913801', '搜狐,空中网', '杨宁', '职位:  , 创始合伙人', '互联网,移动互联网', '海淀区', '565a7466b635a14cdf2a051d77a19be8.jpg', '斯坦福大学,密西根大学', '乐搏资本', '杨宁现任乐搏资本创始合伙人。1997年毕业于美国密西根大学获得电子工程学士学位，1999年毕业于美国斯坦福大学获得电子工程硕士学位。 1998年，在斯坦福读书期间与周云帆和陈一舟共同创办社区网站“ChinaRen.com”。天使投资来自于他们的斯坦福同学，之后融得美国高盛银行、美国著名的KKR财团，和Joho财团共一千五百万美金。融资时杨宁仅24岁。截止2000年5月，“ChinaRen.com”成为了中国第四大网站。由于2000年互联网泡沫的破灭，2000年9月时“ChinaRen.com”以三千五百万美金卖给了搜狐。 在搜狐公司任职首席技术官一年半的时间，杨宁对搜狐的技术体系架构进行了大规模的升级。在2002年5月，杨宁离开了搜狐创办了空中网，中国领先的移动互联网增值服务公司，服务范围包括手机铃声，彩铃，社区，游戏等业务。仅用两年时间，空中网已成为行业用户数和收入最大的公司。 在2004年7月，空中网在美国纳斯达克上市（股票代码KONG）。公司从成立到上市仅用了2年零2个月的时间。直到今天仍然保持着从成立到上市最快的记录。杨宁在上市时年龄仅28岁，成为了在美国上市最年轻的总裁。 在2008年8月，杨宁离开了空中网开展了他的第三次创业，悟空网，一个与国内三大电信运营商合作的移动互联网运营支撑与推广渠道公司。另外杨宁也是中国非常活跃的天使投资人，专注投资电信，互联网，媒体等领域。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(431, '北京', 'http://weibo.com/2748106880', '中星微电子', '张辉', '职位:  , 合伙人', '互联网,移动互联网', '海淀区', '4809720030661ed92361c02e25129248.jpg', '中国科学技术大学,加州大学伯克利分校', '乐搏资本', '张辉是乐搏资本的合伙人，现任创毅视讯公司创始人、CEO。获中国科技大学物理学学士，美国加州伯克利大学电子工程博士 2005年获得Berkeley工程学院杰出校友奖。曾获同一奖项的有Intel创始人Andy Groove，Unix 发明人Bill Joy等世界级IT大师。这是中国留学生第一次在Berkeley获得此项殊荣。 1999年底，年仅26岁的张辉博士回国共同创办中星微电子公司，获得GAP, DCM等著名风险投资公司的数千万美金的投资。2005年11月中星微成功在Nasdaq上市，成为第一家在纳斯达克上市的中国芯片设计公司。 2006年9月，张辉博士二次创业，创办北京创毅公司。 创毅公司是中国领先的移动互联网和无线宽带核心芯片及系统公司，提供CMMB手机电视芯片、第四代移动通信TD-LTE核心芯片、无线宽带“云管端”整体解决方案。目前正在酝酿在中国创业板上市。 张辉博士在2009年入选中组部首批“千人计划”人才，目前担任“千人计划”专家联谊会副会长，主管创业和产业推动，掌握数百家创业公司项目资源。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(432, '上海', 'http://weibo.com/2405485611', '德意志银行', '姚琮', '职位:  , 创始人', '互联网,移动互联网', '闸北区', 'd3e717fb9f71b135c7a13af75c5b911a.jpg', '浙江大学', '上海融翼资本', '姚琮毕业于浙江大学文学院，后留学德国，获得企业管理硕士学位（Diplom Kaufmann）, 留学英国获得硕士学位。先后在国际知名大企业如德意志银行、HypoVereinsbank、Voith福伊特集团工作过，也在小公司工作过，并先后在国外和中国创过业，深刻理解创业的艰辛和不易。');
