INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(437, '上海', 'http://weibo.com/1774533530', '', '王维玮', '职位:  , 投资总监', '互联网,移动互联网,传媒新媒体,文化创意', '长宁区', '199d904fb831d30f581e32b5ebd1c87d.jpg', '新加坡国立大学', '华映资本', '王维玮是华映资本副总裁。新加坡国立大学工程荣誉学位。曾担任新加坡最大的人力资源管理咨询公司SearchAsia 资深咨询师。在华映任职期间，已参与并完成了多项私募融资。');
