INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(441, '北京', 'http://weibo.com/u/1849638305', '中经合集团', '邱玉芳', '职位:  , 执行合伙人', '互联网,移动互联网,医疗健康,环保能源,消费品,现代服务,传媒新媒体', '朝阳区', 'c409dd3e4aa8526a7978c57dbe0bdbb5.jpg', '乔治华盛顿大学,东北财经大学', '芳晟股权投资基金', '邱玉芳女士有超过20年的投资及相关行业经验，在中国金融财政领域有较为广泛的资源，并与政府机构建立了良好的关系。在发起芳晟基金之前，她曾任美国中经合集团董事总经理。邱女士于99年作为首任中国区首席代表加入中经合集团，参与了多个成功项目的投资，包括分众传媒, DivX和北京信威（该公司创立了两个电信行业的标准TD-SCDMA和McWill）等。加入中经合之前，邱玉芳是财政部直属的中国经济开发信托投资公司国际金融部总经理，管理超过2亿美元资产，并负责国际金融部的所有业务。邱女士还曾在财政部工作八年，担任宏观经济分析员，参与国家重大宏观经济政策决策研究并提供相关研究报告及政策建议。邱女士获得美国华盛顿大学税收财务硕士学位和东北财经大学经济学学士学位并拥有高级经济师的职称。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(444, '北京', 'http://weibo.com/1747800211', 'IBM,普华永道,汉能投资,佳能Canon', '杨宏伟', '职位:  , 投资总监', '互联网,移动互联网,传媒新媒体,硬件', '朝阳区', 'e9e7efe19ac03be102d0a1719001daf6.jpg', '北京大学,天津大学', '芳晟股权投资基金', '杨宏伟先生拥有丰富的TMT行业经验，涉及审计财务、软件外包、影像与信息产品、系统集成和银行等领域。在加入芳晟基金之前，曾在汉能投资集团从事移动互联网方面的研究，在普华永道担任环球资讯部门的应用开发中心部门经理，在IBM全球服务执行中心担任交付经理、项目经理和人员发展经理，在佳能公司担任IT主任和业务分析员，之前还曾在中联兴达软件工程有限公司和建设银行供职。具有多年部门和大中型项目管理经验，拥有PMP资质，在内审、SOX、ISO、CMMI标准实施方面也有一定经验。杨宏伟先生于1996年毕业于天津大学，获得计算机系学士学位，目前在北京大学光华管理学院就读MBA。');
