INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(467, '北京', 'http://weibo.com/1595536922', '中央电视台,华润', '许劲上', '职位:  , 副总裁', '互联网,移动互联网,消费品,现代服务', '朝阳区', '446e0055fceb5fa3e2ca6cade3e947e2.jpg', '中央财经大学', '维西资本/Vcwill Capital', '许劲上Jason 毕业于中央财经大学金融学院，获金融学士学位；国内目前少有的并购整合专业人士，曾创办传媒企业并高价出售；Jason在中国连锁消费、TMT行业领域有着丰富的并购整合操作经验，曾服务于华润雪花啤酒、央视市场研究、物美集团、海航商业等大型中国企业，Jason还致力于中国产业整合事业，积极筹备发起中国首支并购整合股权投资基金。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(468, '北京', 'http://weibo.com/1941145163', 'SunMicrosystems', '张振清', '职位:  , 总裁兼CEO', '互联网,移动互联网,传媒新媒体,硬件', '顺义区', '47724f99d701bb2ce236ce93e65c28fc.jpg', '清华大学,比萨大学', '亚信联创', '张振清于1984年毕业于清华大学计算机科学系，此后留学于美国德州Rice大学计算机科技专业和意大利比萨大学信息科学专业，获硕士学位。1989年至1999年间，张振清在美国硅谷的Blue Martini 软件公司、Hyperion Solutions, Inc., Arbor Software, Sun Microsystems等公司任职，1999年12月加盟原亚信集团，2005年4月正式升任原亚信CEO兼总裁，2009年12月，主导亚信集团股份有限公司和联创科技（南京）有限公司的合并，并成为亚新联创的总裁兼CEO。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(469, '北京', 'http://weibo.com/1749127163', '金山', '雷军', '职位:  , 创始合伙人兼董事长 , , CEO , , 创始人 , , 董事长', '互联网,移动互联网,硬件', '海淀区', 'd4fbefad719f7b947dc39e85f93b67d0.jpg', '武汉大学', '顺为基金,小米科技,雷军/顺为创业投资,金山软件', '雷军先生是中国IT界知名的企业家和天使投资人。雷军先生于2010年创办小米科技，现任小米科技的董事长兼CEO。雷军先生的职业生涯始于金山，他是金山软件的联合创始人及现任董事长，于2007年带领金山软件成功上市。他同时也是卓越网创始人，并于2004年成功将其出售给亚马逊。作为天使投资人，雷军先生乐于为创业企业的创立及成长提供帮助。得益于雷军先生的协助，他投资的企业，包括凡客诚品、多玩(YY)及UCweb等，在各自领域均有优异表现。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(470, '北京', 'http://weibo.com/2657160081', '德意志银行,AIG,新加坡政府直接投资GIC', '许达来', '职位:  , 创始合伙人兼CEO', '互联网,移动互联网', '朝阳区', '26fab69b43dad6e193f232a178dad1e9.jpg', '斯坦福大学,新加坡国立大学', '顺为基金', '许达来先生在股权投资及金融领域拥有超过13年的经验，曾在C.V. Starr、新加坡政府直接投资公司（GIC）、美国国际集团（AIG）及德意志银行等多家国际知名机构担任管理职位。在其投资生涯中，他所投资的项目金额超过3亿美元，涉及行业广泛。许达来先生现任Infocomm Investments投资委员会委员，并曾在三家中国知名私募基金担任审核委员会或投资委员会委员。许达来先生毕业于新加坡国立大学及美国斯坦福大学。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(471, '北京', 'http://weibo.com/1580546824', '百度,乐淘网', '高少星', '职位:  , 副总裁', '互联网,移动互联网,传媒新媒体', '朝阳区', '9ba4e72d30c84097b530fcba950951e0.jpg', '', '顺为基金', '高少星先生拥有超过15年的互联网从业经验，对互联网产品、用户以及市场有着独特认知和见解。高少星先生曾任乐淘网副总裁，从零开始组建乐淘新媒体事业部，发展移动电子商务业务。他还曾在百度产品、商务及投资部门任职，直接领导百度多个产品上线，并通过投资、并购等资本手段助力百度战略和业务的发展。此外，高少星先生还拥有两次创业经历，深刻理解公司在创业早期方向选择、团队建设、产品运营、资金管理的艰辛与困惑。');
