INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(473, '北京', 'http://weibo.com/1084999002', '腾讯', '邓菁', '职位:  , 投资经理', '互联网,移动互联网', '朝阳区', 'f6ed9d5ad4b837c19fa74d85fddc2bdf.jpg', '北京邮电大学', '顺为基金', '邓菁女士拥有超过8年的互联网从业经验，对互联网产品设计、用户运营及市场有着深刻的体验和理解。 邓菁女士曾担任腾讯数字多媒体产品中心QQ音乐产品负责人,全线负责腾讯音乐产品业务发展。 她帮助QQ音乐成为国内在线音乐市场占有率第一的音乐产品。 在加入顺为之前，邓菁女士曾担任易讯天空网络公司运营总监。邓菁女士拥有北京邮电大学学士学位。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(474, '北京', 'http://weibo.com/1774173930', '中信', '刘琼岭', '职位:  , 分析师', '互联网,移动互联网', '朝阳区', '09ee23b999e04a9fbf9ac2f8744010ad.jpg', '北京大学,香港大学', '顺为基金', '刘琼岭女士主要负责考察、研究和筛选互联网、移动互联网领域的投资机会。在加入顺为之前，刘琼岭女士曾任中信证券高级经理，并在中国国际金融有限公司、天图资本、贝恩咨询公司有过众多实习经历。刘琼岭女士拥有北京大学经济学及法学学士学位以及香港大学金融硕士学位。');
