INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(476, '北京', '', '华登国际', '刘宇环', '职位:  , 创始人及董事长', '互联网,移动互联网,医疗健康,环保能源,消费品,现代服务,传媒新媒体,教育培训,金融,文化创意,硬件', '朝阳区', 'ba7eab93bf93b610dd7273fdf018f009.jpg', '加州大学伯克利分校', '美国中经合集团/ WI Harper Group', '刘宇环先生被业内公认为亚洲风险投资界的先锋人物之一，已有二十多年的投资经验，培育了数十家上市公司，其深厚的业务功底和乐于助人的风格赢得了诸多创业者和业界同仁的尊重。他生于北京、成长于台北，在美国完成学业，深谙东西方文化的优势与差异。在创立中经合之前，他亦共同创立了华登国际亚洲基金，并成功融资4亿美元。刘宇环先生在其投资职业生涯中亲身投资的企业已超过100家，包括DivX, Creative Technology, CommerceOne, Intraware, S3, ISSI等数十家上市公司，为股东创造了丰厚的回报。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(477, '北京', 'ygwang@wiharper.com.cn', '', '王亦工', '职位:  , 首席运营官', '医疗健康,环保能源,制造业,教育培训,金融,文化创意,硬件', '朝阳区', '59f8e211f264960c10aaacb97191aa79.jpg', '天津大学,布鲁内尔大学', '美国中经合集团/ WI Harper Group', '王亦工先生是美国中经合集团北京办事处首席运营官，主要负责公司日常运营管理。在加入中经合集团之前，王先生在中国远洋运输（集团）总公司(COSCO) 工作了19年，曾担任研究发展中心副主任。之前，他还曾担任船舶建设和贸易部副总经理、技术中心主任、以及各委员会的成员。加入中远之前，王先生曾在天津大学海洋工程和造船系工作。王先生拥有英国布鲁内尔大学机械工程博士学位和天津大学工学硕士学位。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(299, '北京', '', '', '吴华', '职位:  , 副总裁', '互联网,移动互联网,消费品,现代服务,传媒新媒体', '朝阳区', '334f6176671159eb968ad25d48407715.jpg', '', '鼎晖投资', '吴华于2006年加入鼎晖创投，已主导和参与多个项目的投资，并出任多家公司的董事。投资行业涉及新媒体、电子商务、大众消费等多个领域，包括从创业期到快速成长期的投资。此前就职于鼎晖投资旗下的鼎晖成长基金，主要专注于媒体、大众消费、建材、制造业、连锁经营、软件等多个领域扩展期的投资，积累了丰富的投资经验。他曾就职于中国投资担保有限公司，任高级投资经理。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(298, '北京', '', '艾威基金Avenue', '王晖', '职位:  , 合伙人', '医疗健康,消费品', '朝阳区', 'd6da60d23f10adca5a2f2f9276e83aec.jpg', '中国科学技术大学,英国伦敦商学院,纽约大学', '鼎晖投资', '王晖2009年加入鼎晖创投，专注于医疗卫生, 消费品等领域。已主导和参与医疗服务，化学制药和介入器械等投资。加入鼎晖前，曾筹建并领导美国艾威基金首支医药业务团队。在此期间, 主导和参与多个项目的投资；此前，王晖就职Becton Dickinson 医疗集团（BDX），负责美国总部的收购及风险投资业务，共涉及5亿美金的兼并投资项目。王晖前后积累了九年的国内外的医疗卫生行业和投资经验。');
