INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(301, '北京', 'http://weibo.com/1499038733', '中兴', '赵新宇', '职位:  , 副总裁', '互联网,移动互联网,硬件', '朝阳区', 'fdf5e07e2f603287ae0e556e91365dea.jpg', '中国人民大学,英国卡迪夫大学', '鼎晖投资', '赵新宇于2010年加入鼎晖创投，负责鼎晖创投所投资企业的投后运营管理。此前他担任中兴通讯股份有限公司人力资源中心总经理、监事和中软海纳技术有限公司总经理等职务，强力主导中资跨国公司人力资源战略转型和管理变革，带领超过600人的全球HR团队成功构建遍布120个国家和地区的集中管控与共享服务模式。他在通信、IT、能源、外包等相关行业具有丰富、深入、独到的企业管理经验和视野，主导、参与大量的管理咨询项目，他之前还曾任职于中国电信。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(302, '北京', '', '', '李牧晴', '职位:  , 副总裁', '互联网,移动互联网,消费品,现代服务,传媒新媒体', '朝阳区', 'aade2aea15d6087934315280de3d4ccb.jpg', '', '鼎晖投资', '李牧晴于2006年正式加入鼎晖创投，已参与业务流程外包、新能源、连锁经营、互联网、新媒体、电子商务、大众消费品等多个领域的投资，并对这些领域及医疗设备及服务等其他多个领域有深入的了解。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(303, '北京', '', 'HP惠普', '吴子烁', '职位:  , 副总裁', '互联网,移动互联网,医疗健康,制造业,消费品,现代服务,传媒新媒体', '朝阳区', '51551873999c90ca788ff163a990cd0b.jpg', '中欧国际工商学院', '鼎晖投资', '吴子烁于2007年加入鼎晖创投，已参与新媒体、电子商务、大众消费、芯片、制造业及医疗保健等多个领域的投资，同时对环保，移动互联网，软件等领域有深入的了解。此前他就职于惠普战略投资部，主要专注于广义IT领域的投资。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(304, '北京', '', '毕马威KPMG,艾威基金Avenue', '吴进', '职位:  , 副总裁', '医疗健康,环保能源,消费品,现代服务,金融', '朝阳区', '47fa43d9c9902996ac3a8929617ca2b3.jpg', '', '鼎晖投资', '吴进于2010年加入鼎晖创投，主要专注于清洁能源、医疗器械、大众消费和金融服务等领域。加入鼎晖前曾就职于美国艾威基金和毕马威会计师事务所，主导和参与过医疗和教育等领域的项目投资。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(284, '北京', 'http://weibo.com/1866145691', '麦肯锡', '易多多', '职位:  , 副总裁', '互联网,移动互联网,传媒新媒体,教育培训', '朝阳区', 'fdc4986655e29e3e83039497ddbf4941.jpg', '', '贝塔斯曼亚洲投资基金', '易多多是贝塔斯曼亚洲投资基金副总裁，此前曾在麦肯锡工作。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(280, '香港', 'http://weibo.com/1751885472', '阿里巴巴,普华永道,荷兰银行', '赵鸿', '职位:  , 联合创始人', '互联网,移动互联网', '九龙城区', 'c02eca58384b2e2223c05bd8726c063e.jpg', '', '阿米巴资本', '赵鸿是阿米巴资本创始管理合伙人。此前曾担任阿里巴巴集团副总裁、企业融资及财资管理部董事总经理。 参与了阿里巴巴所有重大的并购、融资项目，包括2005年收购中国雅虎以及美国雅虎入股;2007年B2B公司香港上市；2008年防范微软敌意收购雅虎、阿里巴巴股权;今年的支付宝股权变更、引进DST等私募基金购买员工股份计划等。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(274, '上海', 'http://weibo.com/1871686290', 'IBM,普华永道,波士顿咨询BCG', '顾为国', '职位:', '互联网,移动互联网,环保能源,消费品,现代服务', '卢湾区', '317b215fc914965ec1e64b08508cd4d2.jpg', '上海交通大学,麻省理工学院MIT', '', '顾为国先生2009年加入光速创投，先后参与了多个投资组合案例，包括大众点评和优众网。顾先生专注的投资领域包括：互联网，移动，企业软件及服务，消费服务，以及清洁科技。2013年从光速创投离职。在加入光速创投之前，顾先生任职于波士顿咨询公司上海办公室，任职项目经理，主要专注于消费品、工业产品及能源领域。他曾帮助多家客户开展他们在中国的发展和转型战略，定位新业务机会和并购目标，并指导相关商业尽职调查。在波士顿咨询公司之前，顾先生任职于IBM和PwC公司高级咨询总监，主要负责SAP系统的贯彻实施。由于表现出色，顾先生获得了破格提升，成为了PwC中国区最年轻的咨询总监。此外，他是PwC公司唯一的中国籍员工外派在瑞典马尔默一个全球项目工作了两年，在那里他领导了一个国际开发团队。顾先生拥有麻省理工商学院MBA学位，上海交通大学电子工程学士学位。由于高中成绩出色，顾先生被免试保送进入上海交通大学试点班。顾先生曾任上海交大电子工程系学生会主席。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(244, '北京', 'http://weibo.com/1989742287', '高通Qualcomm', '沈劲', '职位:  , 中国区总经理', '互联网,移动互联网,硬件', '朝阳区', '4fbcc6f7e8dd8280c173c1d6b9ba7568.jpg', '浙江大学,南加州大学', '高通投资Qualcomm', '沈劲现任高通公司业务拓展全球副总裁，兼高通风险投资中国区总经理。作为董事或董事会观察员，沈劲先生负责管理高通在四川长城、贝多、汉翔、网秦、中科创达等公司的投资。沈劲先生在美国、中国和全球很多市场的电信和数字无线通信领域有着丰富的专业经验。在就任此职之前，沈劲先生曾是佳网公司的创始人和CEO，和General Photonics公司的副总裁，从事VoIP、光通信等新技术的开发。其中佳网公司在2000年上市，市值逾4亿美元。此前，他曾在美联航等企业担任电信工程师。沈劲先生在浙江大学获电子工程学士学位，并在南加利福尼亚大学（USC）获得通信管理硕士学位。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(245, '北京', 'http://weibo.com/1764905165', '百度', '王啸', '职位:  , 创始人', '互联网,移动互联网', '海淀区', 'b8d4582cb0eaff2df927adb7edabc690.jpg', '北京邮电大学,中欧国际工商学院', '九合创投', '王啸是百度创始人团队“七剑客”之一，2000年加入百度，2011年离开，期间参与百度第一代搜索引擎研发，负责百度后台服务器系统架构，带领百度大客户销售团队，百度企业搜索事业部，组建百度客户端软件部以及之后的百度无线事业部2011年创立九合创投，关注并投资早期互联网/移动互联网/社会化电子商务公司和团队。2006中欧商学院EMBA。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(246, '上海', 'http://weibo.com/u/1864282321', 'HP惠普,德丰杰DFJ', '符绩勋', '职位:  , 管理合伙人', '互联网,移动互联网', '浦东新区', '0cac4bf8c85fae98a535f708904e1808.jpg', '新加坡国立大学', '纪源资本GGV', '符绩勋 (Jixun Foo)于2006年加入纪源资本。作为中国区的主要合伙人，他在风险投资领域已工作了十二年多，拥有丰富的与企业家的合作经验。符绩勋关注互联网、移动互联网领域的投资。目前他担任董事的企业包括去哪儿网、聚胜万合、豆果网等。他也负责并积极参与以下投资企业的工作，美丽说、土豆网（已被优酷并购）、优视科技(UCWeb)、AAC、超力、梅花集团等。在加入纪源资本之前，符绩勋曾负责德丰杰环球基金亚洲区的投资，领导了百度和龙旗等中国区的投资项目。进入德丰杰之前，符绩勋任职于新加坡国家科技局(NSTB)金融与投资部，负责领导相关投资事务。跨入风险投资领域前，符绩勋曾是惠普公司的研发项目负责人。符绩勋毕业于新加坡国立大学，获得工程学一级荣誉学士学位并在之后获得新加坡国立大学商学院科技管理学硕士学位。符绩勋于2007年上榜“全球最佳投资人”排名第11，并自2006起连续6年被福布斯评选为“中国最佳创业投资人”。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(247, '上海', 'http://weibo.com/1881276625', '祥峰投资', '金炯', '职位:  , 合伙人', '互联网,移动互联网,医疗健康,制造业,硬件', '浦东新区', '22ef5c9eb2e707c49e6db9623d56f918.jpg', '复旦大学', '纪源资本GGV', '金炯(Jessie Jin)于2008年以合伙人身份加入纪源资本。金炯还担任思格资本的创始合伙人, 思格资本是一家专注于在中国进行投资管理和相关咨询业务的风险投资管理公司, 目前思格资本是科星风险投资基金的管理人。金炯目前担任新通联公司和青岛赛维公司的董事。 她参与的其他投资包括：新生源生物, 步长医药及腾中重工等。自2002年至2005年, 金炯曾任职于新加坡祥峰(中国)投资公司副总裁, 期间完成了包括对中芯国际, 展迅通信, 智芯科技, 天融信软件等企业的投资。祥峰是一家新加坡具领导地位的风险投资管理公司。金炯于1995年至2002年服务于上海实业控股有限公司(HKSE:363)的香港总部, 作为骨干人员, 她协助上海实业完成香港主板上市, 后升任公司投资策划部主管, 执行其在境外资本市场的屡次大型融资和收购行动, 完成了超过20个大型中国直接投资项目。作为中国最早的证券业专业人员, 金炯曾于1992年至1994年服务于当时中国最大的证券公司 - 上海万国证券有限公司国际业务部。金炯毕业于复旦大学获经济学学士及企业管理硕士学位。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(248, '上海', 'http://weibo.com/1813719912', '摩根斯坦利,集富亚洲', '李宏玮', '职位:  , 合伙人', '互联网,移动互联网,传媒新媒体,硬件', '静安区', 'f0109f39682891f268857b8a6e62ae3d.jpg', '康奈尔大学,美国西北大学', '纪源资本GGV', '2005年李宏玮 (Jenny Lee)作为合伙人加入纪源资本，并为纪源资本在中国的建立发挥了积极的作用。李宏玮关注软件、商务服务与互联网、数字媒体领域的投资，由她主导投资并担任董事的企业包括，海辉软件、世纪互联、易才集团、华道数据、SocialTouch、多玩/YY , 优视科技、Ali Animation, Chukong/CocoaChina, 7k7k、和久娱。此外，她还积极参与了对Buddy Media (已被Salesforce.com并购)、Glu Mobile和Wild Tangent的投资。李宏玮之前在新加坡航空航天科技部、摩根斯坦利、和JAFCO亚洲的工作经历及所积累的运营及金融工作经验，使她成为受很多中国企业家欢迎的投资人和董事会的良师益友。李宏玮拥有康耐尔大学工程学学士和硕士学位、及西北大学凯洛格学院MBA学位。最近，李宏玮被《财富》杂志评选为2012年全球五大最有权势女性创投人之一。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(249, '上海', 'http://weibo.com/2432390252', 'SunMicrosystems', '黄榆鑌', '职位:  , 合伙人', '互联网,移动互联网,现代服务,硬件', '静安区', '57df259a9d9e4cbcf6ef0dba48845988.jpg', '斯坦福大学,南洋理工大学,旧金山大学', '纪源资本GGV', '黄榆镔(Ng Yi Pin)是纪源资本(GGV Capital)合伙人, 常驻上海办公室。他专注的投资领域有手机软硬件和物流服务等。此前，黄榆镔在风险投资集团GIC工作。作为一名风险投资家，黄榆镔主导了多个项目的投资， 包括: Xfire (被Viacom 收购), Placeware (被Microsoft收购), Arbortext (被Parametric Technology Corp, PTC收购), G-Log (被Oracle收购), Ageia (被Nvidia收购), Wayport (被AT&T收购), Lumension Security, Teranetics以及Inside Contactless.2008年黄榆镔调往上海办公室，并在这里专注于手机零部件以及物流行业。他在中国参与的部分投资项目包括:梅花集团，AAC声学科技以及冷链物流企业荣庆集团等。在加入GIC之前的2000年, 黄榆镔服务于Sun Microsystems, 担任该公司亚太地区运营规划和运营管理的职位。他也是新加坡海军军官。黄榆镔拥有旧金山大学计算机工程和数学学士学位, 斯坦福大学运营研究硕士学位以及南洋理工大学南洋商学院MBA硕士学位。黄先生是特许金融分析师(CFA)。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(250, '国外', '', '', 'Thomas_Ng', '职位:  , 合伙人', '互联网,移动互联网,医疗健康', '', '125f59a0c0e429c17aba01e37cb48bea.jpg', '威斯康新大学', '纪源资本GGV', 'Thomas是纪源资本的共同创始人之一，并主导了对阿里巴巴(1688.HK)、AAC 技术(2018.HK)、海辉(NASDAQ: HSFT)、Si2i (原Media Ring), Oculex 医药( 已被Allergan并购)等公司的投资 和 InVisage Technologies。目前，他担任Chaintea Holdings、Heptagon、Mediacorp 和Reebonz等公司的董事。在创建纪源资本之前，他是由新加坡国家科技局创建的华盈基金(Venture TDF)的创始人和董事总经理。他曾在DuPont and Genelabs Technologies(已被GlaxoSmithKline公司并购) 担任高级管理职位，并曾在下属于能源部的国家研究能源实验室（SERI的前身）和杜邦试验站从事研究实验工作。Thomas在位于迈迪逊的威斯康新大学获得理学学士、硕士和博士学位。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(252, '上海', 'http://weibo.com/1866723285', '祥峰投资', '卓福民', '职位:  , 合伙人', '互联网,移动互联网', '静安区', '37264fd4db14444d1be4a9421812a9ae.jpg', '复旦大学,上海交通大学', '纪源资本GGV', '卓福民 (Fumin Zhuo)先生现为纪源资本(GGV Capital)合伙人和思格资本集团(SIG Capital)创始合伙人。在创建SIG之前, 在2002-2005年期间, 卓福民担任祥峰(Vertex)中国投资公司的董事长及CEO, 并带领祥峰中国投资公司成长为中国最活跃和出色的基金管理公司之一, 曾先后投资了展讯(NASDAQ: SPRD), 天融信等高科技项目, 并促使掌上灵通(NASDAQ: LTON), 金融界(NASDAQ: JRJC)等数家企业在海外成功上市。1995-2002年期间, 卓福民先后担任上海实业控股有限公司(HKSE: 363)董事总经理, 行政总裁(CEO), 副董事长, 主要的投资包括光明乳业(SSE: 600597), 上海家化(SSE: 600315), 联华超市(HKSE: 980), 中芯国际(NYSE: SMI), 南洋酒店管理有限公司(上海四季酒店的业主), 携程网(NASDAQ: CTRP), 白马广告(HKSE: 100)等。目前上海实业控股有限公司仍是香港恒生综合指数和中信标普30指数成分股。1987-1995年期间, 卓福民在上海市政府经济体制改革办公室工作, 曾任处长, 主任助理。卓福民毕业于上海交通大学, 后又获复旦大学经济学硕士学位, 他在基金管理, 直接投资, 资本运作及政府和企业的管理岗位上有38年的工作经验。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(251, '上海', '', '江西铜业', '于立峰', '职位:  , 合伙人', '环保能源,农业', '静安区', '611a1465487e4115d86402dfd24958c1.jpg', '复旦大学,美国伊利诺伊大学', '纪源资本GGV', '于立峰 (Bruce Yu)先生在企业管理、直接投资和资本运作岗位上具有近20年的工作经验。曾在江西铜业股份有限公司负责江西铜业的资产重组和香港上市工作，担任江西铜业董事会秘书处处长。1999年起于先生开始从事风险投资事业, 曾任上海科技投资股份有限公司监事,总裁助理兼投资部经理, 主持和参与了涉及光电子, 生物和环保领域的多个项目的投资。于先生毕业于上海复旦大学管理学院，MBA；是美国伊利诺大学香槟分校商学院(Business School of the University of Illinois at Urban-Champaign)的访问学者。于先生具有高级机械工程师和高级经济师资格。于立峰先生于2008年加入纪源资本，现担任纪源资本（GGV Capital）合伙人，纪源科星人民币基金管理合伙人；主要负责节能环保和现代农业领域的投资。于立峰先生现在还担任四川新力光源董事、安洁士石油技术公司董事、源森红花茶油公司董事。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(253, '国外', '', '', '李庆南', '职位:  , 投资合伙人', '硬件', '', '8ad7425df65f7713af7205d5fc725183.jpg', '加拿大女皇大学', '纪源资本GGV', '李庆南 (Kheng Nam Lee)曾担任全球风险投资公司Vertex Management公司总裁, 任期长达20年。他目前出任创新科技(Creative Technolgy)及中国金融在线有限公司(China Finance Online Co Ltd)等多家上市和非上市公司的董事。此外, 他还担任过包括Centillium Communications, Chartered Semiconductor, GRIC Communications, Gemplus International, ActivCard, SMIC及 United Test and Assembly Center在内的多家科技和电信行业上市和非上市公司的总监。Kheng Nam常驻新加坡, 主要负责协助纪源资本(GGV Capital)在半导体行业和中国地区的投资业务。Kheng Nam拥有加拿大女皇大学机械工程学(一级荣誉)学士学位和美国Naval Postgraduate School运营研究和系统分析硕士学位。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(254, '上海', 'http://weibo.com/1295416291', '波士顿咨询BCG', '胡磊', '职位:  , 副总裁', '互联网,移动互联网', '浦东新区', 'a7e5e61e9c23345d92fed65d9abbd0f9.jpg', '复旦大学,美国西北大学', '纪源资本GGV', '胡磊 (Ray Hu)于2007年加入纪源资本，目前是上海办公室的副总裁。他主要关注互联网，无线和软件行业的投资机会。胡磊目前在美丽说，豆果网和聚胜万合的董事会任职董事/观察员。同时，他也积极参与了纪源资本在去哪儿，世纪互联和海辉软件的投资。在加入纪源资本之前，胡磊在波士顿咨询公司（BCG）工作了4年，帮助大量国际和本地客户制定兼并收购、市场进入和销售营销方面的战略规划。胡磊拥有美国西北大学凯洛格商学院的MBA学位。同时, 他也拥有复旦大学的经济学学士和硕士学位。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(255, '上海', '', '摩立特集团Monitor,罗兰贝格', '田敏', '职位:  , 高级投资经理', '医疗健康,消费品,现代服务', '静安区', 'ea4bed1600e8410debf7fa8b4cc52a8c.jpg', '复旦大学,加州大学洛杉矶分校', '纪源资本GGV', '田敏 (Terry Tian)是纪源资本(GGV Capital)上海办公室的高级投资经理。她主要关注消费领域以及医疗健康领域的投资。并积极参与了纪源资本在多个项目中的投资和管理。在加入纪源资本之前, 田敏就职于摩立特集团以及罗兰贝格管理咨询, 主要关注消费品，医疗以及制造业行业。田敏曾帮助过多家跨国公司和本地企业制定其中国发展战略以及市场进入战略并制定优化销售与市场营销策略组合方案。田敏拥有美国加州大学洛杉矶分校安德森商学院的MBA学位。同时, 她也拥有复旦大学的理科学士学位。在业余时间, 田敏喜欢看电影以及和朋友一起在世界各地旅游。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(256, '上海', '', 'BDA毕德投资', '顾蔚荧', '职位:  , 投资经理', '消费品,现代服务', '静安区', 'cba98a9766ebcebd3a765f43bf540213.jpg', '悉尼大学,惠灵顿维多利亚大学', '纪源资本GGV', '顾蔚荧 (Vita Gu)是纪源资本(GGV Capital)驻上海的投资经理，主要专注于消费和服务领域的投资。加入纪源资本之前，顾蔚荧就职于毕德投资，期间她参与完成了多项跨国并购的交易，专 注于大中华地区的化工、快速消费品, 餐饮和先进制造业。在此之前，顾蔚荧曾服务于Cameron Partners，新西兰领先的独立投资银行，担任分析员一职。顾蔚荧毕业于澳洲悉尼大学，获金融专业商学硕士。她还持有新西兰维多利亚大学的商学学士和应用金融学研究生学位。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(257, '上海', 'http://weibo.com/1674093274', '华兴资本', '黄仲生', '职位:  , 投资经理(已离职)', '互联网,移动互联网', '浦东新区', '309d2e1c5d698d5f7bc98da8424217ae.jpg', '复旦大学', '纪源资本GGV', '黄仲生 (Johnson Huang)是纪源资本(GGV Capital)上海办公室的投资经理高级分析师。他主要关注TMT领域的投资。并积极参与了纪源资本在阿狸、7K7K等公司的投资及管理。加入纪源资本之前，黄仲生就职于华兴资本。他参与了9个私募融资和跨境并购交易，提供一系列投行服务，包括市场研究、财务预测、路演材料准备、估值分析、尽职调查及法律文件谈判。在此之前，他在大学时期曾联合创立了一家互联网公司。黄仲生毕业于上海复旦大学，获电子工程学士学位。在业余时间，黄仲生喜欢和朋友们一起玩游戏、旅游和滑雪。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(259, '上海', '', '荷兰银行,弘亚世代', '盛雷', '职位:  , 投资经理', '环保能源,制造业', '徐汇区', '87a511a253c132f66b23911262e939ac.jpg', '同济大学', '纪源资本GGV', '盛雷是纪源资本(GGV Capital)上海办公室的投资经理。他主要关注清洁技术和先进制造业领域的投资，并积极参与了纪源资本在安洁士和超力高科等公司的投资和管理。加入纪源资本之前，盛雷就职于国内领先的第三方研究公司--弘亚世代。他曾作为清洁技术分析师重点跟踪和研究超过15家上市公司和私人公司，为海外机构投资者提供一系列基本面研究服务，包括市场研究、技术评估、政策分析和财务预测等。在此之前，他曾在荷兰银行环球银行市场部门工作,为客户提供企业信贷服务。盛雷毕业于上海同济大学，获材料物理与化学硕士学位，并且获得注册金融分析师三级候选人资格。在业余时间，盛雷喜欢和朋友们一起打羽毛球、健身和旅游。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(258, '上海', '', '腾讯,雅虎,Jefferies & Co', '吴陈尧', '职位:  , 投资经理', '互联网,移动互联网', '徐汇区', '2ed38cdc76fda52c58304437fdd247d0.jpg', '北京大学', '纪源资本GGV', '吴陈尧是纪源资本(GGV Capital)驻上海的投资经理，主要关注TMT领域的投资。在加入纪源资本之前，吴陈尧在全球最大的独立投行之一的Jefferies & Company 担任亚太地区电信、媒体和高科技行业证券研究员。在此之前，吴陈尧曾在腾讯和雅虎工作。他曾是腾讯副总监，负责领导腾讯在线媒体和广告业务的数据挖掘和战略研究，曾获腾讯年度优秀员工和优秀基干。在雅虎中国（阿里巴巴旗下子公司）期间， 他曾任搜索引擎结果优化的产品经理和分析师。吴陈尧本科毕业于北京大学光华管理学院。在业余时间，他喜欢旅游，跑步和读书。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(260, '北京', 'http://weibo.com/1756881315', '清科', '倪正东', '职位:  , 董事长兼CEO', '互联网,移动互联网,医疗健康,消费品,现代服务,传媒新媒体,教育培训,文化创意,硬件', '朝阳区', 'b3c83e50a6036f387e57e9adcaa3d6e3.jpg', '清华大学,湖南大学', '清科创投', '倪正东先生是清科集团的创始人、董事长兼CEO。在过去十年的时间里，他领导他的团队将清科集团建成了中国最有影响力的创业投资和私募股权投资服务机构。倪先生目前参与投资和帮助融资的企业超过五十多家，直接投资的企业包括奇虎360（NYSE: QIHU）、博纳影视(NASDAQ: BONA)、麦考林（NASDAQ：MCOX）、百分通联（已被澳洲电讯收购）、56网（已被人人网收购）、海报网（已被CBSi收购）、天涯社区、梦芭莎、三夫户外、爱康国宾、5173、中特物流、中经汇通、小马奔腾影视、窝窝团等等。倪先生是中华全国工商联合会经济委员会委员、国家科学技术部火炬创业导师、国家开发银行专家委员会成员。他目前还担任中华有限合伙人协会（LPACN）的联合创始人和秘书长、清华企业家协会（TEEC）理事、亚杰商会（AAMA）执委。作为股权投资行业的权威人士之一，倪先生还经常被邀请在国内外重要会议/论坛上就中国创业投资和私募股权投资现状、趋势等发表演讲。2010年，他被华夏时报和中国CEO俱乐部评为“2010年度中国十大杰出CEO” 。在创办清科之前，倪先生于1998年初创办了亚洲第一个创业计划竞赛－－清华大学创业计划竞赛；倪先生还曾任清华大学科技创业者协会会长(1998-1999)。倪先生是湖南大学工学学士、清华大学工学硕士以及清华大学经济管理学院工商管理博士。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(261, '北京', '', '联想,华美科技', '钟伟智', '职位:  , 董事总经理', '互联网,移动互联网,消费品,现代服务,传媒新媒体,硬件', '朝阳区', '41c6a60a327ab66f8d926b81c4846c95.jpg', '', '清科创投', '钟伟智先生于2005年加盟清科，出任集团首席运营官，3年来全面负责各项业务运营管理工作。目前出任集团首席财务官，同时为旗下的风险投资基金-清科创投董事总经理。钟先生之前曾担任翱科创投的投资高级副总裁，主要负责投资评估、风险管理和公司运作。 加入翱科前，钟先生为华美科技集团的财务和企业拓展副总裁。华美科技集团为一家创业投资集团， 在硅谷、中国/香港和其它亚洲国家都有参与投资。华美科技集团已投资逾二十家硅谷科技公司， 此等公司主要从事软件开发、无线和宽频网络技术、电子商贸解决方案等。钟先生加入华美科技前曾担任香港上市公司及国内第一计算机制造商联想集团的资金部主管，负责管理营运资金和控制整个集团的现金流量，并与超过三十家银行及跨国财务机构洽商银行信贷。他的职责还包括制定和监控信贷管理政策，保险管理及协调股票上市、法律、商标、专利权等事务。钟先生是加拿大特许会计师公会会员，拥有超过十五年营运及财务管理经验。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(275, '北京', 'http://weibo.com/1924522877', '泰乐琪Trilogy,思伟投资', '郝玮', '职位:  , 副总裁', '互联网,移动互联网,环保能源,消费品,现代服务', '朝阳区', '0013cf58f4a8b3bd3f595aaa26d820de.jpg', '上海交通大学,中欧国际工商学院', '光速安振/光速创投', '郝玮先生于2010年加入光速创投, 参与投资并管理旗下的投资组合优众网。郝先生专注的投资领域包括互联网、移动、新媒体、清洁技术以及消费服务。在加入光速创投之前，郝先生曾任职于思伟投资，投资与研究领域涉及电子商务、互联网广告、移动互联网、云计算和网络游戏等。在此之前，他共同创立了由泰乐琪(Trilogy)全球孵化基金投资的基于GPRS的跨平台手机短信服务“做做客”，“做做客”服务的全球用户超过500万，并支持多国语言和不同的手机设备。“做做客”曾在2008年获得中国无线互联网行业中最受用户欢迎的十大产品之一。在此之前，郝玮先生就职于泰乐琪公司，为世界500强企业客户提供IT咨询。郝先生拥有中欧国际工商学院MBA学位和上海交通大学计算机科学与技术学士学位。郝先生在中小学各项学科竞赛中曾多次获奖并保送直升至上海交通大学联读班。郝先生还是中欧红枫会的执行委员。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(276, '北京', 'http://weibo.com/3181231313/', '华兴资本,思伟投资', '潘翔', '职位:  , 投资经理', '互联网,移动互联网,消费品,现代服务,传媒新媒体', '朝阳区', '02c7d7970bb86bdb480bc07fea998a85.jpg', '上海交通大学', '光速安振/光速创投', '潘翔先生2013年加入光速安振中国创投。潘先生专注的投资领域包括：互联网，移动，新媒体以及消费服务。在加入光速安振之前，潘先生曾任职于思伟投资上海办公室，他积极参与到被投资公司的管理工作和项目开发，所涉猎的领域包括移动互联网，电子商务，游戏，广告，在线旅游和教育等。在此之前，潘翔先生的职业生涯始于国内领先的新型投行华兴资本。在华兴资本工作期间，他积极参与到众多国内领先的消费品和科技公司的私募股权融资和并购交易，所参与的项目交易额近2亿美元。潘先生拥有上海交通大学测控技术与仪器学士学位，在上海交通大学就读期间，他曾获得奖学金资助参加纽约大学斯特恩商学院的交流活动。潘先生曾任上海交通大学电子信息与电气工程学院青年志愿者服务队队长，并获得2008年上海市优秀志愿者称号。');
