INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(288, '广东', '', '摩根斯坦利', '靳海涛', '职位:  , 董事长', '互联网,移动互联网,制造业,消费品,现代服务,传媒新媒体,教育培训,金融,文化创意,硬件', '深圳', '9e67d027c5099ce6237dbc114852d9e5.jpg', '华中科技大学', '深圳市创新投资集团(深创投)', '靳海涛2004年加入深圳市创新投资集团。华中理工大学工学硕士。靳海涛先生是国家科技部科技经济专家委员会专家，拥有近30 年企业管理、投融资和资本市场运作经验，曾任深圳市赛格集团有限公司常务副总经理，上市公司赛格股份有限公司总经理，迄今直接管理过国内五家知名上市公司。靳海涛先生曾在摩根斯丹利纽约总部接受高级培训并获证书，担任过全球策略投资基金驻中国特别代表。现兼任深圳市投资商会常务副会长、深圳市金融顾问协会执行副会长和温州市投资协会名誉会长。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(289, '广东', '', '深圳创新投', '李万寿', '职位:  , 总裁', '互联网,移动互联网,医疗健康,环保能源,制造业,消费品,现代服务,传媒新媒体,教育培训,金融,文化创意,硬件,农业', '深圳', '9bcaa5533df5daf29cc8ab997884f32b.jpg', '西安交通大学,中国社会科学院', '深圳市创新投资集团(深创投)', '李万寿1999年加入深圳市创新投资集团。西安交通大学管理科学与工程专业博士、中国社科院经济学博士,曾在美国进修一年。李万寿先生拥有十三年政府领导及宏观经济管理、十年创业投资及基金管理经验。2002年，入选清科公司“中国创业投资十佳基金融资人”。2008年，被“2008年中国国际私募股权投资论坛”评为“中国风险投资十年十大投资家”。');
