INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(292, '上海', 'http://weibo.com/1558226504', '', '查立', '职位:  , 创始合伙人', '互联网,移动互联网,医疗健康,环保能源,现代服务,传媒新媒体,文化创意', '闵行区', 'df6754dec8c443dd77647c90204261fb.jpg', '中国美术学院', '起点创业营', '查立先生是起点创业投资基金和乾龙创投合伙基金的创始合伙人，是一个多次成功创业而蜕变成的天使投资人。查立曾在Saatchi & Sattchi、ZiffDavis等著名跨国媒体公司任管理职务，后在美国创业，曾任EastWest Creative Communication Inc、InterPro等公司的总裁和CEO，并在英国上市公司MoPo担任其亚洲CEO。他在美国、欧洲和亚洲有丰富的国际商务经验，并在中国有多年作为投资人和企业家的直接创业和投资经验。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(294, '北京', '', '阿里巴巴,英特尔,AT&T', '黄炎', '职位:  , 合伙人', '互联网,移动互联网,医疗健康,环保能源,消费品,现代服务,传媒新媒体,教育培训,文化创意,硬件', '朝阳区', 'e2ab1d3a72aa5e6879790a9764a3e0ed.jpg', '浙江大学,宾夕法尼亚大学', '鼎晖投资', '黄炎于2006年加入鼎晖创投，参与了基金的创办。此前曾任英特尔投资中国区联席总监，负责英特尔在中国的风险投资业务。他所投资的行业覆盖互联网、清洁能源、消费服务、医疗器械和医疗服务等多个领域。在进入投资界之前他在阿里巴巴创业并任副总裁。此前就职于美国AT&T贝尔实验室，任技术主管。他拥有近十年的投资经历和丰富的创业及企业运营管理经验，在推动企业的快速成长和上市等方面发挥了重要作用。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(295, '北京', '', 'IDG', '王树', '职位:  , 合伙人', '互联网,移动互联网,医疗健康,环保能源,制造业,消费品,现代服务,传媒新媒体,教育培训,金融,文化创意,硬件,农业', '朝阳区', '83084e917f730e526a2df3d6ee5a3686.jpg', '北京大学', '鼎晖投资', '王树于2006年加入鼎晖投资，参与创办鼎晖创投。此前曾任IDG技术创业投资基金合伙人及IDG广东太平洋技术创业投资公司董事总经理。从事风险投资十余年，积累了丰富的投资与项目管理经验。曾出任多家被投资公司的董事，并且在推动公司上市或并购等重大决策和运作上发挥至关重要的作用。此前，他曾就职于广东省人民银行下属的南方金融公司，任基金管理部总经理；还曾在广东国际信托投资公司先后出任证券部副经理和基金管理部经理。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(293, '北京', '', '中金公司CICC', '吴尚志', '职位:  , 创始人及董事长', '互联网,移动互联网,医疗健康,环保能源,制造业,消费品,现代服务,传媒新媒体,教育培训,金融,文化创意,硬件,农业', '朝阳区', '9ccbfb25cd2ecef6f8c1b2f5356864f0.jpg', '麻省理工学院MIT,北京科技大学', '鼎晖投资', '吴尚志，鼎晖投资创始人、董事长，业界尊称“老吴”，著名风险投资人。吴尚志是“老三届”毕业生，又是文革后最早一批留学美国的，在闯荡华尔街多年后回国，致力于打造本土投资公司品牌。2009年12月15日，位列《福布斯》中文版杂志“2009福布斯中国最佳创业投资人”榜第二位。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(296, '北京', '', '新加坡政府直接投资GIC', '许尚威', '职位:  , 合伙人', '互联网,移动互联网,医疗健康,环保能源,制造业,消费品,现代服务,传媒新媒体,教育培训,金融,文化创意,硬件,农业', '朝阳区', '13380903c09ed165bc78d917b341d002.jpg', '加州大学伯克利分校', '鼎晖投资', '许尚威于2007年加入鼎晖创投。此前曾任新加坡政府直接投资有限公司（GICSI）高级副总裁，工作于新加坡、香港和北京，主要负责GICSI在中国的投资，行业覆盖TMT和传统产业，包括半导体、软件、金融服务、大众消费、能源、制造业等多个领域。许尚威在成长期及扩张期的投资上积累了十余年的投资经验，并且在帮助企业解决不同发展阶段的问题上发挥了重要作用。在此之前，他曾就职于美国Kaiser Aluminum & Chemicals Corp，任高级研发工程师。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(297, '北京', 'http://weibo.com/1772894583', '华登国际,中金公司CICC', '陈文江', '职位:  , 合伙人', '互联网,移动互联网,消费品,现代服务,传媒新媒体,文化创意', '朝阳区', '9aeaac090784c1ea4ce2cfd64e133d0a.jpg', '中国人民大学,中欧国际工商学院', '鼎晖投资', '陈文江于2006年加入鼎晖创投，已主导和参与多个项目的投资，涉及互联网、新媒体、电子商务、大众消费品等多个领域，并出任多家公司的董事。此前她就职于华登国际投资集团，主要专注于互联网、通信、消费品电子、半导体、S软件及IT服务等领域的投资；之前她还曾就职于中国国际金融公司和专注于大众消费品领域的私募股权投资的中国轻工业基金。十余年的投资经历使她对投资项目有独到的眼光和敏锐的判断力；对通讯、互联网、新媒体，消费品电子、软件及大众消费品等诸多行业领域有深刻的理解。');
