INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(310, '北京', '', 'SK集团', '康梁', '职位:  , 投资经理', '互联网,移动互联网,消费品,现代服务', '朝阳区', '2cf710a178fed638da425ca69cc60eab.jpg', '上海交通大学,中欧国际工商学院', '鼎晖投资', '康梁于2012年加入鼎晖创投，专注的投资领域包括互联网、移动、高科技、及消费服务。在加入鼎晖创投之前，康梁就职于韩国SK电讯集团，在企业创新、业务设计、战略投资并购等多个部门工作，在中韩两国的互联网及通讯领域积累了丰富的经验。康梁拥有中欧国际工商学院的MBA学位及上海交通大学的工程学士学位。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(311, '北京', '', '深圳创新投', '张磊', '职位:  , 投资经理', '制造业,消费品,现代服务,农业', '朝阳区', 'bc12c547d69de38aa7042ada01ed5636.jpg', '', '鼎晖投资', '张磊于2012年加入鼎晖创投，主要专注于先进制造、现代农业和消费服务行业的风险投资，加入鼎晖前曾在滨海新区政府引导基金和深创投工作。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(312, '北京', '', '', '王璟婷', '职位:  , 投资经理', '医疗健康', '朝阳区', 'e7e3315fcd7afff4648e3aaddb236cbd.jpg', '中国科学技术大学,纽约州立大学', '鼎晖投资', '王璟婷于2012年加入鼎晖，主要专注于医疗领域的投资。加入鼎晖之前曾就职于名力中国成长基金和苏州生物纳米园，参与医疗等领域的投资。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(313, '北京', 'http://weibo.com/1687424320', '亚盛投资Ascend', '郭佳', '职位:  , 合伙人', '互联网,移动互联网,传媒新媒体', '朝阳区', 'c00bd259d64d147f4e3ce6fc1cec0b8b.jpg', '哈佛大学,长江商学院,中央财经大学', '银泰资本Ventech', '郭佳 (Cindy Guo)，2007年加入欧洲最优异的通讯媒体及创新投资基金“Ventech Capital（银泰资本）”，并成立了专注于中国本土的基金“Ventech China”，致力于培育并扶植中国本土的创新及成长性企业。郭女士极大地丰富了银泰资本中国基金的各种人脉资源，在其领导下，银泰资本完成了对广西开元机械、VIVA手机畅读、神州付收集支付、久娱娱乐、广州盈正手机游戏、51玩、宜搜、聚美优品和寺库的投资。郭女士目前仍是上述公司的董事或观察员。加盟银泰资本之前，郭女士任Ascend Capital Partners的董事总经理。她带领团队负责风险投资与财务顾问工作。主要关注通信、媒体、技术与创新、金融服务、汽车制造与医疗保健行业。她曾经投资、顾问过的公司有： 悠视网, 橡果国际 (NYSE: ATV), 雷士照明(HKSE: 2222), Good Media 等。之前，郭女士作为副总裁，服务于宏天阕信息技术有限公司，负责公司的业务拓展与投资人关系。早时，她作为江苏阳光集团的驻京代表，协助集团与中央政府各职能机构的沟通交流、项目融资、国际合作等工作。郭女士毕业于中央财经大学国际金融系，获国际金融与国际会计双专业之学士学位。学生时代，郭女士历任学生会副主席、团委宣传部长等职务，并在以下竞赛中获得多种荣誉：华罗庚数学竞赛、全国物理竞赛、全省美术大赛等。工作后，郭女士获选于哈佛大学商学院股权投资高级行政班、长江商学院能源总裁班进修。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(314, '广东', 'http://weibo.com/1735880277', '腾讯,A8音乐', '刘晓松', '职位:  , 创始合伙人', '互联网,移动互联网', '深圳', '50ed1d2a1c401b6600b6841cd8793705.jpg', '清华大学,湖南大学', '青松基金', '刘晓松（Alvin Liu）是青松基金创始合伙人、A8音乐集团创始人兼CEO。刘晓松先生于一九八四年毕业于中国湖南大学，获得电子工程学士学位。一九八七年，刘先生毕业于中国电力科学研究院，获得工程硕士学位。一九九一年，刘先生在清华大学攻读其博士研究课程。在技术、媒体及电信行业拥有多年丰富创业和投资经验。2000年创办A8音乐，于2008年成功在香港主板上市，成为中国数字音乐行业领导者。早年投资腾讯QQ，为联交所主板上市公司腾讯控股有限公司的联合创办人之一。刘晓松先生深信移动互联网会给人类生活带来巨大变革。创办青松基金，本着“投资优秀团队、创造伟大公司”的精神，定位于投资早期阶段（尤其是天使投资阶段）的互联网和移动互联网项目。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(315, '上海', 'http://weibo.com/1406101502', '盛大,A8音乐', '苏蔚', '职位:  , 创始合伙人', '互联网,移动互联网', '浦东新区', '6646063bf1616e8ad3b2b0bbde1f508b.jpg', '上海财经大学', '青松基金', '苏蔚，青松基金创始合伙人。1998年毕业于上海财经大学，目前担任A8新媒体集团首席投资官；加入A8之前，在盛大网络工作七年，领导盛大投资部门通过投资并购手段培育了盛大文学、盛大棋牌等目前已具备独立分拆上市条件的业务单元。并且在互联网相关领域完成了多个成功的并购项目，在投资与投资管理方面有10多年的丰富经验。');
