INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(190, '上海', 'http://weibo.com/1706920104', '盛大', '谷承文', '职位:  , 投资经理', '互联网,移动互联网,传媒新媒体', '浦东新区', 'ef4184d21f759f572685d99f1efeaf36.jpg', '武汉大学,中山大学', '晨兴创投MorningSide', '晨兴资本投资经理，关注TMT相关早期项目。此前曾在盛大资本工作。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(226, '国外', '', 'Jefferies & Co', '徐聪', '职位:  , 投资经理', '互联网,移动互联网', '', '86fe2c423c779d9b8c4574ef0748d12d.jpg', '斯坦福大学,南加州大学', 'DCM', '作为投资经理，徐聪负责对新投资项目的分析评估，以及对已投资公司在融资和其他战略领域提供帮助。加入DCM之前，徐聪供职于投资银行Jefferies & Co.， 主要负责各类金融策略咨询业务，并购交易以及股权融资。在Jefferies任职期间，徐聪为科技领域的许多客户提供服务，包括商业智能，大数据分析，医疗信息技术，能源，工业自动化，零售餐饮系统，互动营销以及人力资源管理等领域。在这之前，徐聪曾供职于美国银行证券投资银行部和南加州大学的纳米电子学及先进材料创新实验室。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(228, '上海', '', '普华永道,中国银行,霸菱投资', '徐新', '职位:  , 总裁', '互联网,移动互联网,消费品,现代服务', '浦东新区', 'b5741b234d087fa9bd591138a2841d80.jpg', '南京大学', '今日资本', '徐新女士现任今日资本总裁，1988年毕业于南京大学外语系，中华英才网的初始投资者，中国风险投资协会理事会理事，中国最优秀的风险投资家之一。拥有在中国10年以上的风险投资经验。此前曾担任霸菱投资中国区董事总经理，在百富勤直投部门、普华永道、中国银行总行等工作。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(208, '北京', '', '', '陆勤超', '职位:  , 副总裁', '医疗健康', '朝阳区', 'edbdf5c355cc70498f171a4469df28af.jpg', '华中科技大学,香港中文大学', '红杉资本中国', '陆勤超（Cherry Lu），红杉资本中国基金副总裁关注领域为健康医疗行业。在2005年加入红杉之前，陆勤超女士一直在医疗领域从事市场及医院项目投资合作工作，积累了较丰富的行业知识和经验。陆勤超女士拥有香港中文大学MBA学位，华中科技大学同济医学院临床医学学士学位。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(209, '北京', '', '中金公司CICC', '潘旻', '职位:  , 副总裁', '环保能源,制造业', '朝阳区', '56b42d2b4cf299f8cedfd72df06811f9.jpg', '上海交通大学', '红杉资本中国', '潘旻(Kevin Pan)，红杉资本中国基金副总裁。关注领域为环保新能源及制造业。在2006年加入红杉之前，潘旻先生任中国国际金融有限公司（CICC）副总裁。在中金公司，潘旻先生先后任职于直接投资部和投资银行部，从事私募股权投资、境内外首次公开发行、上市公司收购兼并等工作，向国有、民营、跨国公司等各类型企业提供多种金融产品和服务。潘旻先生拥有上海交通大学硕士和双学士学位。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(210, '北京', 'http://weibo.com/1660225490', '索尼,财讯传媒,时尚传媒', '浦晓燕', '职位:  , 董事总经理', '传媒新媒体,文化创意', '朝阳区', '8f2be4fce694273a075d24bc2d7ccad2.jpg', '南开大学,德克萨斯大学奥斯汀分校', '红杉资本中国', '浦晓燕（Enita Pu），红杉资本中国基金董事总经理。在2012年加入红杉资本前，浦女士拥有近15年的文化、媒体企业运营和投资经验。浦女士曾在中美两地工作，先后服务过多家成功传媒企业包括索尼影视、财讯传媒、时尚传媒集团等，历任CEO、副总经理、助理出版人等高层管理工作。浦女士拥有美国得克萨斯大学奥斯汀分校获得工商管理学硕士学位，南开大学经济学学士学位。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(211, '北京', 'http://weibo.com/1719707494', '', '孙华伟', '职位:  , 分析师', '环保能源', '朝阳区', '894a9cf6d44b9f168282094a3bea9248.jpg', '对外经济贸易大学', '红杉资本中国', '孙华伟(Apple Sun)，红杉资本中国基金业务发展分析师。关注领域为新能源及环保行业。在2008年加入红杉之前， 孙女士拥有丰富的银行实习及海外交流经验。孙华伟女士拥有对外经济贸易大学经济学学士学位。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(212, '上海', 'http://weibo.com/1985075081', '中金公司CICC', '唐一炯', '职位:  , 分析师', '消费品,现代服务', '静安区', 'ea7a7f29a4a2a8064b6c5d829c041f11.jpg', '麻省理工学院MIT,杜克大学,英国布里斯托大学', '红杉资本中国', '唐一炯(Yijiong Tang)，红杉资本中国基金分析师。关注领域为消费品与现代服务行业。在2011年加入红杉之前，唐先生任职于中国国际金融有限公司投资银行部, 参与了上市公司资产重组和再融资等工作。唐一炯先生拥有美国麻省理工学院斯隆商学院金融学硕士学位，美国杜克大学经济学硕士学位和英国布里斯托大学经济与金融学学士学位。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(214, '北京', 'http://weibo.com/1663158744', '', '许维娅', '职位:  , 分析师', '互联网,移动互联网,传媒新媒体', '朝阳区', '98836ec6e205eefb2ee1bf539acf542f.jpg', '对外经济贸易大学', '红杉资本中国', '许维娅（Selina Xu），红杉资本中国基金业务发展分析师。关注领域为传媒与科技行业。在2008年加入红杉资本前，许女士有丰富的海外及国内工作经历，涉及金融、新媒体、电商、消费品等行业。许维娅女士拥有外经济贸易大学经济学学士学位。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(213, '江苏', 'http://weibo.com/1088685265', '', '魏洪波', '职位:  , 区域业务总经理', '环保能源,制造业', '苏州', 'bee56a5a01521ab6fbdbd82e4c1aa99b.jpg', '华中科技大学', '红杉资本中国', '魏洪波（David Wei），红杉资本中国基金区域业务总经理。在加入红杉之前，魏洪波先生先后任职于苏州信托和苏州创投集团，主要关注国内清洁技术、先进制造、新材料以及汽车领域的成长期企业投资，期间负责投资胜利精密（SZ：002426）、九鼎新材（SZ：002201）、德威新材(SZ:300325)、江南嘉捷(SH:601313)等多个先进制造和新材料领域项目国内成功上市。另外，魏洪波先生还在东风汽车公司有10年的机械自动化技术和管理工作经历。魏洪波先生拥有华中科技大学MBA和工学学士学位。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(172, '北京', '', '斯伦贝谢,中科招商', '李飞', '职位:  , 投资经理', '医疗健康,环保能源', '朝阳区', 'a7b108e3c412c35efdfb94461fbe1715.jpg', '清华大学', '启明创投', '李飞2011年加入启明。之前就职于中科招商，从事能源、清洁技术和医疗健康方面的投资。还曾作为高级工程师在油田服务企业斯伦贝谢公司的美国、加拿大、和非洲部门工作五年，对于页岩气开采有着丰富经验。持有清华大学生物医学工程与仪器专业学士和硕士学位。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(173, '北京', '', '诺基亚,宝洁', '周笑羽', '职位:  , 投资经理', '互联网,移动互联网', '朝阳区', '2b820423ef70f978edfb22844e684516.jpg', '北京大学', '启明创投', '周笑羽2012年加入启明。加入启明前就职于诺基亚战略规划部，之前还曾担任宝洁产品经理。她持有高分子材料学士学位和北京大学MBA');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(175, '国外', 'http://weibo.com/1833066574', '', '林仁俊', '职位:  , 董事总经理', '互联网,移动互联网,环保能源', '', '0e80b960f7e43244e5ff2bb2cdd8eddb.jpg', '斯坦福大学,新加坡国立大学', '金沙江创投', '林仁俊是金沙江创投的创始人之一，负责硅谷办事处。林仁俊专注于互联网、无线和新能源领域的投资。他目前尤其关注互联网广告技术、互动娱乐特别是其在无线领域的应用、以及太阳能技术。在互联网领域，他目前担任董事的公司包括易传媒、游亚软件、百合网、百姓网、拉手网、兰亭集势和去哪儿旅游搜索。在新能源领域，他担任董事的公司包括SEEO和喜瑞能源。创立金沙江创投之前，林仁俊曾经在美国参与创办了三家互联网软件公司(Co-nect, iMarket and Quickdot)。他也曾在莲花软件公司担任高层管理职务。林仁俊在新加坡出生长大。他拥有新加坡国立大学的医学学位和斯坦福大学的MBA学位。林仁俊是亚美高科技商业协会(AAMA) 的理事。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(174, '北京', '', '亚信联创', '丁健', '职位:  , 董事总经理', '互联网,移动互联网,传媒新媒体,硬件', '朝阳区', '8ca04c1a01f11cab1804fd7e1ad4390e.jpg', '北京大学,加州大学伯克利分校,加州大学洛杉矶分校', '金沙江创投', '丁健专注于互联网、无线和新媒体领域的投资。他目前担任董事的公司包括博思通信、中广瑞波和网秦。加盟金沙江创投以前，丁健创办了亚信联创。在担任CEO期间，他领导亚信联创成为第一家在美国纳斯达克上市的中国科技公司。丁健目前仍担任亚信联创的董事长，并且担任百度的独立董事。丁健拥有北京大学的化学学士学位，加州大学洛杉矶分校的信息科学硕士学位，和加州大学伯克莱分校Haas商学院的EMBA。丁健是“数字中国”联合会（e-China Alliance）的发起者之一。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(176, '北京', '', 'Nortel北电网络', '潘晓峰', '职位:  , 董事总经理', '互联网,移动互联网,环保能源', '朝阳区', 'a2b3f14655d060568bd08a8b54aeb361.jpg', '浙江大学,南京大学', '金沙江创投', '潘晓峰专注于绿色科技领域的投资，特别是在LED、太阳能和能效技术方面。潘晓峰还参与投资互联网、无线和汽车相关企业（如汽车电子和售后市场服务）投资。他目前担任董事的公司包括阿尔特、晶和照明、晶能光电、易美芯光、惠德时代和太时芯光。加盟金沙江创投前，潘晓峰先生是亚洲无线科技的创始人之一。他也曾在北电网络担任高级职务并在中国高科技业界拥有众多的人脉关系。潘晓峰在浙江大学获得工程学士学位，并在南京大学获得法学硕士学位。他也是霍普金斯南京大学中美中心的毕业生。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(177, '北京', 'http://weibo.com/2309174881', 'Nortel北电网络', '伍伸俊', '职位:  , 董事总经理', '环保能源,硬件', '海淀区', '7d775c2f1fdd4c5d4ce08c15db3f4531.jpg', '加州大学伯克利分校,麻省理工学院MIT,加拿大英属哥伦比亚大学', '金沙江创投', "伍伸俊是金沙江创投的创始人之一，负责金沙江基金中国管理公司，并专注于半导体、新材料和新能源领域的投资。他特别关注LED 与半导体照明领域，投资了包括晶能光电、易美芯光、大连三维和太时芯光在内的一系列LED企业。他负责牵头投资的公司包括恒原微电子、坤锐电子、重庆新格资源和深圳国微技术，并担任晶能光电、易美芯光和恒原微电子的董事长。创立金沙江基金之前，伍伸俊曾负责北电网络在中国的战略投资，包括牵头投资成立上海先进半导体和广东北电通讯设备有限公司 ，并曾担任广东北电和上海北电半导体的总经理。伍伸俊先生在中国长大，早年就读于加拿大英属哥伦比亚大学、加州大学伯克莱分校和麻省理工大学，并曾担任MIT斯隆学院Sloan Fellows' Board of Governors。");
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(179, '北京', '', 'IBM', '王炳晋', '职位:  , 合伙人', '互联网,移动互联网', '朝阳区', '0ad19555342c4dc1c64e12151e88bab8.jpg', '德克萨斯大学奥斯汀分校,美国伊利诺伊大学', '金沙江创投', '王炳晋于2010年8月作为合伙人加入金沙江创业投资基金，专注于互联网、无线和软件领域的投资。王炳晋先生为金沙江创投带来了作为一个成功企业家的多年丰富经验。他创建了Match.com（被IAC收购）、Interwoven（纳斯达克股票代码：IWOS，后被Autonomy收购）和Encentuate（被IBM收购）。他曾于1997年和2002年先后出任Interwoven的总裁兼CEO和董事长。在任职期间，他领导Interwoven在美国纳斯达克成功上市。随后，他加入了Encentuate，它是IBM新加坡软件实验室的前身。此外，王炳晋先生还曾在Sybase、Illustra和Gensym等公司担任管理、工程等重要职务。他还共同开发了多项专利。王炳晋先生目前担任新加坡一家2亿美元规模风险投资基金的主席，也是新加坡电信的独立董事。王炳晋先生持有得克萨斯大学奥斯汀分校电子工程学士学位，以及伊利诺伊大学香槟分校计算机科学硕士学位。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(178, '北京', 'http://weibo.com/1653195803', '百度,华为,中国移动', '李一男', '职位:  , 合伙人', '互联网,移动互联网', '海淀区', '9ca3068302f4f01c08d24dd5ce0f60fb.jpg', '华中科技大学', '金沙江创投', '李一男于2011年8月作为合伙人加入金沙江创业投资基金，专注于互联网和无线领域的投资。李一男在通信、互联网及无线等高科技领域积累了深厚的运营和管理经验，有着良好的人脉和资源，尤其是对技术创新和市场发展方向有着敏锐的判断力。李一男27岁即担任华为公司副总裁，历任总工程师及中央研究部总裁，首席电信科学家等职。在经历了港湾网络的创业历程之后，李一男加入百度任首席技术官，领导了百度下一代搜索引擎的开发。在加盟金沙江创投之前，李一男负责中国移动旗下综合信息服务门户12580的运营。李一男1993年毕业于华中理工大学（现华中科技大学）少年班，取得硕士学位。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(180, '北京', '', 'Nortel北电网络', '尹德纲', '职位:  , 合伙人', '环保能源,硬件', '朝阳区', 'b6d60240bb3954ee21603d51efe0ef34.jpg', '多伦多大学,麻省理工学院MIT', '金沙江创投', '尹德纲于2008年加入金沙江创业投资基金，专注于清洁技术以及电子硬件部件与系统领域的投资。他同时兼顾大陆/台湾的跨海峡投资机会。尹德纲目前担任C3Nano, Clean Focus, 方卫良品和新格资源的董事，是台湾立凯电能科技的董事会观察员，并且积极参与SEEO的投资。在加入金沙江创投之前，尹德纲供职于大河资本。加入大河资本前，尹德纲是北电网络的高级主管，曾负责管理大中华区GSM产品线，并曾担任亚洲区的运营商事业部财务总监。尹德纲曾在美国波士顿地区的Phonetic Systems公司就职。尹德纲在美国麻省理工学院获得工商管理硕士学位，并在加拿大多伦多大学获得工程硕士与学士学位。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(181, '上海', 'http://weibo.com/1714236313', '麦肯锡', '朱啸虎', '职位:  , 合伙人', '互联网,移动互联网', '浦东新区', '2d751e0ffeed591d32a3756b980128bf.jpg', '复旦大学,上海交通大学', '金沙江创投', '朱啸虎于2007年加入金沙江创业投资基金，专注于互联网、无线和企业IT投资。朱啸虎在电子商务领域参与了许多投资，包括拉手网、兰亭集势和梦芭莎。他目前担任精硕科技和梦芭莎的董事，并积极参与了中广瑞波和汇通天下的投资。加盟金沙江创业投资基金以前，朱啸虎创办了全球领先的保险行业应用软件提供商 - 易保网络技术有限公司。他领导了核心产品 - 产寿险核心业务系统的开发，并全面负责中国、日本、韩国及香港地区的业务，在大型软件产品开发和国际化发展方面积累了丰富的经验。朱啸虎还曾是麦肯锡咨询公司的资深咨询顾问。在企业战略、流程再造、成本管理、IT系统等方面有丰富的咨询及实施经验。朱啸虎拥有复旦大学国际经济硕士学位和上海交通大学通信工程学士学位。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(182, '北京', 'http://weibo.com/1834257657', '麦肯锡,厚朴投资', '胡海晨', '职位:  , 副总裁', '医疗健康', '朝阳区', '0e11b9da8de1ebbac17685da7c2cc16e.jpg', '中国协和医科大学', '金沙江创投', '胡海晨于2011年加入金沙江创业投资基金，专注于健康医疗服务、医疗器械和生物制药领域的投资。加盟金沙江创投之前，胡海晨就职于厚朴投资，积极关注医疗健康领域，并参与了对境外大型医疗集团的投资。在就职于厚朴投资之前，胡海晨在麦肯锡咨询公司大中国区任咨询顾问一职。在企业战略、市场评估、流程优化和新业务展开等方面有着丰富的咨询及实施经验。胡海晨参与的医疗咨询项目包括国家医改、欧洲商业医保企业、跨国医药制造公司和跨国医疗器械公司等。胡海晨拥有中国协和医科大学八年制临床医学专业的博士学位。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(183, '北京', 'simonv@gsrventures.com', 'Nortel北电网络', '李显弘', '职位:  , 副总裁', '互联网,移动互联网,硬件', '朝阳区', 'b73eb007de9f8f35390b7ba68d3ad6ce.jpg', '加拿大英属哥伦比亚大学', '金沙江创投', '李显弘专注于互联网，无线，半导体和新材料领域的创业投资。他目前是恒原微电子和坤锐电子的董事。李显弘在无线电通讯，半导体行业从RFIC设计，系统设计，到市场销售有超过15年的经验。加盟金沙江创投之前，李显弘曾在北电中国担任高级职务，负责WCDMA产品的市场发展。在此之前，李显弘先生作为团队的主要成员为北电在北美和韩国市场赢得了极其重要的客户。李显弘获得英属哥伦比亚大学理学学士学位。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(184, '北京', 'http://weibo.com/1990338667', '三星', '刘海', '职位:  , 副总裁', '环保能源,制造业,硬件', '海淀区', '4dc5e15f420c915b3e68030b439f1b4f.jpg', '上海交通大学,麻省理工学院MIT,首尔国立大学', '金沙江创投', '刘海于2011年加入金沙江创业投资基金，专注于清洁技术、新能源、新材料和半导体领域的投资。他同时深入参与了金沙江创投旗下投资的LED公司的运营管理。加盟金沙江创投之前，刘海在韩国三星电子总部担任高级产品经理职务。他在无线，电信和半导体板块有着丰富的行业经验，涉及软件硬件架构设计，供应链管理，以及针对拉丁美洲和中国主要电信运营商的市场销售。此外，刘海是三星电子全球奖学金项目的成员。刘海拥有麻省理工学院的工商管理硕士学位和首尔国立大学的电子工程硕士学位，以及上海交通大学的电子工程学士学位。');
