INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(185, '北京', 'http://weibo.com/1846803727', '亚信联创,中国网通', '杨志伟', '职位:  , 副总裁 , , 合伙人', '互联网,移动互联网', '海淀区', 'dcd48876aefbba07429c6e1285d0ba96.jpg', '北京大学', '金沙江创投,云天使基金', '杨志伟于2010年11月加入金沙江创业投资基金，专注于电信、互联网、无线移动和和支付领域的投资。杨志伟在电信和移动支付领域参与了多项投资，包括SIMO，盒子支付和艾睦网城。他在IT高科技领域拥有丰富的战略咨询和运营管理经验，并具有多年电信运营管理经历，尤其是对技术创新和市场发展方向及其商业机会有着敏锐的判断力。加盟金沙江创投之前，杨志伟具有多次创业和纳斯达克上市的经历。他曾担任原中国网通总工程师，是中国网通早期创始人之一，负责中国网通发展战略、新业务引入和技术发展方向，领导建设了世界上第一个IP/DWDM的中国高速互联网CNCnet开发。在创立中国网通之前，杨志伟曾担任中国第一家成功登陆纳斯达克的高科技公司亚信联创总工程师。此外，杨志伟被国家发改委聘请为中国下一代网络（NGN）战略规划专家委员会专家委员。杨志伟拥有光华管理学院EMBA学位。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(216, '北京', '', '深圳创新投', '姚宇', '职位:  , 董事总经理', '医疗健康,环保能源', '朝阳区', 'c8fd4d097d94d15a58755b3f5dce7d1f.jpg', '吉林大学,中欧国际工商学院', '红杉资本中国', '姚宇(Yu Yao)，红杉资本中国基金董事总经理。关注领域为健康医疗、环保及新能源等行业。在2009年加入红杉之前, 姚宇先生任职深圳市创新投资集团有限公司，负责过潍柴动力等多个项目的投资，并出任董事。姚宇先生拥有中欧国际商务学院MBA学位，吉林大学本科学士学位。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(189, '上海', 'http://weibo.com/1403470520', '', '梁维弘', '职位:  , 副总裁', '互联网,移动互联网', '普陀区', 'a776c7b43d7e6281bc153c9988a34c51.jpg', '南京大学,长江商学院,厦门大学', '晨兴创投MorningSide', '梁维弘，2009年加入晨兴创投，负责TMT领域的早起投资，担任投资经理。加入晨兴之前，梁维弘拥有8年的互联网和移动互联网创业经验。梁维弘毕业于厦门大学和南京大学，并且获得长江商学院MBA学位。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(215, '北京', '', '花旗银行,科尔尼咨询', '姚安民', '职位:  , 副总裁', '环保能源,消费品,现代服务', '朝阳区', 'd5d74c2454834bc8cf84e6154b94a2f8.jpg', '上海交通大学,哥伦比亚大学', '红杉资本中国', '姚安民（Michael Yao），红杉资本中国基金副总裁。关注领域为消费品与现代服务业、环保及新能源等行业。在2007年加入红杉之前，姚安民先生曾工作于科尔尼管理咨询公司、 Crimson Investment和花旗银行。姚安民先生拥有美国哥伦比亚大学商学院MBA和上海交通大学学士学位。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(217, '北京', 'http://weibo.com/2057969010', '花旗银行,东方证券', '赵兴华', '职位:  , 投资经理', '环保能源', '朝阳区', '72ab83bd7c2b738c3d68045ac14d2601.jpg', '北京大学,中山大学', '红杉资本中国', '赵兴华(Eric Zhao)，红杉资本中国基金投资经理。关注领域为能源及环保行业。在2012年加入红杉之前，赵兴华先生曾在东方证券投资银行部、花旗集团投资银行部从事先进制造、消费及现代服务行业公司的IPO及兼并重组业务。赵兴华先生拥有北京大学金融信息工程方向硕士学位及中山大学财务管理学士学位。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(218, '北京', '', '苹果,麦肯锡', '赵克仁', '职位:  , 创始人及董事合伙人', '互联网,移动互联网,传媒新媒体,硬件', '朝阳区', '2384809fe387185709d93de4462fad63.jpg', '斯坦福大学,布朗大学', 'DCM', '赵克仁 (David Chao)是 DCM的联合创始人和董事合伙人。自80年代以来，他一直活跃于信息技术行业，在高速发展的计算机、通讯和互联网等方面有颇多实践。在DCM，他指导创业公司进行企业规划和产品市场战略、组建强大的管理团队、建立国内和国际合作关系。加入DCM前，David是Japan Communications Inc的联合创始人，先后担任过代理首席财务官和首席技术官，并是董事会成员。早前，David在旧金山的麦肯锡任职管理咨询，为通讯和软件公司提供技术、市场和融资战略的咨询。在他职业生涯的早期阶段，David在苹果计算机负责市场和产品管理工作，在苹果前，David曾是日本最大的人力资源广告和网络媒介服务商Recruit的高级客户经理。David在DCM所投资的多个创业公司董事会任职，包括51job、人人等。David还在中国的联想投资公司董事会任职。他也是斯坦福商学院管理委员会 成员。他被Forbes Magazine从2004年以来列为最优秀25名风险投资家。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(219, '北京', 'hurst@dcm.com', '新浪,安永', '林欣禾', '职位:  , 董事合伙人', '互联网,移动互联网', '朝阳区', '87e67afb4af0f77fb98e87e70419e275.jpg', '斯坦福大学,达特茅斯学院', 'DCM', '林欣禾 (Hurst Lin)专注于亚洲业务。他为公司带来了丰富的合作机会和不同的文化视角，拓展公司在全亚洲的投资机会。Hurst曾是中国最大门户网站SINA的联合创始人，他帮助新浪成功的收购中国IT创业公司、管理公司运营和上市募集资金，以至将其发展成为价值十几亿美元的公司。Hurst曾担任新浪SINA Corporation 的首席运营官和董事会成员。新浪公司是第一家在Nasdaq成功上市的中国互联网企业。在1995年，他联合创建Sina.com的前身SinaNet。随后，他不仅带领公司IPO成功募集7,800万美元，还经历过互联网行业的寒冬，最终建立起年营业额10亿美元的企业。Hurst致力于公司日常管理、重大经营决策，以及上市前的资金筹集工作，并见证了新浪的全球化业务拓展以及整合兼并。早先，Hurst曾在安永集团供职，为电信和技术公司提供市场和运营方面的咨询。他还在Octel Communication Inc和NYNEX从事市场、工程和管理等方面的工作。Hurst出生于台湾，成长在纽约。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(221, '北京', 'http://weibo.com/1743219563', '工商银行', '曾振宇', '职位:  , 合伙人', '互联网,移动互联网', '东城区', '5254d36ef02b69b6e2b996e0090d7d7d.jpg', '西安交通大学,长江商学院', 'DCM', '曾振宇(Ramon Zeng)是常驻北京办公室的董事，2014年升为合伙人。 他负责新投资项目的评估和执行，并帮助已投资公司对融资、并购和其他战略问题进行分析规划。曾振宇曾任职于磐锐投资基金，负责项目的评估和交易的执行。曾振宇还曾担任 AIU/AIG公司的商业分析师，负责大中华地区收购兼并和战略规划。在此之前，他就职于工商银行总行电子银行部。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(222, '北京', 'http://weibo.com/1648573770', '摩根斯坦利', '胡博予', '职位:  , 投资副总裁', '互联网,移动互联网', '海淀区', '763bfde84edfd8e96c7db4e6c0f6378d.jpg', '美国西北大学,上海外国语大学', 'DCM', '胡博予在高科技领域有约十年的创业和投资经验。他参与创办了中国最大的信贷风险管理软件公司安硕科技，带队开发了核心产品并帮助其占领市场。他还曾在摩根士丹利风险投资部和金沙江创投从事过创业投资。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(220, '北京', 'http://weibo.com/2218581905', '高盛', '卢蓉', '职位:  , 董事合伙人', '互联网,移动互联网,消费品,现代服务,传媒新媒体,教育培训,金融,文化创意,硬件', '朝阳区', '10ac9d8d5c4e8b74b546b950ce897899.jpg', '约翰霍普金斯大学,马里兰大学', 'DCM', '作为董事合伙人，卢蓉（Ruby Lu）为创业者带来了丰富的国际金融经验和成功的企业管理意识。为DCM对创业者进行投资并建立具有全球化视野的企业。卢蓉主导DCM在文思创新、易车网和当当网的投资，并均为现任董事。在2003年末加入DCM前，卢蓉在华尔街的高盛投资银行工作多年。她在硅谷任TMT投资银行部门副总裁期间，曾为许多知名的公司提供上市，市场融资和公司兼并收购方面的咨询以及实际的市场操作与执行。她在美国硅谷五年期间的一些有代表性的客户包刮：英特尔，雅虎，eBay,联想，神州数码，和记黄埔，日本电信电话，NTT DoCoMo 和网易等。在硅谷前，卢蓉也曾在高盛的纽约资本市场部门从事过股票定价，股票如何合理地配售给有价值的机构投资人的实际操作工作。她在96／97年在高盛亚洲参与过一些国家大型国企的改组和股份私有化工作： 如南方航空集团，中国移动，以及泰国石油和天然气公司的改组与上市。卢蓉是生长在福建厦门的客家人。卢蓉被提名2012年福布斯世界顶级投资人。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(223, '国外', '', '摩立特集团Monitor', '赖嘉满', '职位:  , 投资副总裁', '医疗健康,消费品,现代服务', '', '52fbb021b4274a79a72560c27be94620.jpg', '麻省理工学院MIT,西班牙IE商学院', 'DCM', '赖嘉满是DCM的投资副总裁，主要负责新投资项目的评估和执行，并帮助已投资公司对融资、并购和其他战略问题进行分析规划。赖嘉满曾任职于摩立特战略咨询公司任资深顾问，为医疗器械和消费品企业等提供战略规划咨询、渠道、价格分析及在中国的成长战略。他还曾在劳森软件的顾问部和项目管理部门任职，帮助食品和饮料企业及生产厂家制定业务战略并参与实施。');
