INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(196, '北京', '', '中信', '杜佳佳', '职位:  , 投资经理', '消费品,现代服务', '朝阳区', '1469c1d843bb26683c49b7de4dd340a7.jpg', '武汉大学,对外经济贸易大学', '红杉资本中国', '杜佳佳（Jason Du)，红杉资本中国基金投资经理。关注领域为消费品与现代服务业。在2012年加入红杉之前，杜先生任职于凯晨资本基金，负责过高端制造、消费品等多个领域的投资工作；在此之前，杜先生曾在中信证券股份有限公司企业发展融资部担任项目经理，从事境内中小企业上市融资工作。杜佳佳先生拥有对外经济贸易大学金融学硕士和武汉大学理学学士学位。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(197, '北京', '', '辉瑞Pfizer', '杜莹', '职位:  , 董事总经理', '医疗健康', '朝阳区', 'a2398877e8640d47cd2f9646112ba720.jpg', '辛辛那提大学', '红杉资本中国', '杜莹(Samantha Du)，红杉资本中国基金董事总经理。关注领域为健康医疗行业。在2012年加入红杉之前，杜博士于2002年创立和记黄埔医药上海有限公司并担任总裁。杜博士亦是和黄中国医药科技有限公司主要创立人及首席科技官，曾于2006年主持筹划其在英国上市；在创立和黄医药之前，杜博士曾在美国辉瑞公司全球战略收购部工作，主管全球代谢类疾病项目转让及相关兼并收购。杜博士毕业后于辉瑞公司美国中央研究院开始新药研发科研工作，曾领导不同治疗领域内的多个前期及后期药物项目研究，其中有两个药物获得批文在全球销售。杜博士拥有美国辛辛那提大学生物化学博士学位。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(195, '北京', 'http://weibo.com/1886112020', '昆吾九鼎投资,德勤Deloitte', '程少宾', '职位:  , 投资经理', '医疗健康', '朝阳区', 'f2ee3e7d042826edc96689e1a85cf600.jpg', '北京化工大学', '红杉资本中国', '程少宾（Sammy Cheng），红杉资本中国基金投资经理。在2010年加入红杉之前，程先生任职于九鼎投资（昆吾九鼎投资管理有限公司），负责财务分析工作。在此之前，程先生曾任职于德勤华永会计师事务所。程先生拥有北京化工大学管理学学士学位。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(198, '北京', 'http://weibo.com/2244560783', '法国电力', '富欣', '职位:  , 董事总经理', '环保能源', '朝阳区', '1c5f7711bda9d2fde4f23cf3d56b3e15.jpg', '清华大学,哈尔滨工业大学,INSEAD欧洲工商管理学院,法国Poitier大学', '红杉资本中国', '富欣（Caroline Fu），红杉资本中国基金董事总经理。关注领域为能源与环保行业。在2010年加入红杉之前，曾任职于法国电力公司研发部及亚太总部，负责能源及相关项目的投资，在能源领域，尤其是可再生和清洁能源领域拥有非常丰富的经验。富欣女士拥有INSEAD及清华大学的EMBA学位，法国POITIER大学硕士学位和哈尔滨工业大学动力工程学士学位。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(200, '北京', '', '奥博资本Orbimed,礼来制药', '顾翠萍', '职位:  , 投资经理', '医疗健康', '朝阳区', '981005a3d96e61e7ef7b2521a0f9a33b.jpg', '上海交通大学', '红杉资本中国', '顾翠萍(Trency Gu), 红杉资本中国基金投资经理。关注领域为健康医疗行业。在2012年加入红杉之前，顾女士曾任奥博医疗资本亚太区投资经理，负责生物医疗领域，包括制药、生物技术、医疗器械、医疗服务等细分行业的投资。在加入奥博医疗前，顾女士是礼来制药全球研发部的项目经理，负责礼来制药在中国地区的研发合作、项目引进及管理。在此之前，顾女士是上海睿星基因技术有限公司的研究规划部经理，负责新药项目引进、临床前研究及注册申报工作。顾女士拥有上海交通大学生物化学与分子生物学博士学位，和上海交通大学生物技术与英语双学士学位。');
