INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(201, '上海', 'http://weibo.com/1800398210', '麦肯锡,西门子', '郭山汕', '职位:  , 副总裁', '消费品,现代服务', '静安区', 'ff544e23dde4fdf47911d69f15fd1f8c.jpg', '重庆大学,英国拉夫堡大学', '红杉资本中国', '郭山汕（Colin Guo），现任红杉资本中国基金副总裁。关注消费品和现代服务业领域的投资。在2010年加盟红杉之前，郭山汕先生任职于麦肯锡咨询公司，专注交通运输,基建和旅游行业。郭先生为包括中国邮政、中外运发展、中国国航，上海机场，中集集团，上海港，铁道部和港铁公司在内的政府和企业提供投资、战略和运营咨询意见。郭山汕先生还曾经在西门子公司担任供应链项目经理。郭山汕先生拥有英国拉夫堡大学硕士学位,和重庆大学学士学位。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(202, '北京', 'http://weibo.com/1770704422', '德勤Deloitte,中金公司CICC', '郭振炜', '职位:  , 投资经理', '消费品,现代服务', '朝阳区', '3c22dbd0fe0b5ce7e372c21e9631a3a5.jpg', '中央财经大学', '红杉资本中国', '郭振炜(Wayne Guo)，红杉资本中国基金投资经理。在2010年加入红杉之前，郭先生任职于中国国际金融有限公司研究部，从事消费行业研究工作。郭振炜先生还曾在德勤华永会计师事务所担任高级审计师职务。郭振炜先生拥有中央财经大学财务管理学士学位。');
