INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(205, '上海', 'http://weibo.com/1014317134', '启明创投', '李振明', '职位:  , 副总裁', '医疗健康', '静安区', '7f4c73cab18c66d44c80adfd1beb6f9c.jpg', '清华大学,首都医科大学', '红杉资本中国', '李振明 (Zhenming Li)，红杉资本中国基金副总裁。关注领域为健康医疗行业。在2011年加入红杉之前，李先生曾就职于启明创投，专注于生物医药、医疗器械和医疗服务等方面的投资。加入投资行业之前，李先生曾在北京市同仁医院从事研究工作。李振明先生拥有清华大学生物医学工程硕士学位， 首都医科大学生物医学工程学士学位。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(206, '北京', 'http://weibo.com/1780278033', '昆吾九鼎投资', '梁江', '职位:  , 副总裁', '医疗健康', '朝阳区', '9b2513fcd9e59116c56901fa2444d52f.jpg', '清华大学', '红杉资本中国', '梁江（Jiang Liang)，红杉资本中国基金副总裁。关注领域为健康医疗行业。在2010年加入红杉之前，任九鼎投资（昆吾九鼎投资管理有限公司）投资总监，负责过医药、消费领域多个项目的投资。梁江先生拥有清华大学材料科学与工程系工学学士学位，以及清华大学经管学院技术经济与管理系管理学硕士学位。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(230, '上海', 'http://weibo.com/1752068253', '英特尔', '袁文达', '职位:  , 合伙人', '互联网,移动互联网', '静安区', '64a40413b46975145885488e256255b5.jpg', '麻省理工学院MIT', '红点投资Redpoint Ventures', '袁文达先生(David Yuan)是红点投资的合伙人及中国区的负责人，负责了红点在中国的多项投资，包括玺诚传媒、奇虎360、硅谷动力等。David在加盟红点投资之前是iTelco通讯公司的联合创办人并曾任该公司首席执行官。 ITelco通讯是一家开发并提供新一代通讯网络技术和解决方案的供应商。此前David是Opentel通讯公司（一家国际性电讯运营商）的联合创办人。在Opentel公司之前，David还曾经是Genesol Enterprise的共同创办人。David的职业生涯开始于英特尔公司，担任集成电路设计工程师、微处理器部门产品经理。David拥有麻省理工学院电子工程学士学位、麻省理工学院斯隆管理学院工商管理硕士学位。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(229, '上海', '', '英特尔,摩根大通,AIG', '温保马', '职位:  , 合伙人', '互联网,移动互联网,消费品,现代服务', '浦东新区', '268b28064d0f4a62d52d3de48706c4b4.jpg', '清华大学,英国伦敦商学院', '今日资本', '温保马是今日资本合伙人，曾在英特尔投资工作，领导了数个中国项目的投资。温先生曾在AIG香港担任过投资经理。在此之前，温先生曾在JP摩根的公司融资部工作并为菲利浦公司担任过设计工程师。温先生拥有伦敦商学院的工商管理硕士学位和清华大学的电子工程硕士学位。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(207, '北京', '', '华登国际', '梁杰', '职位:  , 投资经理', '环保能源', '朝阳区', '7851074abb5f6f8a44daceca2e8f9dc4.jpg', '上海交通大学', '红杉资本中国', '梁杰(James Liang)，红杉资本中国基金投资经理。关注领域为能源与环保行业。在2012年加入红杉前，梁杰先生在华登国际负责清洁技术和制造业相关的投资。在此之前，梁杰先生曾在中国和硅谷，服务于半导体设备领导者和太阳能创业公司。梁杰先生拥有上海交通大学微电子学硕士学位，材料科学本科学位。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(231, '香港', '', '溢达集团', '杨敏德', '职位:  , 合伙人', '消费品,现代服务', '湾仔区', '4ff743bbe56cdd9a18592ca48e20ad88.jpg', '哈佛大学,麻省理工学院MIT', '红点投资Redpoint Ventures', '杨敏德(Marjorie Yang)是红点创投在中国的兼职合伙人，现任溢达集团主席兼行政总裁。杨敏德是上海纺织商人杨元龙的长女，商人潘迪生的第一任妻子，在业界有“配额女王”之称。杨敏德于香港出生，并于美国麻省理工学院数学系毕业，哈佛大学工商管理硕士毕业。其后返回香港，协助打理家族的纺织及制衣业务，1978年成为溢达集团的掌舵人，带领集团发展成跨国企业。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(232, '上海', '', '西门子,伟创力,合众达', '朱思行', '职位:  , 副总裁', '互联网,移动互联网', '浦东新区', 'd3ae77b20502cf35227f26b1feb74c13.jpg', '北京航空航天大学,中欧国际工商学院', '红点投资Redpoint Ventures', '朱思行( King Zhu ) 是红点投资的副总裁，此前曾担任北京伟创力电子有限公司软件总监、西门子(中国)有限公司软件集成组长、合众达电子有限公司研发工程师等。朱思行获得北京航空航天大学电子工程学士、中国空间技术研究院空间飞行器设计硕士学位，还获得中欧国际工商学院MBA。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(234, '北京', '', '亚信联创,中国网通,电讯盈科', '田溯宁', '职位:  , 创始人', '互联网,移动互联网,传媒新媒体,硬件', '朝阳区', '8126969d5627894cac035ffb3b66edb4.jpg', '中科院', '宽带资本CBC', '田溯宁博士是通信行业内深孚众望创业者和企业管理者，并在海外投融资管理方面拥有丰富的经验，深受海内外业界同仁、投资者及行业主管部门的认可与信任。在创立CBC之前，田溯宁博士是中国网通控股公司的创始人和首席执行官，并任重组后的中国网络通信集团公司副总经理、党组成员；公司上市后并任上市公司总裁 (CEO)，后兼任副董事长。田溯宁博士同时还担任电讯盈科副董事长在加入中国网通之前，田溯宁博士是亚信公司的CEO及创立者之一。亚信公司是中国第一家互联网技术提供商，在田溯宁博士的带领下，亚信公司于1998年在美国NASDAQ上市。田溯宁博士在联想、万事达卡等多家国际国内著名企业中担任董事，同时还多家海内外金融机构中兼任重要职务，并受邀成为为全球最大并购基金KKR两名亚洲顾问之一。2007年，田溯宁博士在中国第一家股权投资行业协会—天津股权投资基金协会出任主席一职。田溯宁博士1988年在中科院获得生态学硕士学位，并与1992年于德州理工大学获得自然资源管理博士学位。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(233, '上海', 'http://weibo.com/2870095574', '', '张涵', '职位:  , 投资经理', '互联网,移动互联网,环保能源,传媒新媒体,硬件', '静安区', 'cbc29e8c3673fde59d3497d8ed04adae.jpg', '清华大学', '红点投资Redpoint Ventures', 'Redpoint Ventures 投资经理，关注早期T.M.T投资，清洁技术及新材料。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(235, '北京', 'http://weibo.com/1889069311', '麦肯锡,中国网通,电讯盈科', '范星槎', '职位:  , 合伙人', '互联网,移动互联网,传媒新媒体,硬件', '朝阳区', 'b7467f09a2846fd048509868043d3ac1.jpg', '东南大学,澳大利亚弗林德斯大学', '宽带资本CBC', '范星槎博士在股权融投资、兼并收购、公司管理及业务发展等方面均拥有丰富的经验。加入宽带资本团队之前，范星槎博士于2005至2007年期间在电讯盈科担任执行董事，于2000至2005年间在网通集团/网通控股担任首席财务官，并带领网通集团于2004年成功于香港证交所及纽约证券交易所成功上市。在网通期间，范星槎博士同时还曾担任战略与业务发展副总裁、运营执行副总裁，以及亚洲网通首席财务官等多项重要职务加入中国网通之前，范星槎博士在麦肯锡公司上海办事处担任高级咨询顾问。范星槎博士在麦肯锡公司的5年中为多家海内外客户公司设计及制定发展战略、市场策略、协助客户改善管理机制，提高经营业绩范星槎博士于1987年在东南大学获得电子工程学硕士学位，并于1996年在澳大利亚弗林德斯大学获得计算机科学博士学位。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(236, '北京', '', 'TOM在线,中信,野村证券,华润', '许志明', '职位:  , 合伙人', '互联网,移动互联网,传媒新媒体', '朝阳区', 'a9080d1f276d13315e034a2a6e4dff41.jpg', '北京大学,中国社会科学院,英国曼彻斯特大学', '宽带资本CBC', '许志明博士在在股权投资、投资银行及公司管理等方面拥有近二十年丰富的从业经验。加入宽带资本团队之前，许志明博士受聘于中信泰富有限公司,为其新媒体业务提供顾问意见。在2002年至2005年，许博士在TOM在线有限公司担任执行董事兼首席营运官，并出任TOM集团的高级顾问，主持印刷出版及户外广告等业务。1999年至2001年期间，许博士曾出任华润创业有限公司执行董事， 华润北京置地有限公司执行董事和华润励致有限公司董事总经理等职务，负责房地产业务和高科技业务的管理及若干投资及资产重组等工作。自1993年至1998年，许博士任职于包括野村国际，国民西敏银行及波士顿银行在内的投资银行，曾任董事及大中华区投资银行主管， 在公司融资和收购兼并业务等方面具备丰富经验。1986年至1989年， 许博士在北京任职于一家政府的研究机构，从事政策咨询与研究。许志明博士于1983年毕业于北京大学， 获颁物理学学士学位；1986年毕业于中国社会科学院研究生院，获颁经济学硕士学位；1993年毕业于英国曼彻斯特大学，获颁经济学博士学位。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(239, '上海', 'http://weibo.com/2016797273', '阿尔卡特朗讯,中国网通', '刘唯', '职位:  , 合伙人', '互联网,移动互联网,传媒新媒体', '静安区', 'ca11f085e56e56ad3e2b27ce54b4dd01.jpg', '浙江大学,中欧国际工商学院', '宽带资本CBC', '刘唯先生于2006年加入宽带资本团队，主要关注互联网、数字媒体、智能终端、云计算及电信服务相关领域内的投资。刘唯先生在投资和电信行业拥有超过15年的工作经验。刘唯先生在加入宽带资本团队之前，在上海阿尔卡特任业务拓展总监，负责阿尔卡特在中国及亚太区域宽带接入的业务拓展。 2001年至2005年，刘唯先生先后任职于中国网通、及其海外分支机构亚洲网通，在业务拓展、兼并收购和战略规划等方面拥有丰富经验。刘唯先生的职业生涯始于1995年，任职于一家研究机构的光通信工程师。刘唯先生于1995年毕业于浙江大学电子技术专业，并于2000年获得中欧工商管理学院MBA学位。刘唯先生持有美国注册金融分析师CFA资格。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(237, '北京', '', '麦肯锡,中国网通', '张颖(1)', '职位:  , 合伙人', '互联网,移动互联网,传媒新媒体,硬件', '朝阳区', '1f40f7de37ebc69a868b5107fc4fdf5f.jpg', '清华大学,中欧国际工商学院', '宽带资本CBC', '张颖先生在风险投资、通讯行业、管理咨询及企业管理等方面有十余年的丰富经验。张颖是宽带资本(CBC)的创始合伙人之一，投资委员会成员，投资了世纪互联、掌上明珠、3G门户、融易通(上海电银)、奇迹智慧科技、网通宽带、创新科等项目。在加入CBC团队之前，张颖先生自2000年起在网通控股和网通集团曾担任多个部门总经理，先后领导公司股权融投资、运营管理和战略规划等多项工作。曾主持2004年网通集团在香港证交所及纽约证券交易所上市、2003年收购亚洲环球电讯及网通控股私募融资等多个融资与收购项目。在加入网通之前，张颖先生在麦肯锡公司北京办事处担任咨询顾问，协助多家客户制定经营战略，设计组织结构。内部管理流程，并推动运营业绩的改善，并曾协助推动2000年中国最大企业的海外上市项目。张颖先生毕业于清华大学经济管理学院金融专业，并获得中欧国际工商管理学院工商管理硕士学位。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(240, '北京', '', '中国网通', '蒋健', '职位:  , 合伙人', '互联网,移动互联网,硬件', '朝阳区', '3627285274d5e37489d7715cb26d1c27.jpg', '北京大学,天津大学', '宽带资本CBC', '加入宽带资本团队之前，蒋健先生在中国网通集团（香港）有限公司资本市场关系部任职。 之前，他任职于信息产业部电信研究院。蒋健先生持有美国注册金融分析师（CFA）证书。蒋健先生毕业于天津大学管理学院，并在北京大学光华管理学院获得管理科学硕士学位。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(238, '北京', '', '荷兰银行,中国网通', '周耘', '职位:  , 合伙人', '金融', '朝阳区', '1e072aae4410d575696be9a34fb21bea.jpg', '中欧国际工商学院,中央财经大学', '宽带资本CBC', '周耘先生具有丰富的专业投资基金和金融机构的从业经历以及大型企业的融投资和财务管理经验。在加入宽带资本团队之前，周耘先生担任私募股权基金Star Capital董事，具体负责基金筹资、项目投资及项目融资在此之前，周耘先生拥有10年的在国有企业和外资企业从事数百亿元人民币规模的财务管理和融投资经验，以及共计5年的外资银行和中资金融机构的从业经历。周耘先生于2000年至2005年担任中国网通司库及资金部总经理期间，负责公司的融资、资金管理等职责；1996年至2000年作为亚太基建发展有限公司的投资经理，负责公司公路、电力项目的开发和管理；在此之前5年，周耘先生分别任职于荷兰商业银行（ING bank）及中国农村发展信托投资公司周耘先生在中央财经大学获经济学硕士学位，并在中欧国际工商管理学院获工商管理硕士学位。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(241, '上海', 'http://weibo.com/1288817193', '华为', '王利杰', '职位:  , 创始人', '互联网,移动互联网', '徐汇区', 'afd91f106460372acfc9c36c7ee412a8.jpg', '西安邮电大学', 'PreAngel/荷多/镭厉资本/鉴睿资本', '王利杰是PreAngel创始人，天使投资人，Mobile 2.0 创始人。王利杰先生在电信/移动/互联网领域拥有超过10年的从业经验，曾经供职于深圳华为、上海蓝卓、北京移数通、智多微电子、若邻网等电信/增值业务/芯片/互联网公司，对移动互联网有着极大的热情并积累了广泛的人脉和经验。');
