INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(2, '北京', 'http://weibo.com/2570957325', '联想', '周逵', '职位:  , 合伙人', '互联网,移动互联网,传媒新媒体', '海淀区', 'cc2a3905141e1e482f5e47059ddce14a.jpg', '清华大学,武汉工业大学', '红杉资本中国', '周逵（Kui Zhou），红杉资本中国基金合伙人。在2005年加入红杉之前，周逵先生任职于联想投资公司，负责投资中讯软件、深圳讯天、文思创新、开拓科技等多个公司；周逵先生还曾在联想集团从事业务发展和投资管理工作；并在冶金、通讯、制造管理咨询领域有多年中小企业工作经验。周逵先生拥有清华大学MBA硕士学位，以及武汉工业大学电气自动化学士学位。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(1, '北京', 'http://weibo.com/1803519527', '携程,如家,花旗银行,德意志银行', '沈南鹏', '职位:  , 创始及执行合伙人', '互联网,移动互联网,传媒新媒体', '海淀区', '0a6315c1dc8d81a1e8e235f6e76c3c13.jpg', '上海交通大学,耶鲁大学', '红杉资本中国', '沈南鹏（Neil Shen)，红杉资本中国基金创始及执行合伙人。沈先生是携程旅行网的创始人及董事、如家连锁酒店的创始人及董事长；是分众传媒及易居中国的投资人及董事。在创建携程旅行网之前,沈先生在纽约和香港的投资银行业工作了八年多，曾任德意志银行的董事兼中国资本市场主管。这以前,沈先生任职于美国雷曼兄弟证券和花旗银行的投资银行部门。沈先生现任中国企业家论坛理事，浙江商会常务副会长,天津私募股权协会会长和北京私募股权协会副会长。沈先生拥有耶鲁大学管理学硕士学位， 上海交通大学学士学位。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(4, '上海', 'http://weibo.com/1816561255', '美银美林,施乐Xerox', '刘星', '职位:  , 董事总经理', '互联网,移动互联网,消费品,现代服务,传媒新媒体', '静安区', '72855eb3c1d8147d65312a7d96d1ba73.jpg', '复旦大学,宾夕法尼亚大学,美国雪城大学', '红杉资本中国', '刘星(Xing Liu)，红杉资本中国基金董事总经理。专注于科技传媒和消费领域的投资。 在2007年加入红杉之前，刘星先生曾任美林集团亚洲投资银行部的副总裁，服务过携程旅行网、如家酒店连锁、巨人网络、神州数码、海王星辰连锁药店等客户；在此之前，刘星先生任职于美国施乐(Xerox)和硅谷的创业型科技公司，先后担任技术研发、产品管理、战略规划与管理咨询等工作。刘星先生拥有美国宾夕法尼亚大学沃顿商学院的工商管理硕士学位，Syracuse大学电脑工程和通讯与网络管理双硕士学位，以及复旦大学管理学院的管理信息系统学士学位。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(3, '上海', 'http://weibo.com/u/1100026292', '希捷,华登国际,祥峰投资', '计越', '职位:  , 合伙人', '互联网,移动互联网,传媒新媒体', '静安区', '5c5a7343727ef40b785f87392c2432d5.jpg', '中欧国际工商学院,南京航空航天大学', '红杉资本中国', '计越(Steven Ji)，红杉资本中国基金合伙人。在2005年加入红杉之前,曾在华登国际，祥峰投资，CIV风险投资公司负责过多个项目的投资。计越先生还曾是希捷科技中国区的首批职员，担任过多个不同的管理职位。计越先生拥有中欧国际商务学院MBA学位，南京航空航天大学工程学学士学位。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(5, '上海', 'http://weibo.com/1762676204', '摩立特集团Monitor', '孙谦', '职位:  , 董事总经理', '互联网,移动互联网,消费品,传媒新媒体', '静安区', 'af63321ce46872f4f5bccdd1b57373c6.jpg', '哈佛大学', '红杉资本中国', '孙谦(Glen Sun)，红杉资本中国基金董事总经理。关注领域为消费品、科技与传媒等行业。在2006年加入红杉之前，孙谦先生任职于General Atlantic公司，负责对中国IT相关产业成长期公司的投资；在此之前，孙谦先生曾在香港摩立特集团担任管理咨询顾问。孙谦先生拥有哈佛大学工商管理硕士和法学博士学位。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(8, '北京', 'http://weibo.com/1779177925', '麦肯锡,通用电气', '胡丹', '职位:  , 副总裁', '互联网,移动互联网,传媒新媒体', '朝阳区', 'a3a191e7777ea8a6f274babd3bfe43b4.jpg', '清华大学,斯坦福大学', '红杉资本中国', '胡丹 (Dan Hu)，红杉资本中国基金副总裁。关注领域为传媒与科技行业。在2010年加入红杉之前，胡丹先生曾担任麦肯锡咨询公司咨询顾问。在麦肯锡咨询，胡丹先生为包括世界银行、中国国家电网、印度Tata集团和美国TPG集团在内的世界五百强企业提供投资、战略和运营咨询意见。在此之前，胡丹先生任职于通用电器，积累了在供应链、生产、产品研发及六西格玛领域丰富的运营经验。胡丹先生拥有斯坦福大学的工商管理硕士学位， 清华大学工程学学士学位。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(7, '北京', 'http://weibo.com/1495897023', 'UCWEB,联创策源', '曹毅', '职位:  , 创始人 , , 副总裁(副总裁)', '互联网,移动互联网', '朝阳区', '1a54b31962009df8386e34bd1823d7c1.jpg', '清华大学', '源码资本,红杉资本中国', '曹毅（Charlie Cao），红杉资本中国基金副总裁。关注领域为科技与传媒行业。在2009年加入红杉之前，曹毅先生任职于联创策源创投基金，参与了UCWEB，网秦和微杂志的投资及管理，并加入UCWEB担任商务合作总监一职。曹毅先生拥有清华大学计算机学士学位。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(6, '北京', '', '德意志银行', '张思坚', '职位:  , 董事总经理', '消费品,现代服务', '朝阳区', '868c68853b7b48615e189f76b4de18a8.jpg', '上海交通大学,宾夕法尼亚大学,明尼苏达大学', '红杉资本中国', '张思坚（Skee zhang）,红杉资本中国基金董事总经理。专注于消费与服务行业。在2007年加入红杉之前, 张先生任职于霸菱投资香港、上海办公室和伦敦的 Doughty Hanson 。张先生投资的企业有 Alphamosaic (被美国博通收购) 以及敏实集团 (香港主板: 0425.HK)。在进入私募股权投资领域前, 张先生曾在德意志银行的投资银行部门专门从事国际性公司的上市、大宗交易、债券以及可转换债券发行的工作。张先生拥有美国明尼苏达大学工学硕士学位以及美国宾西法尼亚大学沃顿商学院工商管理硕士学位，上海交通大学工学学士学位。');
INSERT INTO investor(`id`, `province`, `weibo`, `jobs`, `name`, `title`, `fields`, `city`, `avatar`, `education`, `institution`, `description`) values(9, '北京', '', '波士顿咨询BCG,富达亚洲', '李剑威', '职位:  , 副总裁 , , 合伙人', '互联网,移动互联网,传媒新媒体', '朝阳区', '9586242158f1dc56b95674cfaa0ce12c.jpg', '北京邮电大学', '红杉资本中国,云天使基金', '李剑威(Jianwei Li), 红杉资本中国基金副总裁。关注领域为科技与传媒行业。在2011年加入红杉之前，李先生曾任一家移动互联网公司CFO，负责公司的业务战略以及财务工作。在此之前，李剑威先生是富达亚洲风险投资(Fidelity Growth Partners Asia) 的副总裁，主要研究科技、金融服务、教育等领域，主导了富达在网秦(NYSE: NQ)的投资，并在水晶石科技、孔明社交、当代金融、大头无线等投资项目中发挥了重要作用。在进入风险投资领域之前，李剑威先生在波士顿咨询公司担任咨询顾问，为电信、银行、互联网、连锁零售领域的大型客户提供富有成效的战略咨询服务。李剑威先生拥有北京邮电大学管理工程的硕士和学士学位，以及计算机科学辅修学位。');
